        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        uint256 contractBalance = balanceOf(address(this));

        bool canSwap = contractBalance >= swapTokensAt;

        uint256 totalFee = totalFees();

        if (canSwap && !swapping && marketMakerPairs[to] && totalFee > 0) {
            swapping = true;

            if (contractBalance > 0 && totalFee > 0) {
                if (hasLiquidity) {
                    uint256 initialBalance0 = address(this).balance;

                    address[] memory path = new address[](2);
                    path[0] = address(this);
                    path[1] = _swapRouter.WETH();

                    _swapRouter
                        .swapExactTokensForETHSupportingFeeOnTransferTokens(
                            contractBalance,
                            0,
                            path,
                            address(this),
                            block.timestamp
                        );

                    uint256 initialBalance1 = address(this).balance;

                    uint256 finalBalance = initialBalance1.sub(initialBalance0);

                    if ((marketingSellFee + marketingBuyFee) > 0) {
                        uint256 marketingBNB = finalBalance
                            .mul(marketingSellFee.add(marketingBuyFee))
                            .div(totalFee);

                        sendBNB(payable(MARKETING_WALLET), marketingBNB);

                        emit SendMarketing(marketingBNB);
                    }

                    if ((liquiditySellFee + liquidityBuyFee) > 0) {
                        uint256 liquidityBNB = finalBalance
                            .mul(liquiditySellFee.add(liquidityBuyFee))
                            .div(totalFee);

                        sendBNB(payable(LIQUIDITY_WALLET), liquidityBNB);

                        emit AddLiquidity(liquidityBNB);
                    }

                    if ((rewardsSellFee + rewardsBuyFee) > 0) {
                        uint256 rewardBNB = finalBalance
                            .mul(rewardsSellFee.add(rewardsBuyFee))
                            .div(totalFee);

                        swapAndSendDividends(rewardBNB);
                    }
                } else {
                    super._transfer(
                        address(this),
                        MARKETING_WALLET,
                        contractBalance
                    );
                }
            }

            swapping = false;
        }

        bool takeFee = !swapping;

        if (_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }

        // Wallet to wallet & not excluded from fees
        if (from != SWAP_PAIR && to != SWAP_PAIR && takeFee) {
            takeFee = false;
        }

        if (takeFee) {
            uint256 _totalFees;

            if (from == SWAP_PAIR) {
                _totalFees = totalBuyFee();
            } else {
                _totalFees = totalSellFee();
            }

            uint256 fees = (amount.mul(_totalFees)).div(100);
            amount = amount.sub(fees);

            super._transfer(from, address(this), fees);
        }

        super._transfer(from, to, amount);

        try _tracker.setBalance(payable(from), balanceOf(from)) {} catch {}
        try _tracker.setBalance(payable(to), balanceOf(to)) {} catch {}

        if (!swapping) {
            try _tracker.process(gasForProcessing) {} catch {}
        }
    }

    function totalBuyFee() private view returns (uint256) {
        return marketingBuyFee.add(liquidityBuyFee).add(rewardsBuyFee);
    }

    function totalSellFee() private view returns (uint256) {
        return marketingSellFee.add(liquiditySellFee).add(rewardsSellFee);
    }

    function totalFees() private view returns (uint256) {
        return totalBuyFee().add(totalSellFee());
    }

    function toggleHasLiquidity() external onlyOwner {
        hasLiquidity = !hasLiquidity;
    }

    function swapAndSendDividends(uint256 amount) private {
        address[] memory path = new address[](2);
        path[0] = _swapRouter.WETH();
        path[1] = _tracker.getRewardToken();

        _swapRouter.swapExactETHForTokensSupportingFeeOnTransferTokens{
            value: amount
        }(0, path, address(this), block.timestamp);

        address rewardToken = _tracker.getRewardToken();

        uint256 balanceRewardToken = IERC20(rewardToken).balanceOf(
            address(this)
        );

        bool success = IERC20(rewardToken).transfer(
            address(_tracker),
            balanceRewardToken
        );

        if (success) {
            _tracker.distributeDividends(balanceRewardToken);
            emit SendDividends(balanceRewardToken);
        }
    }

    function setSwapTokensAt(uint256 amount) external onlyOwner {
        require(amount > (100 * 10 ** 18), "Must be greater than 100");
        swapTokensAt = amount;
    }

    function setDestinations(
        address marketingWallet,
        address liquidityWallet
    ) external onlyOwner {
        MARKETING_WALLET = marketingWallet;
        LIQUIDITY_WALLET = liquidityWallet;
    }

    function setFees(
        uint256 marketingSellFee_,
        uint256 marketingBuyFee_,
        uint256 liquiditySellFee_,
        uint256 liquidityBuyFee_,
        uint256 rewardsSellFee_,
        uint256 rewardsBuyFee_
    ) external onlyOwner {
        marketingSellFee = marketingSellFee_;
        marketingBuyFee = marketingBuyFee_;

        liquiditySellFee = liquiditySellFee_;
        liquidityBuyFee = liquidityBuyFee_;

        rewardsSellFee = rewardsSellFee_;
        rewardsBuyFee = rewardsBuyFee_;
    }

    function updateClaimWait(uint256 newClaimWait) external onlyOwner {
        require(
            newClaimWait >= 3_600 && newClaimWait <= 86_400,
            "claimWait must be updated to between 1 and 24 hours"
        );

        _tracker.updateClaimWait(newClaimWait);
    }

    function getClaimWait() external view returns (uint256) {
        return _tracker.claimWait();
    }

    function getTotalDividendsDistributed() external view returns (uint256) {
        return _tracker.totalDividendsDistributed();
    }

    function withdrawableDividendOf(
        address account
    ) public view returns (uint256) {
        return _tracker.withdrawableDividendOf(account);
    }

    function dividendTokenBalanceOf(
        address account
    ) public view returns (uint256) {
        return _tracker.balanceOf(account);
    }

    function totalRewardsEarned(address account) public view returns (uint256) {
        return _tracker.accumulativeDividendOf(account);
    }

    function excludeFromDividends(address account) external onlyOwner {
        _tracker.excludeFromDividends(account);
    }

    function getAccountDividendsInfo(
        address account
    )
        external
        view
        returns (
            address,
            int256,
            int256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        return _tracker.getAccount(account);
    }

    function getAccountDividendsInfoAtIndex(
        uint256 index
    )
        external
        view
        returns (
            address,
            int256,
            int256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        return _tracker.getAccountAtIndex(index);
    }

    function processDividendTracker(uint256 gas) external {
        _tracker.process(gas);
    }

    function claim() external {
        _tracker.processAccount(payable(msg.sender), false);
    }

    function claimAddress(address claimee) external onlyOwner {
        _tracker.processAccount(payable(claimee), false);
    }

    function getLastProcessedIndex() external view returns (uint256) {
        return _tracker.getLastProcessedIndex();
    }

    function setLastProcessedIndex(uint256 index) external onlyOwner {
        _tracker.setLastProcessedIndex(index);
    }

    function getNumberOfDividendTokenHolders() external view returns (uint256) {
        return _tracker.getNumberOfTokenHolders();
    }
}
