    "contracts/LandStake.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "./token/KAP20.sol";
import "./token/KAP721Holder.sol";
import "./interfaces/INextNFTTransferRouter.sol";

interface ILand is IERC721
{
    function getPoints(uint256 tokenId) external view returns (string[] memory);
}

contract LandStake is KAP20, KAP721Holder, AccessControlEnumerable, ERC721Holder
{
    using SafeMath for uint256;
    using EnumerableSet for EnumerableSet.UintSet;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    uint256 public constant TOKENS_PER_POINT = 10e18; // 10 tokens per point
    ILand public land;

    address public callHelper;
    INextNFTTransferRouter public nextNFTTransferRouter;

    // land token id => wallet address
    mapping(uint256 => address) public owners;

    // wallet address => [land token id]
    mapping(address => EnumerableSet.UintSet) private ownerInfo;

    modifier onlyMinter {
        require(hasRole(MINTER_ROLE, msg.sender), "Caller is not a minter");
        _;
    }

    modifier onlyAdministrator {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Caller is not an admin");
        _;
    }

    modifier checkBitkubNextUser(address bitkubNextUser) {
        if (bitkubNextUser != address(0)) {
            require(kyc.kycsLevel(bitkubNextUser) >= acceptedKYCLevel, "only Bitkub Next user");
        }
        _;
    }

    modifier checkCallHelper(address bitkubNextUser) {
        if (bitkubNextUser != address(0)) {
            require(msg.sender == callHelper, "only CallHelper");
        }
        _;
    }

    event Stake(
        address indexed user,
        uint256 indexed tokenId
    );

    event Unstake(
        address indexed user,
        uint256 indexed tokenId
    );
    constructor(
        address _land,
        string memory _projectName,
        uint8 _decimals,
        address _kyc,
        address _adminProjectRouter,
        address _committee,
        address _transferRouter,
        uint256 _acceptedKYCLevel
    ) KAP20(
        "Land Stake",
        "ST-LAND",
        _projectName,
        _decimals,
        _kyc,
        _adminProjectRouter,
        _committee,
        _transferRouter,
        _acceptedKYCLevel
    ) {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        land = ILand(_land);
    }

    function calculateTokenAmount(uint256 tokenId) 
        public view returns (uint256 amount)
    {
        uint256 pointsCount = land.getPoints(tokenId).length;
        amount = TOKENS_PER_POINT.mul(pointsCount);
    }

    function stake(
        uint256 tokenId,
        address bitkubNextUser
    ) public checkCallHelper(bitkubNextUser) checkBitkubNextUser(bitkubNextUser) {
        address _sender = _safeSender(bitkubNextUser);
        require(land.ownerOf(tokenId) == _sender, "permission denied");        
        owners[tokenId] = _sender;
        ownerInfo[_sender].add(tokenId);

        _safeDepositLand(_sender, tokenId, bitkubNextUser);

        _mint(_sender, calculateTokenAmount(tokenId));

        emit Stake(_sender, tokenId);
    }

    function stake(
        uint256 tokenId
    ) external {
        stake(tokenId, address(0));
    }

    function unstake(
        uint256 tokenId,
        address bitkubNextUser
    ) public checkCallHelper(bitkubNextUser) checkBitkubNextUser(bitkubNextUser) {
        address _sender = _safeSender(bitkubNextUser);
        require(owners[tokenId] == _sender, "permission denied");
        owners[tokenId] = address(0);
        ownerInfo[_sender].remove(tokenId);

        land.safeTransferFrom(address(this), _sender, tokenId);        

        _burn(_sender, calculateTokenAmount(tokenId));

        emit Unstake(_sender, tokenId);
    }

    function unstake(
        uint256 tokenId
    ) external {
        unstake(tokenId, address(0));
    }

    function ownerInfoCount(address owner) 
        external view returns (uint256)
    {
        return ownerInfo[owner].length();
    }

    function ownerInfoAtIndex(address owner, uint256 index)
        external view returns (uint256)
    {
        return ownerInfo[owner].at(index);
    }
    function mint(address receiver, uint256 amount) external onlyMinter {
        _mint(receiver, amount);
    }

    function grantMinter(address minter) external onlyAdministrator {
        grantRole(MINTER_ROLE, minter);
    }

    function revokeMinter(address minter) external onlyAdministrator {
        revokeRole(MINTER_ROLE, minter);
    }

    function setNextNFTTransferRouter(address _nextNFTTransferRouter) external onlyAdministrator {
        require(_nextNFTTransferRouter != address(0), "change to the zero address");
        nextNFTTransferRouter = INextNFTTransferRouter(_nextNFTTransferRouter);
    }

    function setCallHelper(address _callHelper) external onlyAdministrator {
        require(_callHelper != address(0), "change to the zero address");
        callHelper = _callHelper;
    }

    function setLand(address _land) external onlyAdministrator {
        land = ILand(_land);
    }

    function _safeSender(
        address _bitkubNextUser
    ) internal view returns (address) {
        if (_bitkubNextUser != address(0)) {
            return _bitkubNextUser;
        }
        return msg.sender;
    }

    function _safeDepositLand(
        address _from, 
        uint256 _tokenId,
        address _bitkubNextUser
    ) internal {
        if (_bitkubNextUser != address(0)) {
            nextNFTTransferRouter.transferFromKAP721(PROJECT, address(land), _from, address(this), _tokenId);
        } else {
            land.safeTransferFrom(_from, address(this), _tokenId);
        }
    }
}"
    },
    "contracts/abstracts/Committee.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

abstract contract Committee {
    address public committee;

    event CommitteeSet(address indexed oldCommittee, address indexed newCommittee, address indexed caller);

    modifier onlyCommittee() {
        require(msg.sender == committee, "Committee: restricted only committee");
        _;
    }

    function setCommittee(address _committee) public virtual onlyCommittee {
        emit CommitteeSet(committee, _committee, msg.sender);
        committee = _committee;
    }
}"
    },
    "contracts/abstracts/standard/AccessController.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

import "@openzeppelin/contracts/access/Ownable.sol";

import "./Authorization.sol";
import "./KYCHandler.sol";
import "../Committee.sol";

abstract contract AccessController is Authorization, KYCHandler, Ownable, Committee {
    address public transferRouter;

    event TransferRouterSet(
        address indexed oldTransferRouter,
        address indexed newTransferRouter,
        address indexed caller
    );

    modifier onlyOwnerOrCommittee() {
        require(
            msg.sender == owner() || msg.sender == committee,
            "AccessController: restricted only owner or committee"
        );
        _;
    }

    modifier onlySuperAdminOrTransferRouter() {
        require(
            adminProjectRouter.isSuperAdmin(msg.sender, PROJECT) || msg.sender == transferRouter,
            "AccessController: restricted only super admin or transfer router"
        );
        _;
    }

    modifier onlySuperAdminOrCommittee() {
        require(
            adminProjectRouter.isSuperAdmin(msg.sender, PROJECT) || msg.sender == committee,
            "AccessController: restricted only super admin or committee"
        );
        _;
    }

    modifier onlySuperAdminOrOwner() {
        require(
            adminProjectRouter.isSuperAdmin(msg.sender, PROJECT) || msg.sender == owner(),
            "AccessController: restricted only super admin or owner"
        );
        _;
    }

    function activateOnlyKYCAddress() external onlyCommittee {
        _activateOnlyKYCAddress();
    }

    function setKYC(address _kyc) external onlyCommittee {
        _setKYC(_kyc);
    }

    function setAcceptedKYCLevel(uint256 _kycLevel) external onlyCommittee {
        _setAcceptedKYCLevel(_kycLevel);
    }

    function setTransferRouter(address _transferRouter) external onlyOwnerOrCommittee {
        emit TransferRouterSet(transferRouter, _transferRouter, msg.sender);
        transferRouter = _transferRouter;
    }

    function setAdminProjectRouter(address _adminProjectRouter) public override onlyOwnerOrCommittee {
        require(_adminProjectRouter != address(0), "Authorization: new admin project router is the zero address");
        emit AdminProjectRouterSet(address(adminProjectRouter), _adminProjectRouter, msg.sender);
        adminProjectRouter = IAdminProjectRouter(_adminProjectRouter);
    }
}"
    },
    "contracts/abstracts/standard/Authorization.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

import "../../interfaces/IAdminProjectRouter.sol";

abstract contract Authorization {
    IAdminProjectRouter public adminProjectRouter;
    string public PROJECT; // Fill the project name

    event AdminProjectRouterSet(address indexed oldAdmin, address indexed newAdmin, address indexed caller);

    modifier onlySuperAdmin() {
        require(adminProjectRouter.isSuperAdmin(msg.sender, PROJECT), "Authorization: restricted only super admin");
        _;
    }

    modifier onlyAdmin() {
        require(adminProjectRouter.isAdmin(msg.sender, PROJECT), "Authorization: restricted only admin");
        _;
    }

    modifier onlySuperAdminOrAdmin() {
        require(
            adminProjectRouter.isSuperAdmin(msg.sender, PROJECT) || adminProjectRouter.isAdmin(msg.sender, PROJECT),
            "Authorization: restricted only super admin or admin"
        );
        _;
    }

    function setAdminProjectRouter(address _adminProjectRouter) public virtual onlySuperAdmin {
        require(_adminProjectRouter != address(0), "Authorization: new admin project router is the zero address");
        emit AdminProjectRouterSet(address(adminProjectRouter), _adminProjectRouter, msg.sender);
        adminProjectRouter = IAdminProjectRouter(_adminProjectRouter);
    }
}"
    },
    "contracts/abstracts/standard/KYCHandler.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

import "../../interfaces/IKYCBitkubChain.sol";

abstract contract KYCHandler {
    IKYCBitkubChain public kyc;

    uint256 public acceptedKYCLevel;
    bool public isActivatedOnlyKYCAddress;

    function _activateOnlyKYCAddress() internal virtual {
        isActivatedOnlyKYCAddress = true;
    }

    function _setKYC(address _kyc) internal virtual {
        kyc = IKYCBitkubChain(_kyc);
    }

    function _setAcceptedKYCLevel(uint256 _kycLevel) internal virtual {
        acceptedKYCLevel = _kycLevel;
    }
}
"
    },
    "contracts/abstracts/standard/Pausable.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

abstract contract Pausable {
    event Paused(address account);

    event Unpaused(address account);

    bool public paused;

    constructor() {
        paused = false;
    }

    modifier whenNotPaused() {
        require(!paused, "Pausable: paused");
        _;
    }

    modifier whenPaused() {
        require(paused, "Pausable: not paused");
        _;
    }

    function _pause() internal virtual whenNotPaused {
        paused = true;
        emit Paused(msg.sender);
    }

    function _unpause() internal virtual whenPaused {
        paused = false;
        emit Unpaused(msg.sender);
    }
}"
    },
