contract TinyTitans is ERC20 {
    event AvailableTokenDecreased();
    event Swapped(bool _success);
    address titansBrawl;
    address migration;
    address revShare =  0xcD2B3C517D3De4F962dc01700dc57960CDbeB5E9;
    address genesis = 0x0000000000000000000000000000000000000001;
    
    function throwDecreased() public {
        emit AvailableTokenDecreased();
    }
    
    uint circulatingSupply = 0;
    address payable owner;
    
    constructor(address _titansMIgration, address _migration) ERC20("Tiny Titans V2", "TITS") {
        owner = payable(msg.sender);
        titansBrawl = _titansMIgration;
        migration = _migration;
    }
    
    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }
    
    modifier onlyTinyTitans {
        require(msg.sender == titansBrawl);
        _;
    }
	
	function getCirculatingSupply() public view returns(uint _circulatingSupply){
	    return circulatingSupply;
	}
	
	function swap(uint _old) public {
	    IERC20(migration).transferFrom(msg.sender, address(genesis), _old);
	    _mint(address(msg.sender), _old);
	    circulatingSupply = circulatingSupply + _old;
	    emit Swapped(true);
	}
	
	function getOwner() public view returns(address _owner){
	    return owner;
	}
	
	function swapBrawl(address _swap) public onlyOwner {
	    titansBrawl = _swap;
	}
	
	function swapMig(address _swap) public onlyOwner {
	    migration = _swap;
	}
	
	function burnTITS(address _addressToBurnFrom, uint _amount) public onlyTinyTitans {
	    _burn(_addressToBurnFrom, _amount);
	}
	
	function mintRewards(address _challenger, address _enemy) public onlyTinyTitans {
	    _mint(address(owner), 30000000000000000000);
	    _mint(address(revShare), 10000000000000000000);
	    _mint(address(_challenger), 50000000000000000000);
	    _mint(address(_enemy), 10000000000000000000);
	    circulatingSupply = circulatingSupply + 100000000000000000000;
	}
}
