      "content": "/// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.19;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract SelfStakePool {
    using SafeERC20 for IERC20;

    string public constant nameContract = 'SelfStakePool v0.1.7c';

    IERC20 public immutable stakingToken;
    IERC20 public immutable rewardsToken;

    address public owner;

    // Duration of rewards to be paid out (in seconds)
    uint public duration;
    // Timestamp of when the rewards finish
    uint public finishAt;
    // Minimum of last updated time and reward finish time
    uint public updatedAt;
    // Reward to be paid out per second
    uint public rewardRate;
    // Sum of (reward rate * dt * 1e18 / total supply)
    uint public rewardPerTokenStored;
    // User address => rewardPerTokenStored
    mapping(address => uint) public userRewardPerTokenPaid;
    // User address => rewards to be claimed
    mapping(address => uint) public rewards;
    // Total staked supply
    uint public stakeSupply;
    // Total reward supply
    uint public rewardSupply;

    // User address => staked amount
    mapping(address => uint) public balanceOf;

    // User address => bool whitelisted
    mapping(address => bool) public whitelistedAddresses;

    // Option to disable the whitelist
    bool public whiteListDisabled;

    event Deposited(address indexed owner, uint256 amount);
    event DepositWitdrawn(address indexed owner, uint256 amount);
    event Staked(address indexed holder, uint256 amount);
    event Withdrawn(address indexed holder, uint256 amount);
    event Rewarded(address indexed holder, uint256 amount);

    constructor(address _stakingToken, address _rewardToken) {
        owner = msg.sender;
        stakingToken = IERC20(_stakingToken);
        rewardsToken = IERC20(_rewardToken);
        whiteListDisabled = false;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "not authorized");
        _;
    }

    modifier updateReward(address _account) {
        rewardPerTokenStored = rewardPerToken();
        updatedAt = lastTimeRewardApplicable();
        if (_account != address(0)) { 
            rewards[_account] = earned(_account);
            userRewardPerTokenPaid[_account] = rewardPerTokenStored;
        }
        _;
    }

    modifier isWhitelisted(address _address) {
      require(whitelistedAddresses[_address] || whiteListDisabled, "You need to be whitelisted");
      _;
    }

    function addWhiteListed(address _address) external onlyOwner {
        whitelistedAddresses[_address] = true;
    }

    function addArrayWhiteListed(address[] calldata _toAddAddresses) 
        external onlyOwner
    {
        for (uint i = 0; i < _toAddAddresses.length; i++) {
            whitelistedAddresses[_toAddAddresses[i]] = true;
        }
    }

    function depositTokens(uint _amount) external onlyOwner {
        rewardsToken.safeTransferFrom(msg.sender, address(this), _amount);
        rewardSupply += _amount;

        emit Deposited(msg.sender, _amount);
    }

    function withdrawDepositTokens(uint _amount) external onlyOwner {
        require(_amount <= rewardsToken.balanceOf(address(this)) , "insufficient supply");

        rewardsToken.safeTransfer(msg.sender, _amount);
        rewardSupply -= _amount;

        emit DepositWitdrawn(msg.sender, _amount);
    }

    function lastTimeRewardApplicable() public view returns (uint) {
        return _min(finishAt, block.timestamp);
    }

    function rewardPerToken() public view returns (uint) {
        if (stakeSupply == 0) {
            return rewardPerTokenStored;
        }

        return
            rewardPerTokenStored +
            (rewardRate * (lastTimeRewardApplicable() - updatedAt) * 1e18) /
            stakeSupply;
    }

    function stake(uint _amount) external isWhitelisted(msg.sender) updateReward(msg.sender) {
        require(_amount > 0, "amount = 0");
        stakingToken.safeTransferFrom(msg.sender, address(this), _amount);
        balanceOf[msg.sender] += _amount;
        stakeSupply += _amount;

        emit Staked(msg.sender, _amount);
    }

    function withdraw(uint _amount) external updateReward(msg.sender) {
        uint balance = balanceOf[msg.sender];

        require(balance >= _amount, "No sufficient stake balance!");

        balanceOf[msg.sender] -= _amount; 

        stakeSupply -= _amount;

        stakingToken.safeTransfer(msg.sender, _amount);

        emit Withdrawn(msg.sender, _amount);
    }

    function earned(address _account) public view returns (uint) {
        return
            ((balanceOf[_account] *
                (rewardPerToken() - userRewardPerTokenPaid[_account])) / 1e18) +
            rewards[_account];
    }

    function getReward() external updateReward(msg.sender) {
        uint reward = rewards[msg.sender];
        require(rewardSupply >= reward, "insufficient supply");
        require(reward >= 0, "no rewards");

        rewards[msg.sender] = 0;
        rewardsToken.safeTransfer(msg.sender, reward);
        rewardSupply -= reward;

        emit Rewarded(msg.sender, reward);
    }

    function setRewardsDuration(uint _duration) external onlyOwner {
        require(finishAt < block.timestamp, "reward duration not finished");
        duration = _duration;
    }

    function notifyRewardAmount(
        uint _amount
    ) external onlyOwner updateReward(address(0)) {
        if (block.timestamp >= finishAt) {
            rewardRate = _amount / duration;
        } else {
            uint remainingRewards = (finishAt - block.timestamp) * rewardRate;
            rewardRate = (_amount + remainingRewards) / duration;
        }

        require(rewardRate > 0, "reward rate = 0");
        require(
            rewardRate * duration <= rewardsToken.balanceOf(address(this)),
            "reward amount > balance"
        );

        finishAt = block.timestamp + duration;
        updatedAt = block.timestamp;
    }

    function apr() external view returns (uint) {
        uint _apr = 0; 
        if (stakeSupply > 0) _apr = (rewardRate * 86400 * 365 * 10000) / stakeSupply;  
        // divide returnvalue by 100 to give apr in %
        return _apr;
    }

    function _min(uint x, uint y) private pure returns (uint) {
        return x <= y ? x : y;
    }

    /// @dev check if address isWhiteListed
    function getWhitelisted(address _account) external view returns (bool) {
        return whitelistedAddresses[_account];
    }

    /// @dev owner controls whiteListFlags
    function setWhiteListDisabled(bool _flag) external onlyOwner {
        whiteListDisabled = _flag;
    }

    function setOwner(address _newOwner) external onlyOwner {
         owner = _newOwner;
    }

}"
