contract gfDao {

 using SafeMath for uint256; 
    IERC20 public usdtERC20;//0x55d398326f99059fF775485246999027B3197955

    address public _owner;

    uint256[3] public nodeAmount = [100e18,1000e18, 3000e18]; //单位 usdt  

    address public defaultRefer;

    address public boss ;

    uint256 private constant referDepth = 20;

    uint256 public  smallNode ;

    uint256 public  bigNode ;

    modifier onlyOwner() {
        require(msg.sender == _owner, "Permission denied"); _;
    }



    struct UserInfo {

        uint256 count;

        address referrer;

        uint256 node;
   
        uint256  directNum;   
   
        uint256 teamNum;

        uint256 totalRevenue;

        uint256 totalDeposit;

        uint256 teamTotalDeposit;

        uint256 startTime;
    }

    mapping(address=>UserInfo) public userInfo;

    mapping(address => mapping(uint256 => address[])) public teamUsers;

    event Register(address user, address referral);

    event BuyNode(address _buyer, uint256 _amount);

    //boos 0xf940fe437128089173c05b637c732b0059364ca9
    
    constructor(address _boss) public { 
       boss = _boss;
       //main 0x55d398326f99059fF775485246999027B3197955
       //test 0x66CCed6cB1b59c76Ef8BA6dAaDa6C494a050233f
       usdtERC20 = IERC20(0x66CCed6cB1b59c76Ef8BA6dAaDa6C494a050233f);
       defaultRefer = _boss;
       _owner = msg.sender;
    }

    function transferOwnership(address newOwner) public onlyOwner {
        require(newOwner != address(0));
        _owner = newOwner;
    }

    

    // function register(address _referral) external {  
    //     UserInfo storage user = userInfo[msg.sender];
    //     UserInfo storage refer = userInfo[_referral];
    //     require(refer.node !=0  || _referral == defaultRefer , "Invalid superior");
    //     require(user.referrer == address(0), "referrer bonded");
    //     user.referrer = _referral; 
    //     user.startTime = block.timestamp;
    //     emit Register(msg.sender, _referral);
    // }



    function buyNode(uint256 _index,uint256 _usdt) external {
        UserInfo storage user = userInfo[msg.sender];      
        require(user.node ==0, "Cannot repeat purchase"); 
        require(nodeAmount[_index]==_usdt,"Wrong Quantity");    
        usdtERC20.transferFrom(msg.sender, address(this), _usdt);  
        user.node = _index+1;
        emit BuyNode(msg.sender, _usdt);
        if(_index==1){
          smallNode +=1;
        }
        if(_index==2){
          bigNode +=1;
        }
       usdtERC20.transfer(boss,_usdt);
    }

    function getTeamUsersLength(address _user, uint256 _layer) external view returns(uint256) {
        return teamUsers[_user][_layer].length;
    }



    function setNodeAmount(uint256 _node1,uint256 _node2,uint256 _node3) external onlyOwner{
       nodeAmount = [_node1,_node2,_node3];
    }

    function setBoss(address _boss) external onlyOwner{
       boss = _boss;
    }

    
    function setUsdt(address _usdt) external onlyOwner{
      usdtERC20 = IERC20(_usdt);
    }

    function _updateTeamNum(address _user) private {
        UserInfo storage user = userInfo[_user];
        address upline = user.referrer;
        for(uint256 i = 0; i < referDepth; i++){
            if(upline != address(0)){
             if(teamUsers[upline][i].length>0){
                    for(uint256 j=0;j<teamUsers[upline][i].length;j++){
                        if(teamUsers[upline][i][j]==_user){
                          return;
                        }
                    }
                }
                userInfo[upline].teamNum = userInfo[upline].teamNum.add(1);
                teamUsers[upline][i].push(_user);
                if(upline == defaultRefer) break;
                upline = userInfo[upline].referrer;
            }else{
                break;
            }
        }
    }


    function getTeamDeposit(address _user) public view returns(uint256){
        uint256 maxTeam;
        for(uint256 i = 0; i < teamUsers[_user][0].length; i++){
            uint256 userTotalTeam = userInfo[teamUsers[_user][0][i]].teamTotalDeposit.add(userInfo[teamUsers[_user][0][i]].totalDeposit);
            maxTeam = maxTeam.add(userTotalTeam);
        }
        return(maxTeam);
    }
     


 function getMessageHash(

        string memory _message
    ) public pure returns (bytes32) {
        return keccak256(abi.encodePacked( _message));
    }

