            ttmToken.safeTransfer(burnFeeTo, fees);
        }
        if (burnAmount.sub(fees) > 0) {
            ttmToken.safeTransfer(0x000000000000000000000000000000000000dEaD, burnAmount.sub(fees));
        }
        return (burnAmount, fees);
    }

    function buy(
        uint256 amountIn,
        uint256 deadline,
        address otherUser
    ) external noEmergency ensure(deadline) nonReentrant {
        (uint256 amountIn_,uint256 amountOut_,uint256 fees_,uint256 burnAmount_) = _buy(amountIn, 0);
        uint256 power = getUsdtPower(amountIn_);
        emit Buy(msg.sender, otherUser, amountIn_, amountOut_, fees_, burnAmount_, power, referrerRate, 0, 0);
    }

    function getY(uint256 amount) public view returns (uint256){
        // y = x * 9 * 3   / 4
        uint256 ttmRate = RATE_DENOMINATOR.sub(usdtRate);
        return amount.mul(ttmRate).div(usdtRate).mul(usdtPowerRate).div(ttmPowerRate);
    }



    function getUsdtPower(uint256 usdtAmount) public view returns (uint256){
        return usdtAmount.mul(usdtPowerRate).div(RATE_DENOMINATOR);
    }

    function getTtmPower(uint256 usdtAmount) public view returns (uint256){
        return usdtAmount.mul(ttmPowerRate).div(RATE_DENOMINATOR);
    }


    function _buy(uint256 amountIn, uint256 amountOutMin) private returns (uint256 amountIn_, uint256 amountOut_, uint256 fees_, uint256 burnAmount_) {
        address[] memory path = new address[](2);
        path[0] = address(usdtToken);
        path[1] = address(ttmToken);

        uint256[] memory amounts = factory.getAmountsOut(amountIn, path);
        require(
            amounts[1] >= amountOutMin,
            "Router: INSUFFICIENT_OUTPUT_AMOUNT"
        );
        usdtToken.safeTransferFrom(msg.sender, pair, amounts[0]);
        _swap(amounts, path, address(this));
        uint256 amountOut = amounts[1];
        uint256 fees = amountOut.mul(buyFeeRate).div(RATE_DENOMINATOR);
        if (fees > 0) {
            ttmToken.safeTransfer(feeTo, fees);
        }
        uint256 burnAmount = amountOut.sub(fees);
        if (burnAmount > 0) {
            ttmToken.safeTransfer(0x000000000000000000000000000000000000dEaD, burnAmount);
        }

        return (amountIn, amountOut, fees, burnAmount);
    }


    function sell(
        uint256 amountIn,
        uint256 amountOutMin,
        uint256 deadline
    ) external noEmergency ensure(deadline) nonReentrant {
        address[] memory path = new address[](2);
        path[0] = address(ttmToken);
        path[1] = address(usdtToken);

        uint256[] memory amounts = factory.getAmountsOut(amountIn, path);
        require(
            amounts[1] >= amountOutMin,
            "Router: INSUFFICIENT_OUTPUT_AMOUNT"
        );
        ttmToken.safeTransferFrom(msg.sender, pair, amounts[0]);
        _swap(amounts, path, address(this));

        uint256 amountOut = amounts[1];
        ISwapPair(pair).burnTTM(amounts[0]);


        uint256 fees = amountOut.mul(sellFeeRate).div(RATE_DENOMINATOR);
        if (fees > 0) {
            usdtToken.safeTransfer(feeTo, fees);
        }
        uint256 userAmount = amountOut.sub(fees);
        if (userAmount > 0) {
            usdtToken.safeTransfer(msg.sender, userAmount);
        }
        emit Sell(msg.sender, amounts[0], amountOut, fees, userAmount);
    }

    function _swap(
        uint256[] memory amounts,
        address[] memory path,
        address _to
    ) internal virtual {
        for (uint256 i; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            (address token0,) = factory.sortTokens(input, output);
            uint256 amountOut = amounts[i + 1];
            (uint256 amount0Out, uint256 amount1Out) =
                input == token0
                    ? (uint256(0), amountOut)
                    : (amountOut, uint256(0));
            address to =
                i < path.length - 2
                    ? factory.pairFor(output, path[i + 2])
                    : _to;
            ISwapPair(factory.pairFor(input, output)).swap(amount0Out, amount1Out, to, new bytes(0));
        }
    }


