pragma solidity ^0.8.0;

interface IPool {
    function addLiquidity(address user_, uint256 amountLD_) external;

    function removeLiquidity(address user_, uint256 amountLD_) external;

    function removeLiquidityTo(address user_, uint256 amountLD_, address to_) external;

    function deposit(uint16 dstChainId_, uint256 dstPoolId_, address user_, uint256 amountLD_)
        external
        returns (uint256 amountSD);

    function withdraw(uint16 dstChainId_, uint256 dstPoolId_, address user_, uint256 amountSD_)
        external
        returns (uint256 amountLD);

    function token() external returns (address token);

    function convertRate() external returns (uint256 convertRate);

    function setMaxLiquidity(uint256 maxLiquidity_) external;

    function maxLiquidity() external returns (uint256);

    function setMaxTransferLimit(uint16 dstChainId_, uint256 maxTransferLimit_) external;

    function maxTransferLimits(uint16 dstChainId_) external view returns (uint256);

    function chainPaths(uint16 dstChainId_, uint256 dstPoolId_) external view returns (bool);

    function activateChainPath(uint16 dstChainId_, uint256 dstPoolId_) external;

    function inactivateChainPath(uint16 _dstChainId, uint256 _dstPoolId) external;

    function balance() external view returns (uint256);

    function provider() external view returns (address);

    function newLiquidityProvider(address provider_) external;
}
