    "src/Bridge.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

import "./interfaces/IZKBridgeReceiver.sol";
import "./interfaces/IZKBridgeEndpoint.sol";
import "./interfaces/IFee.sol";
import "./Router.sol";

contract Bridge is IZKBridgeReceiver, Initializable, OwnableUpgradeable, ReentrancyGuardUpgradeable {
    IZKBridgeEndpoint public zkBridgeEndpoint;
    Router public router;

    // Mapping of token_bridge contracts on other chains
    mapping(uint16 => address) public bridgeLookup;

    IFee public feeContract;

    //-------------------------------------EVENTS--------------------------------------
    event TransferToken(
        uint64 indexed sequence,
        uint16 dstChainId,
        uint256 srcPoolId,
        uint256 dstPoolId,
        address sender,
        address recipient,
        uint256 amount
    );

    event ReceiveToken(
        uint64 indexed sequence,
        uint16 sourceChainId,
        uint256 srcPoolId,
        uint256 dstPoolId,
        address recipient,
        uint256 amount
    );

    event NewBridge(uint16 chainId, address bridge);

    event NewFeeContract(IFee feeContract);

    //--------------------------------------MODIFIERS-------------------------------------
    modifier onlyRouter() {
        require(msg.sender == address(router), "Bridge: caller must be Router.");
        _;
    }

    /**
     * @dev Initialize pool contract function.
     */
    function initialize(address payable router_, address zkBridgeEndpoint_, IFee feeContract_) public initializer {
        __Ownable_init();
        __ReentrancyGuard_init();

        require(router_ != address(0), "Bridge: Router cannot be zero address");
        require(zkBridgeEndpoint_ != address(0), "Bridge: ZkBridgeEndpoint cannot be zero address");

        router = Router(router_);
        zkBridgeEndpoint = IZKBridgeEndpoint(zkBridgeEndpoint_);
        _setFeeContract(feeContract_);
    }

    function send(
        uint16 dstChainId_,
        uint256 srcPoolId_,
        uint256 dstPoolId_,
        uint256 amount_,
        address sender_,
        address recipient_
    ) external payable nonReentrant onlyRouter {
        require(bridgeLookup[dstChainId_] != address(0), "Bridge: dstChainId does not exist");
        require(msg.value >= estimateFee(dstChainId_, amount_), "Bridge: Insufficient Fee");
        uint256 zkBridgeFee = zkBridgeEndpoint.estimateFee(dstChainId_);
        if (msg.value - zkBridgeFee > 0) {
            payable(address(feeContract)).transfer(msg.value - zkBridgeFee);
        }

        address dstAddress = bridgeLookup[dstChainId_];
        bytes memory payload = abi.encode(srcPoolId_, dstPoolId_, amount_, recipient_);

        uint64 sequence = zkBridgeEndpoint.send{value: zkBridgeFee}(dstChainId_, dstAddress, payload);

        emit TransferToken(sequence, dstChainId_, srcPoolId_, dstPoolId_, sender_, recipient_, amount_);
    }

    function zkReceive(uint16 srcChainId_, address srcAddress_, uint64 sequence_, bytes calldata payload_)
        external
        override
        nonReentrant
    {
        require(msg.sender == address(zkBridgeEndpoint), "Bridge: Not From ZKBridgeEntrypoint");

        require(srcAddress_ == bridgeLookup[srcChainId_], "Bridge: Invalid emitter");

        (uint256 srcPoolId, uint256 dstPoolId, uint256 amount, address recipient) =
            abi.decode(payload_, (uint256, uint256, uint256, address));

        router.withdraw(srcChainId_, dstPoolId, srcPoolId, amount, recipient);

        emit ReceiveToken(sequence_, srcChainId_, srcPoolId, dstPoolId, recipient, amount);
    }

    function estimateFee(uint16 dstChainId_, uint256 value_) public view returns (uint256) {
        uint256 uaFee = feeContract.getStaticFee(dstChainId_);
        uint256 zkBridgeFee = zkBridgeEndpoint.estimateFee(dstChainId_);
        return uaFee + zkBridgeFee;
    }

    function setBridge(uint16 chainId_, address bridge_) external onlyOwner {
        require(chainId_ > 0, "Bridge: Invalid chainid");
        require(address(bridge_) != address(0), "Bridge: Bridge cannot be zero address");

        bridgeLookup[chainId_] = bridge_;

        emit NewBridge(chainId_, bridge_);
    }

    function _setFeeContract(IFee feeContract_) internal {
        require(address(feeContract_) != address(0), "Bridge: feeContract cannot be zero address");
        feeContract = feeContract_;

        emit NewFeeContract(feeContract);
    }

    function setFeeContract(IFee feeContract_) external onlyOwner {
        _setFeeContract(feeContract_);
    }

    function setRouter(address payable router_) external onlyOwner {
        require(router_ != address(0), "Bridge: Router cannot be zero address");

        router = Router(router_);
    }

    function setZkBridgeEndpoint(address zkBridgeEndpoint_) external onlyOwner {
        require(zkBridgeEndpoint_ != address(0), "Bridge: ZkBridgeEndpoint cannot be zero address");

        zkBridgeEndpoint = IZKBridgeEndpoint(zkBridgeEndpoint_);
    }

    receive() external payable {}
}
"
    },
