    "contracts/BNBLottery.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/vrf/VRFConsumerBaseV2.sol";

contract BNBLottery is VRFConsumerBaseV2, Ownable, ReentrancyGuard {
    using Counters for Counters.Counter;
    Counters.Counter public roundId;
    struct RequestStatus {
        bool fulfilled; // whether the request has been successfully fulfilled
        bool exists; // whether a requestId exists
        uint256[] randomWords;
    }
    mapping(uint256 => RequestStatus)
        public s_requests; /* requestId --> requestStatus */

    bool public Paused;
    uint256 public SUBSCRIPTION_ID;
    bytes32 public KEY_HASH;
    VRFCoordinatorV2Interface VRF_COORDINATOR;

    mapping(uint256 => Round) public roundInfo;
    mapping(uint256 => mapping(uint256 => Ticket)) public roundTickets;
    mapping(address => mapping(uint256 => uint256)) public userRoundShare;
    mapping(address => uint256) public whiteListed;

    struct Ticket {
        uint256 round;
        address owner;
    }

    struct Round {
        uint256 id;
        uint256 total;
        uint256 index;
        address winner;
        uint256 winnerIndex;
        uint256 randomWord;
    }

    event RaffleEntered(address from, uint256 round, uint256 share);
    event RoundStart(uint256 round, uint256 timeStamp);
    event RoundClose(uint256 round, address winner, uint256 timeStamp);

    constructor(
        uint256 subscriptionId_,
        bytes32 keyHash_,
        address vrfCoordinator_
    ) VRFConsumerBaseV2(vrfCoordinator_) {
        SUBSCRIPTION_ID = subscriptionId_;
        KEY_HASH = keyHash_;
        VRF_COORDINATOR = VRFCoordinatorV2Interface(vrfCoordinator_);
        _initRound();
    }

    modifier callerIsUser() {
        require(tx.origin == msg.sender, "The caller is another contract");
        _;
    }

    modifier onlyWhiteListed() {
        require(whiteListed[msg.sender] > 0, "The caller is not whiteListed");
        _;
    }

    function _initRound() internal {
        roundId.increment();
        uint256 round = roundId.current();
        Round storage rI = roundInfo[round];
        rI.id = round;
        rI.total = 50;
        emit RoundStart(round, block.timestamp);
    }

    function enterRaffle(
        uint256 amount
    ) public payable nonReentrant callerIsUser {
        require(!Paused, "Paused");
        require(amount > 0, "Invalid amount");
        uint256 round = roundId.current();
        Round storage rI = roundInfo[round];
        uint256 index = rI.index;
        uint256 total = rI.total;

        uint256 share = index + amount > total ? total - index : amount;
        uint256 totalCost = share * 10 ** 16;

        refundIfOver(totalCost);
        uint256 lastIndex = index + amount > total ? total : index + amount;
        for (uint256 i = index; i < lastIndex; ) {
            Ticket storage ticket = roundTickets[round][i];
            ticket.round = round;
            ticket.owner = msg.sender;
            unchecked {
                ++i;
            }
        }

        userRoundShare[msg.sender][round] += share;
        rI.index += share;

        if (lastIndex == total) _drawWinner();
        emit RaffleEntered(msg.sender, round, share);
    }

    function freePlay() public onlyWhiteListed nonReentrant callerIsUser {
        require(!Paused, "Paused");
        uint256 round = roundId.current();
        Round storage rI = roundInfo[round];
        uint256 index = rI.index;
        uint256 total = rI.total;
        uint256 lastIndex = index + 1;

        Ticket storage ticket = roundTickets[round][index];
        ticket.round = round;
        ticket.owner = msg.sender;

        userRoundShare[msg.sender][round] += 1;
        rI.index += 1;

        if (lastIndex == total) _drawWinner();
        emit RaffleEntered(msg.sender, round, 1);
    }

    function _drawWinner() internal {
        uint256 requestId = VRF_COORDINATOR.requestRandomWords(
            KEY_HASH,
            uint64(SUBSCRIPTION_ID),
            3,
            500_000,
            1
        );
        s_requests[requestId] = RequestStatus({
            randomWords: new uint256[](0),
            exists: true,
            fulfilled: false
        });
    }

    function _startNewRound() internal returns (uint256) {
        roundId.increment();
        uint256 round = roundId.current();
        Round storage rI = roundInfo[round];
        rI.id = round;
        rI.total = 50;
        emit RoundStart(round, block.timestamp);
        return round;
    }

    function refundIfOver(uint256 price) private {
        require(msg.value >= price, "Need to send more ETH.");
        if (msg.value > price) {
            payable(msg.sender).transfer(msg.value - price);
        }
    }

    function getCurrentRound() public view returns (Round memory) {
        uint256 round = roundId.current();
        Round memory rI = roundInfo[round];
        return rI;
    }

    function getWinner(uint256 round) public view returns (address) {
        Round memory rI = roundInfo[round];
        return rI.winner;
    }

    function getUserRoundShare(
        address user,
        uint256 round
    ) public view returns (uint256) {
        return userRoundShare[user][round];
    }

    function fulfillRandomWords(
        uint256 _requestId,
        uint256[] memory _randomWords
    ) internal override {
        require(s_requests[_requestId].exists, "Request not found");
        s_requests[_requestId].fulfilled = true;
        s_requests[_requestId].randomWords = _randomWords;

        uint256 round = roundId.current();
        Round storage rI = roundInfo[round];
        uint256 total = rI.total;
        uint256 index = _randomWords[0] % total;

        address winner = roundTickets[round][index].owner;
        require(winner != address(0), "On going");
        (bool success, ) = winner.call{value: 0.45 ether}("");
        require(success, "ETH transfer failed.");
        rI.winnerIndex = index;
        rI.winner = winner;
        rI.randomWord = _randomWords[0];
        emit RoundClose(round, winner, block.timestamp);
        _startNewRound();
    }

    function pause() public onlyOwner {
        Paused = !Paused;
    }

    function configVRF(
        uint256 subscriptionId_,
        bytes32 keyHash_,
        address vrfCoordinator_
    ) public onlyOwner {
        SUBSCRIPTION_ID = subscriptionId_;
        KEY_HASH = keyHash_;
        VRF_COORDINATOR = VRFCoordinatorV2Interface(vrfCoordinator_);
    }

    function rescueToken(address token) public onlyOwner {
        uint256 amount = IERC20(token).balanceOf(address(this));
        IERC20(token).transfer(msg.sender, amount);
    }

    function withdraw() public onlyOwner {
        uint256 _balance = address(this).balance;
        require(_balance > 0, "No ETH to withdraw");
        (bool success, ) = msg.sender.call{value: _balance}("");
        require(success, "ETH transfer failed.");
    }

    receive() external payable {}
