pragma solidity ^0.8.11;

abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes calldata) {
        return msg.data;
    }
}

abstract contract Ownable is Context {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    /**
     * @dev Initializes the contract setting the deployer as the initial owner.
     */
    constructor() {
        _transferOwnership(_msgSender());
    }

    /**
     * @dev Returns the address of the current owner.
     */
    function owner() public view virtual returns (address) {
        return _owner;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(owner() == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    /**
     * @dev Leaves the contract without owner. It will not be possible to call
     * `onlyOwner` functions anymore. Can only be called by the current owner.
     *
     * NOTE: Renouncing ownership will leave the contract without an owner,
     * thereby removing any functionality that is only available to the owner.
     */
    function renounceOwnership() public virtual onlyOwner {
        _transferOwnership(address(0));
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Can only be called by the current owner.
     */
    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        _transferOwnership(newOwner);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Internal function without access restriction.
     */
    function _transferOwnership(address newOwner) internal virtual {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

interface IERC20 {
    /**
     * @dev Returns the amount of tokens in existence.
     */
    function totalSupply() external view returns (uint256);

    /**
     * @dev Returns the amount of tokens owned by `account`.
     */
    function balanceOf(address account) external view returns (uint256);

    /**
     * @dev Moves `amount` tokens from the caller's account to `recipient`.
     *
     * Returns a boolean value indicating whether the operation succeeded.
     *
     * Emits a {Transfer} event.
     */
    function transfer(address recipient, uint256 amount) external returns (bool);

    /**
     * @dev Returns the remaining number of tokens that `spender` will be
     * allowed to spend on behalf of `owner` through {transferFrom}. This is
     * zero by default.
     *
     * This value changes when {approve} or {transferFrom} are called.
     */
    function allowance(address owner, address spender) external view returns (uint256);

    /**
     * @dev Sets `amount` as the allowance of `spender` over the caller's tokens.
     *
     * Returns a boolean value indicating whether the operation succeeded.
     *
     * IMPORTANT: Beware that changing an allowance with this method brings the risk
     * that someone may use both the old and the new allowance by unfortunate
     * transaction ordering. One possible solution to mitigate this race
     * condition is to first reduce the spender's allowance to 0 and set the
     * desired value afterwards:
     * https://github.com/ethereum/EIPs/issues/20#issuecomment-263524729
     *
     * Emits an {Approval} event.
     */
    function approve(address spender, uint256 amount) external returns (bool);

    /**
     * @dev Moves `amount` tokens from `sender` to `recipient` using the
     * allowance mechanism. `amount` is then deducted from the caller's
     * allowance.
     *
     * Returns a boolean value indicating whether the operation succeeded.
     *
     * Emits a {Transfer} event.
     */
    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external returns (bool);

    /**
     * @dev Emitted when `value` tokens are moved from one account (`from`) to
     * another (`to`).
     *
     * Note that `value` may be zero.
     */
    event Transfer(address indexed from, address indexed to, uint256 value);

    /**
     * @dev Emitted when the allowance of a `spender` for an `owner` is set by
     * a call to {approve}. `value` is the new allowance.
     */
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

interface IERC20Metadata is IERC20 {
    /**
     * @dev Returns the name of the token.
     */
    function name() external view returns (string memory);

    /**
     * @dev Returns the symbol of the token.
     */
    function symbol() external view returns (string memory);

    /**
     * @dev Returns the decimals places of the token.
     */
    function decimals() external view returns (uint8);
}

contract GovernanceToken is Context, IERC20, IERC20Metadata, Ownable {
    mapping(address => uint256) internal _balances;

    mapping(address => mapping(address => uint256)) private _allowances;

    mapping(address => bool) private _isUSCitizen; // custom variable

    address[] private _usCitizenList;

    address private _nativeToken;

    uint8 private _decimals = 18;
    uint256 private _totalSupply = 100 * 10 ** _decimals;
    
    uint256 private _maxOwnableBalance = 5 * 10 ** _decimals; // custom variable
    uint256 private _requiredNativeTokenBalance = 60000; // custom variable

    uint256 private _usCitizenOwnableAmount = 51 * 10 ** _decimals; // custom variable
    uint256 private _unUSCitizenOwnableAmount = 49 * 10 ** _decimals; // custom variable

    uint256 private _usCitizenOwnedAmount; // custom variable
    uint256 private _unUSCitizenOwnedAmount; // custom variable

    string private _name;
    string private _symbol;

    constructor(string memory name_, string memory symbol_, address nativeToken_) {
        _nativeToken = nativeToken_;
        
        _name = name_;
        _symbol = symbol_;

        _mint(msg.sender, _totalSupply);
    }

    modifier onlyController {
        require(msg.sender == _nativeToken, "Only Governance Controller");
        _;
    }

    function name() public view virtual override returns (string memory) {
        return _name;
    }

    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view virtual override returns (uint256) {
        return _balances[account];
    }

    function getUsCitizenList() public view virtual returns(address[] memory) {
        return _usCitizenList;
    }

    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        _transfer(sender, recipient, amount);

        uint256 currentAllowance = _allowances[sender][_msgSender()];
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");
        unchecked {
            _approve(sender, _msgSender(), currentAllowance - amount);
        }

        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender] + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero");
        unchecked {
            _approve(_msgSender(), spender, currentAllowance - subtractedValue);
        }

        return true;
    }

    function setCitizenShip(address address_) public onlyOwner {
        require(_isUSCitizen[address_] == false, "GovernanceToken: this address already has citizenship");
        _isUSCitizen[address_] = true;
        _usCitizenList.push(address_);
    }

    function unsetCitizenShip(address address_) public onlyOwner {
        require(_isUSCitizen[address_], "GovernanceToken: this address has not citizenship");
        _isUSCitizen[address_] = false;

        for (uint i = 0; i < _usCitizenList.length; i++) {
            if (_usCitizenList[i] == address_) {
                _usCitizenList[i] = _usCitizenList[_usCitizenList.length - 1];
                _usCitizenList.pop();
            }
        }
    }

    function burn(address _account, uint256 _amount) external onlyController {
        _burn(_account, _amount);
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        

        /**
            custom requirements
         */
        require(balanceOf(recipient) + amount <= _maxOwnableBalance, "GovernanceToken: this token can not be over owned"); // custom statement - limit ownable balance
        require(IERC20(_nativeToken).balanceOf(recipient) >= (this.balanceOf(recipient) + amount) * _requiredNativeTokenBalance, "GovernanceToken: native token balance is not enough"); // custom statement - check native token balance to be owned
        
        require(_usCitizenOwnedAmount + amount <= _usCitizenOwnableAmount, "GovernanceToken: US citizen ownable balance over amount"); // custom statement - check US citizen ownable amount
        require(_unUSCitizenOwnedAmount + amount <= _unUSCitizenOwnableAmount, "GovernanceToken: non-US citizen ownable balance over amount"); // custom statement - check non-US citizen ownable amount
        
        /**
            citizenship process between owner(supplyer) and holders
         */
        if(sender == owner() && _isUSCitizen[recipient] == false) {
            _unUSCitizenOwnedAmount = _unUSCitizenOwnedAmount + amount;
        }
        else if(sender == owner() && _isUSCitizen[recipient] == true) {
            _usCitizenOwnedAmount = _usCitizenOwnedAmount + amount;
        }
        /**
            citizenship process between holders
         */
        else if(_isUSCitizen[sender] == true && _isUSCitizen[recipient] == false)
        {
            _usCitizenOwnedAmount = _usCitizenOwnedAmount - amount;
            _unUSCitizenOwnedAmount = _unUSCitizenOwnedAmount + amount;
        }
        else if(_isUSCitizen[sender] == false && _isUSCitizen[recipient] == true)
        {
            _unUSCitizenOwnedAmount = _unUSCitizenOwnedAmount - amount;
            _usCitizenOwnedAmount = _usCitizenOwnedAmount + amount;
        }

        _beforeTokenTransfer(sender, recipient, amount);

        uint256 senderBalance = _balances[sender];
        require(senderBalance >= amount, "ERC20: transfer amount exceeds balance");
        unchecked {
            _balances[sender] = senderBalance - amount;
        }
        _balances[recipient] += amount;

        emit Transfer(sender, recipient, amount);

        _afterTokenTransfer(sender, recipient, amount);
    }

    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply += amount;
        _balances[account] += amount;
        emit Transfer(address(0), account, amount);

        _afterTokenTransfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        uint256 accountBalance = _balances[account];
        require(accountBalance >= amount, "ERC20: burn amount exceeds balance");
        unchecked {
            _balances[account] = accountBalance - amount;
        }
        _totalSupply -= amount;

        emit Transfer(account, address(0), amount);

        _afterTokenTransfer(account, address(0), amount);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
}
