    "src/inbox/Inbox.sol": {
      "content": "// SPDX-License-Identifier: MIT OR Apache-2.0
pragma solidity ^0.8.13;

import {IVerifier} from "../interfaces/IVerifier.sol";
import {IWormhole} from "../interfaces/IWormhole.sol";
import {IHandler} from "../interfaces/IHandler.sol";
import {MessageLib} from "../shared/message/MessageLib.sol";
import {TokenMessageLib} from "../shared/message/TokenMessageLib.sol";
import {ExtendedMessageLib} from "../shared/message/ExtendedMessageLib.sol";
import {calculateReplayHash} from "../shared/message/BaseMessage.sol";
import {UsesCore} from "../shared/UsesCore.sol";
import {NonReentrant} from "../shared/NonReentrant.sol";

/// @title Inbox
/// @author The Warp Team <contact@warp.inc>
/// @notice This contract implements a generic cross-chain Inbox.
///
/// @dev The Inbox contract handles receiving messages from an off-chain
///      transport agent then delivering them to the correct handler
///      application.
///
/// @dev The Inbox is a verification-agnostic message deliverer. It allows
///      messages to be verified by any abstract Verifier contract, or the
///      Wormhole core contract.
///
/// @dev The Inbox checks that the message is addressed to this chain, that the
///      message has not been delivered already, that it was committed to by a
///      known Outbox, and that the message is valid according to the Verifier.
///      If all checks pass, the message is delivered to the Handler.
///
/// @dev When delivering the message to the Handler, the Inbox passes relevant
///      information including:
///      - The message itself.
///      - The address of the Verifier that validated the message.
///      - The address of the sender of the message.
///      - The chain id of the sender of the message.
abstract contract Inbox is UsesCore, NonReentrant {
    using MessageLib for MessageLib.Message;
    using TokenMessageLib for TokenMessageLib.TokenMessage;
    using ExtendedMessageLib for ExtendedMessageLib.ExtendedMessage;

    /// @notice Emitted when a message is received and delivered to a handler.
    /// @dev Topic0
    ///      0xb1f9608fc9a04b1041cff0ff42f903f47c60650e2cf5a50591327ce831ea07a5
    /// @param index The index of the message in the Outbox on the emitting
    ///        chain.
    /// @param emitterChainId The Wormhole chain id from which the message was
    ///        emitted.
    /// @param emitterAddress The Wormhole-formatted address of the contract
    ///        that emitted the message, presumably an Outbox.
    /// @param handler The address of the handler that processed the message.
    /// @param verifier The address of the verifier that validated the message.
    event Received(
        uint64 indexed index,
        uint16 indexed emitterChainId,
        bytes32 emitterAddress,
        address indexed handler,
        address verifier
    );

    /// @notice Emitted when a message is received and delivered to
    ///         a handler with extra un-verified data.
    /// @dev Identical to Received, but with a different topic0.
    /// @dev Topic0
    ///      0xa11231c10ad1681023b505b6836eace2b100967a3047848374598fe02df8a4ae
    /// @param index The index of the message in the Outbox on the emitting
    ///        chain.
    /// @param emitterChainId The Wormhole chain id from which the message was
    ///        emitted.
    ///        that emitted this message, presumably an Outbox.
    /// @param emitterAddress The Wormhole-formatted address of the contract.
    /// @param handler The address of the handler that processed the message.
    /// @param verifier The address of the verifier that validated the message.
    event ReceivedExt(
        uint64 indexed index,
        uint16 indexed emitterChainId,
        bytes32 emitterAddress,
        address indexed handler,
        address verifier
    );

    /// @notice Emitted when a message with tokensis received and delivered to
    ///         a handler.
    /// @dev Identical to Received, but with a different topic0.
    /// @dev Topic0
    ///      0x6a7d9c2416abb712bc96f9eff261297cfccc5781844a77ad2791826e4ba5161d
    /// @param index The index of the message in the Outbox on the emitting
    ///        chain.
    /// @param emitterChainId The Wormhole chain id from which the message was
    ///        emitted.
    /// @param emitterAddress The Wormhole-formatted address of the contract
    ///        that emitted the message, presumably an Outbox.
    /// @param handler The address of the handler that processed the message.
    /// @param verifier The address of the verifier that validated the message.
    event ReceivedWithTokens(
        uint64 indexed index,
        uint16 indexed emitterChainId,
        bytes32 emitterAddress,
        address indexed handler,
        address verifier
    );

    /// @notice Core rejected signed VAA.
    /// @dev Selector 0x8ee2e336.
    /// @param reason The reason the VAA was rejected, provided by the Wormhole
    ///        core contract.
    error InvalidVaa(string reason);

    /// @notice Message is not addressed to this chain.
    /// @dev Selector 0xf31052b3.
    /// @param _chainId The chain id to which the message ought to be delivered.
    error WrongTargetChain(uint16 _chainId);

    /// @notice Message has been delivered already, and cannot be delivered
    ///         again.
    /// @dev Selector 0xbf51ac51.
    error DisallowedDelivery();

    /// @notice Handler does not allow this messenger.
    /// @dev Selector 0xbe18d4f5.
    error DisallowedMessenger(uint16 chainId, bytes32 outbox);

    /// @notice Handler does not allow this verifier.
    /// @dev Selector 0x266fa8b5.
    error DisallowedVerifier(address verifier);

    /// @notice Chain has forked and message receipt is currently
    ///         disabled. This condition may be temporary, should
    ///         the guardian set restart service on this chain by
    ///         updating the stored EVM chain ID in Wormhole core.
    /// @dev Selector 0xf22c96c1.
    error Forked();

    /// @notice Receive a message, and validate it via a specified verifier.
    ///         If the witness is valid, deliver the message to the handler.
    /// @param verifier The address of the Verifier contract which will be used
    ///        to authenticate the message.
    /// @param witnessedStatement The witnessed statement to verify and deliver.
    ///        The format of this statement is Verifier-specific.
    /// @custom:reverts as verify
    /// @custom:reverts as deliver
    /// @custom:emits as deliver
    function recv(address verifier, bytes memory witnessedStatement) external nonReentrant {
        IVerifier.VerifiedStatement memory statement = verify(verifier, witnessedStatement);
        deliver(verifier, statement.emitterChainId, statement.emitterAddress, statement.message);
    }

    /// @notice Receive a message to be delivered with un-verified extra data.
    ///         Validate the message via the specified verifier.
    ///         While the message and witness are verified, the extra data is
    ///         not. If the witness is valid, deliver the message and the
    ///         un-verified extra data to the handler.
    ///         Applications MUST perform additional verification as necessary
    ///         before trusting the extra data.
    /// @dev Security notice: `recvExt` is not reentrance protected.
    ///      This choice was made to enable the delivery of
    ///      additional messages contained in `unverifiedRelayerData.`
    ///      App devs are responsible for ensuring that their `handleExt`
    ///      function is secure even if re-entered by this `Messenger` contract.
    /// @param verifier The address of the Verifier contract which will be used
    ///        to authenticate the message.
    /// @param witnessedStatement The witnessed statement to verify and deliver.
    ///        The format of this statement is Verifier-specific.
    /// @param unverifiedRelayerData The un-verified extra data to deliver.
    ///        This data is submitted by the message relayer,
    ///        and may contain any un-trusted data.
    ///        Applications may leverage this field to extend any extra
    ///        functionalitythat requires data not available at the time of
    ///        sending a message, such as (for example) additional signed
    ///        Wormhole VAAs.
    /// @custom:reverts as verify
    /// @custom:reverts as deliverExt
    /// @custom:emits as deliverExt
    function recvExt(address verifier, bytes memory witnessedStatement, bytes memory unverifiedRelayerData) external {
        IVerifier.VerifiedStatement memory statement = verify(verifier, witnessedStatement);
        deliverExt(
            verifier, statement.emitterChainId, statement.emitterAddress, statement.message, unverifiedRelayerData
        );
    }

    /// @notice Receive a message to be delivered with tokens.
    ///         Validate the message via the specified verifier.
    ///         If the witness is valid, deliver the message and
    ///         the token transfer data to the handler.
    ///         Applications MUST submit the transfer to the specified bridge
    ///         and verify that it results in a transfer of the expected
    ///         type and amount of tokens.
    /// @dev Security notice: `recvWithTokens` is not reentrance protected.
    ///      This choice was made to enable the delivery of
    ///      additional messages contained in `transfer` data
    ///      (e.g. a message delivering a Burn-and-Mint token built on this
    ///      contract).
    ///      App devs are responsible for ensuring that their `handleWithTokens`
    ///      function is secure even if re-entered by this `Messenger` contract.
    /// @param verifier The address of the Verifier contract which will be used
    ///        to authenticate the message.
    /// @param witnessedStatement The witnessed statement to verify and deliver.
    ///        The format of this statement is Verifier-specific.
    /// @param transfer The transfer to deliver. This is an opaque array of
    ///        bytes that contains bridge-specific asset transfer data. The
    ///        transfer is not verified.
    /// @custom:reverts as verify
    /// @custom:reverts as deliverWithTokens
    /// @custom:emits as deliverWithTokens
    function recvWithTokens(address verifier, bytes memory witnessedStatement, bytes memory transfer) external {
        IVerifier.VerifiedStatement memory statement = verify(verifier, witnessedStatement);
        deliverWithTokens(verifier, statement.emitterChainId, statement.emitterAddress, statement.message, transfer);
    }

    /// @notice Verify a witnessed statement using the specified verifier.
    /// @dev Verification is outsourced to a Verifier contract, which is
    ///      responsible for validating the witness and parsing the statement.
    ///      This contract is specified by the Relayer when delivering the
    ///      message via the recv function. The address of the Verifier is later
    ///      passed to the handler via the IHandler.acceptableVerifier function.
    /// @return statement - the parsed statement (iff the witness is valid)
    function verify(address verifier, bytes memory witnessedStatement)
        public
        view
        returns (IVerifier.VerifiedStatement memory statement)
    {
        if (verifier == address(core())) {
            statement = verifyVaa(witnessedStatement);
        } else {
            statement = IVerifier(verifier).verify(witnessedStatement);
        }
    }

    /// @dev This function is called by recv after verify. It implements the
    ///      logic for delivering a message to a handler.
    /// @param emitterChainId The chain id of the remote Outbox.
    /// @param emitterAddress The address of the remote Outbox (using Wormhole
    ///        VAA addressing) which emitted the message.
    /// @param encodedMessage The encoded message to deliver.
    /// @custom:reverts as MessageLib.decode
    /// @custom:reverts as extractEvmTarget
    /// @custom:reverts as checkPreconditions
    /// @custom:reverts as checkPreflights
    /// @custom:emits Received(emitterChainId, index, handler, verifier)
    function deliver(address verifier, uint16 emitterChainId, bytes32 emitterAddress, bytes memory encodedMessage)
        internal
    {
        // Decode
        MessageLib.Message memory message = MessageLib.decode(encodedMessage);
        uint64 index = message.index;
        IHandler handler = message.extractEvmTarget();

        // pre-checks
        checkPreconditions(message.targetChain);
        bytes32 replayHash = calculateReplayHash(emitterChainId, emitterAddress, index);
        checkPreflights(handler, verifier, emitterChainId, emitterAddress, replayHash);

        // emit
        emit Received(index, emitterChainId, emitterAddress, address(handler), verifier);

        // deliver
        handler.handle(emitterChainId, message.sender, replayHash, message.contents);
    }

    /// @dev This function is called by recvExt after verify. It
    ///      implements the logic for delivering a message to a handler
    ///      along with some extra un-trusted data.
    /// @dev Security notice: the handler MUST auth the verifier address and
    ///      MUST auth the (emitter chain, sender).
    /// @dev Security notice: the handler MUST auth the caller of handle.
    /// @param verifier The address of the Verifier contract which authenticated
    ///        the message.
    /// @param emitterChainId The chain id of the remote Outbox.
    /// @param emitterAddress The address of the remote Outbox (using Wormhole
    ///        VAA addressing) which emitted the message.
    /// @param encodedMessage The encoded message to deliver.
    /// @param unverifiedRelayerData The un-verified extra data to deliver.
    ///        This data is submitted by the message relayer,
    ///        and may contain any un-trusted data.
    ///        Applications may leverage this field to extend any extra
    ///        functionality that requires data not available at the time of
    ///        sending a message, such as (for example) additional signed
    ///        Wormhole VAAs.
    /// @custom:reverts as ExtendedMessageLib.decode
    /// @custom:reverts as extractEvmTarget
    /// @custom:reverts as checkPreconditions
    /// @custom:reverts as checkPreflights
    /// @custom:emits ReceivedExt(emitterChainId, index, handler, emitterAddress, verifier)
    function deliverExt(
        address verifier,
        uint16 emitterChainId,
        bytes32 emitterAddress,
        bytes memory encodedMessage,
        bytes memory unverifiedRelayerData
    ) internal {
        // Decode
        ExtendedMessageLib.ExtendedMessage memory message = ExtendedMessageLib.decode(encodedMessage);
        uint64 index = message.index;
        IHandler handler = message.extractEvmTarget();

        // pre-checks
        checkPreconditions(message.targetChain);
        bytes32 replayHash = calculateReplayHash(emitterChainId, emitterAddress, index);
        checkPreflights(handler, verifier, emitterChainId, emitterAddress, replayHash);

        // emit
        emit ReceivedExt(index, emitterChainId, emitterAddress, address(handler), verifier);

        // deliver
        handler.handleExt(
            emitterChainId, message.sender, replayHash, message.contents, message.relaySignal, unverifiedRelayerData
        );
    }

    /// @dev This function is called by recvWithTokens after verify. It
    ///      implements the logic for delivering a message with value to a
    ///      handler.
    /// @param verifier The address of the Verifier contract which authenticated
    ///        the message.
    /// @param emitterChainId The chain id of the remote Outbox.
    /// @param emitterAddress The address of the remote Outbox (using Wormhole
    ///        VAA addressing) which emitted the message.
    /// @param encodedMessage The encoded message to deliver.
    /// @param transfer The transfer to deliver. This is an opaque array of
    ///        bytes that contains bridge-specific asset transfer data. The
    ///        transfer is not verified.
    /// @custom:reverts as TokenMessageLib.decode
    /// @custom:reverts as extractEvmTarget
    /// @custom:reverts as checkPreconditions
    /// @custom:reverts as checkPreflights
    /// @custom:emits ReceivedWithTokens(emitterChainId, index, handler, emitterAddress, verifier)
    function deliverWithTokens(
        address verifier,
        uint16 emitterChainId,
        bytes32 emitterAddress,
        bytes memory encodedMessage,
        bytes memory transfer
    ) internal {
        // Decode
        TokenMessageLib.TokenMessage memory message = TokenMessageLib.decode(encodedMessage);
        uint64 index = message.index;
        IHandler handler = message.extractEvmTarget();

        // pre-checks
        checkPreconditions(message.targetChain);
        bytes32 replayHash = calculateReplayHash(emitterChainId, emitterAddress, index);
        checkPreflights(handler, verifier, emitterChainId, emitterAddress, replayHash);

        // emit
        emit ReceivedWithTokens(index, emitterChainId, emitterAddress, address(handler), verifier);

        // deliver
        handler.handleWithTokens(
            emitterChainId,
            message.sender,
            replayHash,
            message.contents,
            message.bridge,
            message.assetIdentifier,
            message.amount,
            transfer
        );
    }

    /// @notice Revert if the message is not addressed to this chain.
    /// @dev Ensures that messages are only delivered to the chain to which they
    ///      are addressed.
    /// @param _chainId The chain id to which the mesage is address, as provided
    ///        in the message itself, and authenticated by some Verifier.
    /// @custom:reverts WrongTargetChain(_chainId) if the message is not
    ///                 addressed to this chain.
    function checkTargetChain(uint16 _chainId) internal view {
        if (core().chainId() != _chainId) revert WrongTargetChain(_chainId);
    }

    /// @notice Revert if the core has detected a chain fork.
    /// @dev Core uses a stored EVM chain ID and compares it to the
    ///      current EVM chain ID. If they differ, the chain has forked.
    ///      To prevent uncertainty during forks, the Inbox stops receiving
    ///      messages until the guardian restarts the service by updating
    ///      the stored EVM chain ID in Wormhole core.
    /// @custom:reverts If the chain has forked.
    function checkNotForked() internal view {
        if (core().isFork()) revert Forked();
    }

    /// @notice Check preconditions for a message. The preconditions are
    ///         - This chain is not known to be forked.
    ///         - The target chain is this chain.
    /// @dev Simple wrapper for above check____ functions.
    /// @param targetChain The chain id to which the mesage is addressed, as
    ///        provided in the message itself, and authenticated by some
    ///        Verifier.
    /// @custom:reverts as checkNotForked
    /// @custom:reverts as checkTargetChain
    function checkPreconditions(uint16 targetChain) internal view {
        checkNotForked();
        checkTargetChain(targetChain);
    }

    /// @notice Check preflights for a message. The Preflight checks are
    ///        - The handler accepts the outbox
    ///        - The handler accepts the verifier
    /// @dev Preflights are distinct from preconditions because they are
    ///      not required for the message to be valid, but rather are
    ///      required for the message to be delivered to the handler.
    /// @dev Preflights provide a mechanism for an application to configure
    ///      its trust model in any way it pleases.
    /// @param handler The handler contract.
    /// @param verifier The verifier contract address that was used  to verify
    ///        the message.
    /// @param emitterChain The Wormhole chain ID of the emitter.
    /// @param emitterAddress The address of the emitter on that chain.
    /// @param replayHash A protocol-specific unique identifier for the message
    /// @custom:reverts DisallowedMessenger(emitterChain, emitterAddress) If the
    ///                 handler does not accept the outbox.
    /// @custom:reverts DisallowedVerifier(verifier) If the handler does not
    ///                 accept the verifier.
    /// @custom:reverts DisallowedDelivery() If the handler does not accept the delivery due to message having been processed already.
    function checkPreflights(
        IHandler handler,
        address verifier,
        uint16 emitterChain,
        bytes32 emitterAddress,
        bytes32 replayHash
    ) internal view {
        if (!handler.acceptableMessenger(emitterChain, emitterAddress)) {
            revert DisallowedMessenger(emitterChain, emitterAddress);
        }
        if (!handler.acceptableVerifier(verifier)) revert DisallowedVerifier(verifier);
        if (!handler.acceptableDelivery(replayHash)) revert DisallowedDelivery();
    }

    /// @notice Verify a Wormhole core witnessed statement using
    ///         parseAndVerifyVM
    /// @dev This is a special-case Verifier for Wormhole core contracts. It
    ///      essentially is a local shim to make the core conform to the
    ///      Verifier interface. It is used by the recvVaa function to verify
    ///      input that specifies the core as the Verifier.
    /// @dev MUST revert if the VM is not valid.
    /// @param vaa The puported VAA to verify and parse.
    /// @return statement - the statement parsed from the VM (iff the Wormhole
    ///         witness is valid).
    /// @custom:reverts InvalidVaa(reason) if the VM is not valid.
    function verifyVaa(bytes memory vaa) internal view returns (IVerifier.VerifiedStatement memory statement) {
        // call Wormhole core parseAndVerifyVM
        IWormhole.VM memory vm;
        bool result;
        string memory reason;
        (vm, result, reason) = core().parseAndVerifyVM(vaa);
        // revert if VM is not valid
        if (!result) revert InvalidVaa(reason);
        // transform VM to VerifiedStatement and return
        statement.emitterChainId = vm.emitterChainId;
        statement.emitterAddress = vm.emitterAddress;
        statement.message = vm.payload;
    }
}
"
    },
    "src/outbox/Outbox.sol": {
      "content": "// SPDX-License-Identifier: MIT OR Apache-2.0
pragma solidity ^0.8.13;

import {RingBufferStorage} from "./RingBufferStorage.sol";
import {UsesCore} from "../shared/UsesCore.sol";
import {MessageLib} from "../shared/message/MessageLib.sol";
import {TokenMessageLib} from "../shared/message/TokenMessageLib.sol";
import {ExtendedMessageLib} from "../shared/message/ExtendedMessageLib.sol";

/// @title Outbox
/// @author The Warp Team <contact@warp.inc>
/// @notice This contract implements a generic cross-chain Outbox.
///
/// @dev The Outbox is a transport-agnostic message sender. It allows
///      applications to send messages 2 ways:
///        1. By publishing a message via the Wormhole core contract.
///        2. By storing the message hash in the Outbox contract state.
///      This ensures that messages may always be signed by the Wormhole
///      guardians, AND may be proven to any light client or oracle-based
///      verifier via Merkle-Patricia trie proofs retrieved via the
///      eth_getProof RPC method.
///
/// @dev Message hashes are stored in a ring buffer, and given an index in the
///      history of all messages. The message hash may be proven via MPT proof
///      while it is in the ring buffer. Once the message is removed from the
///      ring buffer, it may no longer be proven via MPT proof under a current
///      contract storage proof, but any archive node may still generate the
///      historical proof.
abstract contract Outbox is UsesCore, RingBufferStorage(2 ** 15) {
    using MessageLib for MessageLib.Message;
    using TokenMessageLib for TokenMessageLib.TokenMessage;
    using ExtendedMessageLib for ExtendedMessageLib.ExtendedMessage;

    /// @notice CONSISTENCY is passed as consistencyLevel to Wormhole Core
    ///         consistencyLevel 202 indicates final messages on all chains
    uint8 public constant CONSISTENCY = 202;

    /// @notice Emitted when a message is sent
    /// @dev Topic 0
    ///      0x02dee5df1fa0e8120f2e774312add7033b258f9c15fcb2c66c4f559bb507fa55
    /// @param index The index of the message in the history of all messages.
    /// @param wormholeSequence The sequence number of the message in the
    ///        Wormhole core send response.
    /// @param targetChain The Wormhole chain ID of the destination chain.
    /// @param targetAddress The address of the recipient on the targetChain.
    /// @param message The full encoded message.
    event Sent(
        uint64 indexed index,
        uint64 wormholeSequence,
        uint16 targetChain,
        bytes indexed targetAddress,
        bytes indexed message
    );

    /// @notice Emitted when a message is sent
    ///         that is meant to be delivered with extra un-verified data
    /// @dev Topic 0
    ///      0xbc107fad92d8ed23e5f0b2abe135332ab9bf80e68e9b6a25c0834fe38ced089b
    /// @param index The index of the message in the history of all messages.
    /// @param wormholeSequence The sequence number of the message in the
    ///        Wormhole core send response.
    /// @param targetChain The Wormhole chain ID of the destination chain.
    /// @param targetAddress The address of the recipient on the targetChain.
    /// @param message The full encoded message.
    event SentExt(
        uint64 indexed index,
        uint64 wormholeSequence,
        uint16 targetChain,
        bytes indexed targetAddress,
        bytes indexed message
    );

    /// @notice Emitted when a message with tokens is sent.
    /// @dev Identical body to Sent, but with a different topic0.
    /// @dev Topic 0
    ///      0xec6a9cdec02327573aacebdab9311dc8dbd12d36cd56d585081142d9f19aabb1
    /// @param index The index of the message in the history of all messages.
    /// @param wormholeSequence The sequence number of the message in the
    ///        Wormhole core send response.
    /// @param targetChain The Wormhole chain ID of the destination chain.
    /// @param targetAddress The address of the recipient on the targetChain.
    /// @param message The full encoded message.
    event SentWithTokens(
        uint64 indexed index,
        uint64 wormholeSequence,
        uint16 targetChain,
        bytes indexed targetAddress,
        bytes indexed message
    );

    /// @notice Send a message to the the target on the target chain.
    /// @dev Emits the message via Wormhole core, and stores the message
    ///      hash in the ring buffer.
    /// @param targetChain The target chain.
    /// @param targetAddress The target address on the target chain.
    /// @param contents The message contents.
    /// @return index The index of the message in the history of all messages.
    /// @return sequence The sequence number of the message returned by
    ///         Wormhole core.
    /// @custom:reverts As MessageLib.encode may revert.
    /// @custom:emits Sent(index, sequence, targetChain, targetAddress, encoded)
    function send(uint16 targetChain, bytes calldata targetAddress, bytes calldata contents)
        external
        returns (uint64 index, uint64 sequence)
    {
        return send(pack(targetChain, targetAddress, contents));
    }

    /// @notice Send a message to the the target address on the target chain.
    /// @dev Alias for `send` when sending to EVM chains specifically.
    /// @param targetChain The target chain.
    /// @param targetAddress The target address on the target chain.
    /// @param contents The message contents.
    /// @return index The index of the message in the history of all messages.
    /// @return sequence The sequence number of the message returned by
    ///         Wormhole core.
    /// @custom:reverts As MessageLib.encode may revert.
    /// @custom:emits Sent(index, sequence, targetChain, targetAddress, encoded)
    function send(uint16 targetChain, address targetAddress, bytes calldata contents)
        external
        returns (uint64 index, uint64 sequence)
    {
        return send(pack(targetChain, abi.encodePacked(targetAddress), contents));
    }

    /// @notice Send a message to the the target on the target chain.
    /// @dev Emits the message via Wormhole core, and stores the message
    ///      hash in the ring buffer.
    /// @param targetChain The target chain.
    /// @param targetAddress The target address on the target chain.
    /// @param contents The message contents.
    /// @param relaySignal Meant to signal to off-chain relayers what type of ExtendedMessage this is.
    /// @return index The index of the message in the history of all messages.
    /// @return sequence The sequence number of the message returned by
    ///         Wormhole core.
    /// @custom:reverts As ExtendedMessageLib.encode may revert.
    /// @custom:emits SentExt(index, sequence, targetChain, targetAddress, encoded)
    function sendExt(
        uint16 targetChain,
        bytes calldata targetAddress,
        bytes calldata contents,
        bytes calldata relaySignal
    ) external returns (uint64 index, uint64 sequence) {
        return sendExt(pack(targetChain, targetAddress, contents, relaySignal));
    }

    /// @notice Send a message to the the target address on the target chain.
    /// @dev Alias for `sendExt` when sending to EVM chains specifically.
    /// @param targetChain The target chain.
    /// @param targetAddress The target address on the target chain.
    /// @param contents The message contents.
    /// @param relaySignal Meant to signal to off-chain relayers what type of ExtendedMessage this is.
    /// @return index The index of the message in the history of all messages.
    /// @return sequence The sequence number of the message returned by
    ///         Wormhole core.
    /// @custom:reverts As ExtendedMessageLib.encode may revert.
    /// @custom:emits SentExt(index, sequence, targetChain, targetAddress, encoded)
    function sendExt(uint16 targetChain, address targetAddress, bytes calldata contents, bytes calldata relaySignal)
        external
        returns (uint64 index, uint64 sequence)
    {
        return sendExt(pack(targetChain, abi.encodePacked(targetAddress), contents, relaySignal));
    }

    /// @notice Send a message to the the target on the target chain, along with
    ///         a header indicating that the message should be delivered
    ///         alongside some asset transfer. This feature is intended to be
    ///         used alongside CCTP or other asset transfer protocols. This
    ///         message type is used to ensure that a message arrives WITH the
    ///         asset transfer, and is not delivered before or after. The
    ///         application must ensure that the message is delivered alongside
    ///         a transfer of the specified asset and amount via the specified
    ///         asset bridge.
    /// @dev The Messenger does NOT handle sending the assets or verifying
    ///      receipt. The application MUST coordinate both of these actions.
    /// @dev Emits the message via Wormhole core, and stores the message
    ///      hash in the ring buffer.
    /// @param targetChain The target chain.
    /// @param targetAddress The target address on the target chain.
    /// @param contents The message contents.
    /// @param bridge The bridge used for asset transfer.
    /// @param assetIdentifier The asset identifier for the message.
    /// @param amount The amount of value to expect with the message.
    /// @return index The index of the message in the history of all messages.
    /// @return sequence The sequence number of the message returned by
    ///         Wormhole core.
    /// @custom:reverts As TokenMessageLib.encode may revert.
    /// @custom:emits SentWithTokens(index, sequence, targetChain, targetAddress, encoded)
    function sendWithTokens(
        uint16 targetChain,
        bytes calldata targetAddress,
        bytes calldata contents,
        uint8 bridge,
        bytes calldata assetIdentifier,
        uint256 amount
    ) external returns (uint64 index, uint64 sequence) {
        return sendWithTokens(pack(targetChain, targetAddress, contents, bridge, assetIdentifier, amount));
    }

    /// @notice Send a message to the the target on the target chain.
    /// @dev Alias for `sendWithTokens` when sending to EVM chains specifically.
    /// @dev Emits the message via Wormhole core, and stores the message
    ///      hash in the ring buffer.
    /// @param targetChain The target chain.
    /// @param targetAddress The target address on the target chain.
    /// @param contents The message contents.
    /// @param bridge The bridge used for asset transfer.
    /// @param assetIdentifier The asset identifier for the message.
    /// @param amount The amount of value to expect with the message.
    /// @return index The index of the message in the history of all messages.
    /// @return sequence The sequence number of the message returned by
    ///         Wormhole core.
    /// @custom:reverts As TokenMessageLib.encode may revert.
    /// @custom:emits SentWithTokens(index, sequence, targetChain, targetAddress, encoded)
    function sendWithTokens(
        uint16 targetChain,
        address targetAddress,
        bytes calldata contents,
        uint8 bridge,
        bytes calldata assetIdentifier,
        uint256 amount
    ) external returns (uint64 index, uint64 sequence) {
        return sendWithTokens(
            pack(targetChain, abi.encodePacked(targetAddress), contents, bridge, assetIdentifier, amount)
        );
    }

    /// @notice Send a message by publishing it via the Wormhole core and also
    ///         storing the hash.
    /// @dev This internal send function MUST operate on a packed message.
    /// @param encoded The packed message.
    /// @return index The index of the message in the history of all messages.
    /// @return sequence The sequence number of the message returned by
    ///         Wormhole core.
    function sendBytes(bytes memory encoded) internal returns (uint64 index, uint64 sequence) {
        index = pushMessage(keccak256(encoded));
        sequence = core().publishMessage(0, encoded, CONSISTENCY);
    }

    /// @notice Send a message by publishing it via the Wormhole core and also
    ///         storing the hash.
    /// @dev This internal send function MUST operate on a packed message.
    /// @param message The message.
    /// @return index The index of the message in the history of all messages.
    /// @return sequence The sequence number of the message returned by
    ///         Wormhole core.
    /// @custom:reverts As MessageLib.encode may revert.
    /// @custom:emits Sent(index, sequence, targetChain, targetAddress, encoded)
    function send(MessageLib.Message memory message) internal returns (uint64 index, uint64 sequence) {
        bytes memory encoded = message.encode();
        (index, sequence) = sendBytes(encoded);
        emit Sent(index, sequence, message.targetChain, message.targetAddress, encoded);
    }

    /// @notice Send a message by publishing it via the Wormhole core and also
    ///         storing the hash.
    /// @dev This internal send function MUST operate on a packed message.
    /// @param message The message.
    /// @return index The index of the message in the history of all messages.
    /// @return sequence The sequence number of the message returned by
    ///         Wormhole core.
    /// @custom:reverts As ExtendedMessageLib.encode may revert.
    /// @custom:emits SentExt(index, sequence, targetChain, targetAddress, encoded)
    function sendExt(ExtendedMessageLib.ExtendedMessage memory message)
        internal
        returns (uint64 index, uint64 sequence)
    {
        bytes memory encoded = message.encode();
        (index, sequence) = sendBytes(encoded);
        emit SentExt(index, sequence, message.targetChain, message.targetAddress, encoded);
    }

    /// @notice Send a message by publishing it via the Wormhole core and also
    ///         storing the hash.
    /// @dev This internal send function MUST operate on a packed message.
    /// @param message The message.
    /// @return index The index of the message in the history of all messages.
    /// @return sequence The sequence number of the message returned by
    ///         Wormhole core.
    /// @custom:reverts As TokenMessageLib.encode may revert.
    /// @custom:emits SentWithTokens(index, sequence, targetChain, targetAddress, encoded)
    function sendWithTokens(TokenMessageLib.TokenMessage memory message)
        internal
        returns (uint64 index, uint64 sequence)
    {
        bytes memory encoded = message.encode();
        (index, sequence) = sendBytes(encoded);
        emit SentWithTokens(index, sequence, message.targetChain, message.targetAddress, encoded);
    }

    /// @notice Pack a message, prepping it for sending.
    /// @dev Packing a message is the process of associating the target info
    ///      and the message index with the contents. The packed message will be
    ///      encoded before being sent via the Wormhole core contract, and
    ///      hashed  before being stored in the ring buffer.
    /// @param targetChain The target chain.
    /// @param targetAddress The target address on the target chain.
    /// @param contents The message contents.
    /// @return message The packed message.
    function pack(uint16 targetChain, bytes memory targetAddress, bytes calldata contents)
        internal
        view
        returns (MessageLib.Message memory message)
    {
        message = MessageLib.Message({
            index: uint64(nextIndex()),
            targetChain: targetChain,
            targetAddress: targetAddress,
            sender: abi.encodePacked(msg.sender),
            contents: contents
        });
    }

    /// @notice Pack an ExtendedMessage, prepping it for sending.
    /// @dev Packing a message is the process of associating the target info
    ///      and the message index with the contents. The packed message will be
    ///      encoded before being sent via the Wormhole core contract, and
    ///      hashed  before being stored in the ring buffer.
    /// @param targetChain The target chain.
    /// @param targetAddress The target address on the target chain.
    /// @param contents The message contents.
    /// @param relaySignal Extra message data used to signal to relayers.
    /// @return message The packed message.
    function pack(uint16 targetChain, bytes memory targetAddress, bytes calldata contents, bytes calldata relaySignal)
        internal
        view
        returns (ExtendedMessageLib.ExtendedMessage memory message)
    {
        message = ExtendedMessageLib.ExtendedMessage({
            index: uint64(nextIndex()),
            targetChain: targetChain,
            targetAddress: targetAddress,
            sender: abi.encodePacked(msg.sender),
            contents: contents,
            relaySignal: relaySignal
        });
    }

    /// @notice Pack a message with value, prepping it for sending.
    /// @dev Packing a message is the process of associating the target info
    ///      and the message index with the contents. The packed message will be
    ///      encoded before being sent via the Wormhole core contract, and
    ///      hashed  before being stored in the ring buffer.
    /// @param targetChain The target chain.
    /// @param targetAddress The target address on the target chain.
    /// @param contents The message contents.
    /// @param bridge The application-specific bridge identifier.
    /// @param assetIdentifier The asset identifier.
    /// @param amount The amount of the asset expected to be transferred.
    /// @return message The packed message.
    function pack(
        uint16 targetChain,
        bytes memory targetAddress,
        bytes calldata contents,
        uint8 bridge,
        bytes memory assetIdentifier,
        uint256 amount
    ) internal view returns (TokenMessageLib.TokenMessage memory message) {
        message = TokenMessageLib.TokenMessage({
            index: uint64(nextIndex()),
            targetChain: targetChain,
            targetAddress: targetAddress,
            sender: abi.encodePacked(msg.sender),
            contents: contents,
            bridge: bridge,
            assetIdentifier: assetIdentifier,
            amount: amount
        });
    }
}
"
    },
    "lib/forge-std/src/Base.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.6.2 <0.9.0;

import {StdStorage} from "./StdStorage.sol";
import {Vm, VmSafe} from "./Vm.sol";

abstract contract CommonBase {
    // Cheat code address, 0x7109709ECfa91a80626fF3989D68f67F5b1DD12D.
    address internal constant VM_ADDRESS = address(uint160(uint256(keccak256("hevm cheat code"))));
    // console.sol and console2.sol work by executing a staticcall to this address.
    address internal constant CONSOLE = 0x000000000000000000636F6e736F6c652e6c6f67;
    // Default address for tx.origin and msg.sender, 0x1804c8AB1F12E6bbf3894d4083f33e07309d1f38.
    address internal constant DEFAULT_SENDER = address(uint160(uint256(keccak256("foundry default caller"))));
    // Address of the test contract, deployed by the DEFAULT_SENDER.
    address internal constant DEFAULT_TEST_CONTRACT = 0x5615dEB798BB3E4dFa0139dFa1b3D433Cc23b72f;
    // Deterministic deployment address of the Multicall3 contract.
    address internal constant MULTICALL3_ADDRESS = 0xcA11bde05977b3631167028862bE2a173976CA11;

    uint256 internal constant UINT256_MAX =
        115792089237316195423570985008687907853269984665640564039457584007913129639935;

    Vm internal constant vm = Vm(VM_ADDRESS);
    StdStorage internal stdstore;
}

abstract contract TestBase is CommonBase {}

abstract contract ScriptBase is CommonBase {
    // Used when deploying with create2, https://github.com/Arachnid/deterministic-deployment-proxy.
    address internal constant CREATE2_FACTORY = 0x4e59b44847b379578588920cA78FbF26c0B4956C;

    VmSafe internal constant vmSafe = VmSafe(VM_ADDRESS);
}
"
