    "lib/forge-std/src/Vm.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.6.2 <0.9.0;

pragma experimental ABIEncoderV2;

// Cheatcodes are marked as view/pure/none using the following rules:
// 0. A call's observable behaviour includes its return value, logs, reverts and state writes,
// 1. If you can influence a later call's observable behaviour, you're neither `view` nor `pure (you are modifying some state be it the EVM, interpreter, filesystem, etc),
// 2. Otherwise if you can be influenced by an earlier call, or if reading some state, you're `view`,
// 3. Otherwise you're `pure`.

interface VmSafe {
    struct Log {
        bytes32[] topics;
        bytes data;
        address emitter;
    }

    struct Rpc {
        string key;
        string url;
    }

    struct FsMetadata {
        bool isDir;
        bool isSymlink;
        uint256 length;
        bool readOnly;
        uint256 modified;
        uint256 accessed;
        uint256 created;
    }

    // Loads a storage slot from an address
    function load(address target, bytes32 slot) external view returns (bytes32 data);
    // Signs data
    function sign(uint256 privateKey, bytes32 digest) external pure returns (uint8 v, bytes32 r, bytes32 s);
    // Gets the address for a given private key
    function addr(uint256 privateKey) external pure returns (address keyAddr);
    // Gets the nonce of an account
    function getNonce(address account) external view returns (uint64 nonce);
    // Performs a foreign function call via the terminal
    function ffi(string[] calldata commandInput) external returns (bytes memory result);
    // Sets environment variables
    function setEnv(string calldata name, string calldata value) external;
    // Reads environment variables, (name) => (value)
    function envBool(string calldata name) external view returns (bool value);
    function envUint(string calldata name) external view returns (uint256 value);
    function envInt(string calldata name) external view returns (int256 value);
    function envAddress(string calldata name) external view returns (address value);
    function envBytes32(string calldata name) external view returns (bytes32 value);
    function envString(string calldata name) external view returns (string memory value);
    function envBytes(string calldata name) external view returns (bytes memory value);
    // Reads environment variables as arrays
    function envBool(string calldata name, string calldata delim) external view returns (bool[] memory value);
    function envUint(string calldata name, string calldata delim) external view returns (uint256[] memory value);
    function envInt(string calldata name, string calldata delim) external view returns (int256[] memory value);
    function envAddress(string calldata name, string calldata delim) external view returns (address[] memory value);
    function envBytes32(string calldata name, string calldata delim) external view returns (bytes32[] memory value);
    function envString(string calldata name, string calldata delim) external view returns (string[] memory value);
    function envBytes(string calldata name, string calldata delim) external view returns (bytes[] memory value);
    // Read environment variables with default value
    function envOr(string calldata name, bool defaultValue) external returns (bool value);
    function envOr(string calldata name, uint256 defaultValue) external returns (uint256 value);
    function envOr(string calldata name, int256 defaultValue) external returns (int256 value);
    function envOr(string calldata name, address defaultValue) external returns (address value);
    function envOr(string calldata name, bytes32 defaultValue) external returns (bytes32 value);
    function envOr(string calldata name, string calldata defaultValue) external returns (string memory value);
    function envOr(string calldata name, bytes calldata defaultValue) external returns (bytes memory value);
    // Read environment variables as arrays with default value
    function envOr(string calldata name, string calldata delim, bool[] calldata defaultValue)
        external
        returns (bool[] memory value);
    function envOr(string calldata name, string calldata delim, uint256[] calldata defaultValue)
        external
        returns (uint256[] memory value);
    function envOr(string calldata name, string calldata delim, int256[] calldata defaultValue)
        external
        returns (int256[] memory value);
    function envOr(string calldata name, string calldata delim, address[] calldata defaultValue)
        external
        returns (address[] memory value);
    function envOr(string calldata name, string calldata delim, bytes32[] calldata defaultValue)
        external
        returns (bytes32[] memory value);
    function envOr(string calldata name, string calldata delim, string[] calldata defaultValue)
        external
        returns (string[] memory value);
    function envOr(string calldata name, string calldata delim, bytes[] calldata defaultValue)
        external
        returns (bytes[] memory value);
    // Records all storage reads and writes
    function record() external;
    // Gets all accessed reads and write slot from a recording session, for a given address
    function accesses(address target) external returns (bytes32[] memory readSlots, bytes32[] memory writeSlots);
    // Gets the _creation_ bytecode from an artifact file. Takes in the relative path to the json file
    function getCode(string calldata artifactPath) external view returns (bytes memory creationBytecode);
    // Gets the _deployed_ bytecode from an artifact file. Takes in the relative path to the json file
    function getDeployedCode(string calldata artifactPath) external view returns (bytes memory runtimeBytecode);
    // Labels an address in call traces
    function label(address account, string calldata newLabel) external;
    // Using the address that calls the test contract, has the next call (at this call depth only) create a transaction that can later be signed and sent onchain
    function broadcast() external;
    // Has the next call (at this call depth only) create a transaction with the address provided as the sender that can later be signed and sent onchain
    function broadcast(address signer) external;
    // Has the next call (at this call depth only) create a transaction with the private key provided as the sender that can later be signed and sent onchain
    function broadcast(uint256 privateKey) external;
    // Using the address that calls the test contract, has all subsequent calls (at this call depth only) create transactions that can later be signed and sent onchain
    function startBroadcast() external;
    // Has all subsequent calls (at this call depth only) create transactions with the address provided that can later be signed and sent onchain
    function startBroadcast(address signer) external;
    // Has all subsequent calls (at this call depth only) create transactions with the private key provided that can later be signed and sent onchain
    function startBroadcast(uint256 privateKey) external;
    // Stops collecting onchain transactions
    function stopBroadcast() external;
    // Reads the entire content of file to string
    function readFile(string calldata path) external view returns (string memory data);
    // Reads the entire content of file as binary. Path is relative to the project root.
    function readFileBinary(string calldata path) external view returns (bytes memory data);
    // Get the path of the current project root
    function projectRoot() external view returns (string memory path);
    // Get the metadata for a file/directory
    function fsMetadata(string calldata fileOrDir) external returns (FsMetadata memory metadata);
    // Reads next line of file to string
    function readLine(string calldata path) external view returns (string memory line);
    // Writes data to file, creating a file if it does not exist, and entirely replacing its contents if it does.
    function writeFile(string calldata path, string calldata data) external;
    // Writes binary data to a file, creating a file if it does not exist, and entirely replacing its contents if it does.
    // Path is relative to the project root.
    function writeFileBinary(string calldata path, bytes calldata data) external;
    // Writes line to file, creating a file if it does not exist.
    function writeLine(string calldata path, string calldata data) external;
    // Closes file for reading, resetting the offset and allowing to read it from beginning with readLine.
    function closeFile(string calldata path) external;
    // Removes file. This cheatcode will revert in the following situations, but is not limited to just these cases:
    // - Path points to a directory.
    // - The file doesn't exist.
    // - The user lacks permissions to remove the file.
    function removeFile(string calldata path) external;
    // Convert values to a string
    function toString(address value) external pure returns (string memory stringifiedValue);
    function toString(bytes calldata value) external pure returns (string memory stringifiedValue);
    function toString(bytes32 value) external pure returns (string memory stringifiedValue);
    function toString(bool value) external pure returns (string memory stringifiedValue);
    function toString(uint256 value) external pure returns (string memory stringifiedValue);
    function toString(int256 value) external pure returns (string memory stringifiedValue);
    // Convert values from a string
    function parseBytes(string calldata stringifiedValue) external pure returns (bytes memory parsedValue);
    function parseAddress(string calldata stringifiedValue) external pure returns (address parsedValue);
    function parseUint(string calldata stringifiedValue) external pure returns (uint256 parsedValue);
    function parseInt(string calldata stringifiedValue) external pure returns (int256 parsedValue);
    function parseBytes32(string calldata stringifiedValue) external pure returns (bytes32 parsedValue);
    function parseBool(string calldata stringifiedValue) external pure returns (bool parsedValue);
    // Record all the transaction logs
    function recordLogs() external;
    // Gets all the recorded logs
    function getRecordedLogs() external returns (Log[] memory logs);
    // Derive a private key from a provided mnenomic string (or mnenomic file path) at the derivation path m/44'/60'/0'/0/{index}
    function deriveKey(string calldata mnemonic, uint32 index) external pure returns (uint256 privateKey);
    // Derive a private key from a provided mnenomic string (or mnenomic file path) at {derivationPath}{index}
    function deriveKey(string calldata mnemonic, string calldata derivationPath, uint32 index)
        external
        pure
        returns (uint256 privateKey);
    // Adds a private key to the local forge wallet and returns the address
    function rememberKey(uint256 privateKey) external returns (address keyAddr);
    //
    // parseJson
    //
    // ----
    // In case the returned value is a JSON object, it's encoded as a ABI-encoded tuple. As JSON objects
    // don't have the notion of ordered, but tuples do, they JSON object is encoded with it's fields ordered in
    // ALPHABETICAL order. That means that in order to successfully decode the tuple, we need to define a tuple that
    // encodes the fields in the same order, which is alphabetical. In the case of Solidity structs, they are encoded
    // as tuples, with the attributes in the order in which they are defined.
    // For example: json = { 'a': 1, 'b': 0xa4tb......3xs}
    // a: uint256
    // b: address
    // To decode that json, we need to define a struct or a tuple as follows:
    // struct json = { uint256 a; address b; }
    // If we defined a json struct with the opposite order, meaning placing the address b first, it would try to
    // decode the tuple in that order, and thus fail.
    // ----
    // Given a string of JSON, return it as ABI-encoded
    function parseJson(string calldata json, string calldata key) external pure returns (bytes memory abiEncodedData);
    function parseJson(string calldata json) external pure returns (bytes memory abiEncodedData);

    // The following parseJson cheatcodes will do type coercion, for the type that they indicate.
    // For example, parseJsonUint will coerce all values to a uint256. That includes stringified numbers '12'
    // and hex numbers '0xEF'.
    // Type coercion works ONLY for discrete values or arrays. That means that the key must return a value or array, not
    // a JSON object.
    function parseJsonUint(string calldata, string calldata) external returns (uint256);
    function parseJsonUintArray(string calldata, string calldata) external returns (uint256[] memory);
    function parseJsonInt(string calldata, string calldata) external returns (int256);
    function parseJsonIntArray(string calldata, string calldata) external returns (int256[] memory);
    function parseJsonBool(string calldata, string calldata) external returns (bool);
    function parseJsonBoolArray(string calldata, string calldata) external returns (bool[] memory);
    function parseJsonAddress(string calldata, string calldata) external returns (address);
    function parseJsonAddressArray(string calldata, string calldata) external returns (address[] memory);
    function parseJsonString(string calldata, string calldata) external returns (string memory);
    function parseJsonStringArray(string calldata, string calldata) external returns (string[] memory);
    function parseJsonBytes(string calldata, string calldata) external returns (bytes memory);
    function parseJsonBytesArray(string calldata, string calldata) external returns (bytes[] memory);
    function parseJsonBytes32(string calldata, string calldata) external returns (bytes32);
    function parseJsonBytes32Array(string calldata, string calldata) external returns (bytes32[] memory);

    // Serialize a key and value to a JSON object stored in-memory that can be later written to a file
    // It returns the stringified version of the specific JSON file up to that moment.
    function serializeBool(string calldata objectKey, string calldata valueKey, bool value)
        external
        returns (string memory json);
    function serializeUint(string calldata objectKey, string calldata valueKey, uint256 value)
        external
        returns (string memory json);
    function serializeInt(string calldata objectKey, string calldata valueKey, int256 value)
        external
        returns (string memory json);
    function serializeAddress(string calldata objectKey, string calldata valueKey, address value)
        external
        returns (string memory json);
    function serializeBytes32(string calldata objectKey, string calldata valueKey, bytes32 value)
        external
        returns (string memory json);
    function serializeString(string calldata objectKey, string calldata valueKey, string calldata value)
        external
        returns (string memory json);
    function serializeBytes(string calldata objectKey, string calldata valueKey, bytes calldata value)
        external
        returns (string memory json);

    function serializeBool(string calldata objectKey, string calldata valueKey, bool[] calldata values)
        external
        returns (string memory json);
    function serializeUint(string calldata objectKey, string calldata valueKey, uint256[] calldata values)
        external
        returns (string memory json);
    function serializeInt(string calldata objectKey, string calldata valueKey, int256[] calldata values)
        external
        returns (string memory json);
    function serializeAddress(string calldata objectKey, string calldata valueKey, address[] calldata values)
        external
        returns (string memory json);
    function serializeBytes32(string calldata objectKey, string calldata valueKey, bytes32[] calldata values)
        external
        returns (string memory json);
    function serializeString(string calldata objectKey, string calldata valueKey, string[] calldata values)
        external
        returns (string memory json);
    function serializeBytes(string calldata objectKey, string calldata valueKey, bytes[] calldata values)
        external
        returns (string memory json);

    //
    // writeJson
    //
    // ----
    // Write a serialized JSON object to a file. If the file exists, it will be overwritten.
    // Let's assume we want to write the following JSON to a file:
    //
    // { "boolean": true, "number": 342, "object": { "title": "finally json serialization" } }
    //
    // ```
    //  string memory json1 = "some key";
    //  vm.serializeBool(json1, "boolean", true);
    //  vm.serializeBool(json1, "number", uint256(342));
    //  json2 = "some other key";
    //  string memory output = vm.serializeString(json2, "title", "finally json serialization");
    //  string memory finalJson = vm.serialize(json1, "object", output);
    //  vm.writeJson(finalJson, "./output/example.json");
    // ```
    // The critical insight is that every invocation of serialization will return the stringified version of the JSON
    // up to that point. That means we can construct arbitrary JSON objects and then use the return stringified version
    // to serialize them as values to another JSON object.
    //
    // json1 and json2 are simply keys used by the backend to keep track of the objects. So vm.serializeJson(json1,..)
    // will find the object in-memory that is keyed by "some key".
    function writeJson(string calldata json, string calldata path) external;
    // Write a serialized JSON object to an **existing** JSON file, replacing a value with key = <value_key>
    // This is useful to replace a specific value of a JSON file, without having to parse the entire thing
    function writeJson(string calldata json, string calldata path, string calldata valueKey) external;
    // Returns the RPC url for the given alias
    function rpcUrl(string calldata rpcAlias) external view returns (string memory json);
    // Returns all rpc urls and their aliases `[alias, url][]`
    function rpcUrls() external view returns (string[2][] memory urls);
    // Returns all rpc urls and their aliases as structs.
    function rpcUrlStructs() external view returns (Rpc[] memory urls);
    // If the condition is false, discard this run's fuzz inputs and generate new ones.
    function assume(bool condition) external pure;
    // Pauses gas metering (i.e. gas usage is not counted). Noop if already paused.
    function pauseGasMetering() external;
    // Resumes gas metering (i.e. gas usage is counted again). Noop if already on.
    function resumeGasMetering() external;
    // Writes a breakpoint to jump to in the debugger
    function breakpoint(string calldata char) external;
    // Writes a conditional breakpoint to jump to in the debugger
    function breakpoint(string calldata char, bool value) external;
}

interface Vm is VmSafe {
    // Sets block.timestamp
    function warp(uint256 newTimestamp) external;
    // Sets block.height
    function roll(uint256 newHeight) external;
    // Sets block.basefee
    function fee(uint256 newBasefee) external;
    // Sets block.difficulty
    function difficulty(uint256 newDifficulty) external;
    // Sets block.chainid
    function chainId(uint256 newChainId) external;
    // Sets tx.gasprice
    function txGasPrice(uint256 newGasPrice) external;
    // Stores a value to an address' storage slot.
    function store(address target, bytes32 slot, bytes32 value) external;
    // Sets the nonce of an account; must be higher than the current nonce of the account
    function setNonce(address account, uint64 newNonce) external;
    // Sets the *next* call's msg.sender to be the input address
    function prank(address msgSender) external;
    // Sets all subsequent calls' msg.sender to be the input address until `stopPrank` is called
    function startPrank(address msgSender) external;
    // Sets the *next* call's msg.sender to be the input address, and the tx.origin to be the second input
    function prank(address msgSender, address txOrigin) external;
    // Sets all subsequent calls' msg.sender to be the input address until `stopPrank` is called, and the tx.origin to be the second input
    function startPrank(address msgSender, address txOrigin) external;
    // Resets subsequent calls' msg.sender to be `address(this)`
    function stopPrank() external;
    // Sets an address' balance
    function deal(address account, uint256 newBalance) external;
    // Sets an address' code
    function etch(address target, bytes calldata newRuntimeBytecode) external;
    // Expects an error on next call
    function expectRevert(bytes calldata revertData) external;
    function expectRevert(bytes4 revertData) external;
    function expectRevert() external;

    // Prepare an expected log with all four checks enabled.
    // Call this function, then emit an event, then call a function. Internally after the call, we check if
    // logs were emitted in the expected order with the expected topics and data.
    // Second form also checks supplied address against emitting contract.
    function expectEmit() external;
    function expectEmit(address emitter) external;

    // Prepare an expected log with (bool checkTopic1, bool checkTopic2, bool checkTopic3, bool checkData).
    // Call this function, then emit an event, then call a function. Internally after the call, we check if
    // logs were emitted in the expected order with the expected topics and data (as specified by the booleans).
    // Second form also checks supplied address against emitting contract.
    function expectEmit(bool checkTopic1, bool checkTopic2, bool checkTopic3, bool checkData) external;
    function expectEmit(bool checkTopic1, bool checkTopic2, bool checkTopic3, bool checkData, address emitter)
        external;

    // Mocks a call to an address, returning specified data.
    // Calldata can either be strict or a partial match, e.g. if you only
    // pass a Solidity selector to the expected calldata, then the entire Solidity
    // function will be mocked.
    function mockCall(address callee, bytes calldata data, bytes calldata returnData) external;
    // Mocks a call to an address with a specific msg.value, returning specified data.
    // Calldata match takes precedence over msg.value in case of ambiguity.
    function mockCall(address callee, uint256 msgValue, bytes calldata data, bytes calldata returnData) external;
    // Reverts a call to an address with specified revert data.
    function mockCallRevert(address callee, bytes calldata data, bytes calldata revertData) external;
    // Reverts a call to an address with a specific msg.value, with specified revert data.
    function mockCallRevert(address callee, uint256 msgValue, bytes calldata data, bytes calldata revertData)
        external;
    // Clears all mocked calls
    function clearMockedCalls() external;
    // Expects a call to an address with the specified calldata.
    // Calldata can either be a strict or a partial match
    function expectCall(address callee, bytes calldata data) external;
    // Expects given number of calls to an address with the specified calldata.
    function expectCall(address callee, bytes calldata data, uint64 count) external;
    // Expects a call to an address with the specified msg.value and calldata
    function expectCall(address callee, uint256 msgValue, bytes calldata data) external;
    // Expects given number of calls to an address with the specified msg.value and calldata
    function expectCall(address callee, uint256 msgValue, bytes calldata data, uint64 count) external;
    // Expect a call to an address with the specified msg.value, gas, and calldata.
    function expectCall(address callee, uint256 msgValue, uint64 gas, bytes calldata data) external;
    // Expects given number of calls to an address with the specified msg.value, gas, and calldata.
    function expectCall(address callee, uint256 msgValue, uint64 gas, bytes calldata data, uint64 count) external;
    // Expect a call to an address with the specified msg.value and calldata, and a *minimum* amount of gas.
    function expectCallMinGas(address callee, uint256 msgValue, uint64 minGas, bytes calldata data) external;
    // Expect given number of calls to an address with the specified msg.value and calldata, and a *minimum* amount of gas.
    function expectCallMinGas(address callee, uint256 msgValue, uint64 minGas, bytes calldata data, uint64 count)
        external;
    // Only allows memory writes to offsets [0x00, 0x60) ∪ [min, max) in the current subcontext. If any other
    // memory is written to, the test will fail. Can be called multiple times to add more ranges to the set.
    function expectSafeMemory(uint64 min, uint64 max) external;
    // Only allows memory writes to offsets [0x00, 0x60) ∪ [min, max) in the next created subcontext.
    // If any other memory is written to, the test will fail. Can be called multiple times to add more ranges
    // to the set.
    function expectSafeMemoryCall(uint64 min, uint64 max) external;
    // Sets block.coinbase
    function coinbase(address newCoinbase) external;
    // Snapshot the current state of the evm.
    // Returns the id of the snapshot that was created.
    // To revert a snapshot use `revertTo`
    function snapshot() external returns (uint256 snapshotId);
    // Revert the state of the EVM to a previous snapshot
    // Takes the snapshot id to revert to.
    // This deletes the snapshot and all snapshots taken after the given snapshot id.
    function revertTo(uint256 snapshotId) external returns (bool success);
    // Creates a new fork with the given endpoint and block and returns the identifier of the fork
    function createFork(string calldata urlOrAlias, uint256 blockNumber) external returns (uint256 forkId);
    // Creates a new fork with the given endpoint and the _latest_ block and returns the identifier of the fork
    function createFork(string calldata urlOrAlias) external returns (uint256 forkId);
    // Creates a new fork with the given endpoint and at the block the given transaction was mined in, replays all transaction mined in the block before the transaction,
    // and returns the identifier of the fork
    function createFork(string calldata urlOrAlias, bytes32 txHash) external returns (uint256 forkId);
    // Creates _and_ also selects a new fork with the given endpoint and block and returns the identifier of the fork
    function createSelectFork(string calldata urlOrAlias, uint256 blockNumber) external returns (uint256 forkId);
    // Creates _and_ also selects new fork with the given endpoint and at the block the given transaction was mined in, replays all transaction mined in the block before
    // the transaction, returns the identifier of the fork
    function createSelectFork(string calldata urlOrAlias, bytes32 txHash) external returns (uint256 forkId);
    // Creates _and_ also selects a new fork with the given endpoint and the latest block and returns the identifier of the fork
    function createSelectFork(string calldata urlOrAlias) external returns (uint256 forkId);
    // Takes a fork identifier created by `createFork` and sets the corresponding forked state as active.
    function selectFork(uint256 forkId) external;
    /// Returns the identifier of the currently active fork. Reverts if no fork is currently active.
    function activeFork() external view returns (uint256 forkId);
    // Updates the currently active fork to given block number
    // This is similar to `roll` but for the currently active fork
    function rollFork(uint256 blockNumber) external;
    // Updates the currently active fork to given transaction
    // this will `rollFork` with the number of the block the transaction was mined in and replays all transaction mined before it in the block
    function rollFork(bytes32 txHash) external;
    // Updates the given fork to given block number
    function rollFork(uint256 forkId, uint256 blockNumber) external;
    // Updates the given fork to block number of the given transaction and replays all transaction mined before it in the block
    function rollFork(uint256 forkId, bytes32 txHash) external;
    // Marks that the account(s) should use persistent storage across fork swaps in a multifork setup
    // Meaning, changes made to the state of this account will be kept when switching forks
    function makePersistent(address account) external;
    function makePersistent(address account0, address account1) external;
    function makePersistent(address account0, address account1, address account2) external;
    function makePersistent(address[] calldata accounts) external;
    // Revokes persistent status from the address, previously added via `makePersistent`
    function revokePersistent(address account) external;
    function revokePersistent(address[] calldata accounts) external;
    // Returns true if the account is marked as persistent
    function isPersistent(address account) external view returns (bool persistent);
    // In forking mode, explicitly grant the given address cheatcode access
    function allowCheatcodes(address account) external;
    // Fetches the given transaction from the active fork and executes it on the current state
    function transact(bytes32 txHash) external;
    // Fetches the given transaction from the given fork and executes it on the current state
    function transact(uint256 forkId, bytes32 txHash) external;
}
"
    },
    "src/interfaces/IVerifier.sol": {
      "content": "// SPDX-License-Identifier: MIT OR Apache-2.0
pragma solidity ^0.8.13;

/// @title IVerifier
/// @author The Warp Team <contact@warp.inc>
/// @notice Interface for a cross-chain message verifier.
/// @dev The verifier is responsible for verifying the authenticity of a
///      message. It contains any necessary logic and state to authenticate
///      messages. The Verifier is called by the Inbox contract before
///      delivering a message to the handler. The address of the Verifier is
///      passed to the Handler with the message. The handler may then check
///      that the Verifier is trusted before processing messages.
interface IVerifier {
    /// @notice A statement that has been verified by a Verifier. Contains a
    ///         message field that SHOULD contain a serialized `Message`.
    /// @dev The fields are as follows:
    ///      - emitterChainId - The ID of the chain that emitted the statement.
    ///      - emitterAddress - The address that emitted the statement.
    ///      - message - The body of the statement, which SHOULD contain a
    ///                  serialized message.
    struct VerifiedStatement {
        uint16 emitterChainId;
        bytes32 emitterAddress;
        bytes message;
    }

    /// @notice Verify a witnessed statement, returning the statement it
    ///         contains  if the witness is valid, and contains a validly
    ///         formatted statement, and reverting otherwise.
    /// @dev    This function may unpack the statement according to any
    ///         arbitrary logic and/or state. Application developers must
    ///         evaluate the safety and trustworthiness of a particular
    ///         verifier.
    /// @dev    MUST revert if statement is not valid.
    /// @param  witnessedStatement - The statement to verify.
    /// @return statement The statement contained in the witnessed statement.
    function verify(bytes memory witnessedStatement) external view returns (VerifiedStatement memory statement);
}
"
    },
    "src/interfaces/IHandler.sol": {
      "content": "// SPDX-License-Identifier: MIT OR Apache-2.0
pragma solidity ^0.8.13;

/// @title IHandler
/// @author The Warp Team <contact@warp.inc>
/// @notice Interface for a cross-chain message handler.
/// @dev The handler is responsible for processing a message upon delivery. It
///      contains any application logic performed on message receipt.
/// @dev Security notice: within any `handle,`
///      the handler MUST check that the caller (msg.sender)
///      is a registered Messenger contract.
///      Otherwise, any caller can pass un-verified data to the application.
/// @dev Security notice: within any `handle`,
///      the handler SHOULD check that the (emitter chain, sender)
///      is a trusted remote sender (likely the address of a
///      remote deployment of the same handler application).
///      Most cross-chain applications SHOULD NOT accept messages from
///      un-trusted remote senders.
interface IHandler {
    /// @notice Handle a message.
    /// @dev This function is called by the `Messenger` contract to deliver
    ///      a message.
    /// @dev Security notice: the handler MUST check that msg.sender is an
    ///      approved Messenger contract. Application developers are
    ///      responsible for selecting approved Messengers.
    /// @dev Security notice: The handler MUST correctly implement
    ///      `approvedVerifier` and `approvedMessenger`
    ///      to prevent unauthorized messages.
    /// @dev Security notice: The handler SHOULD check the (emitter chain,
    ///      sender) information to prevent unauthorized messages. The
    ///      functionality of this check is application-specific.
    /// @dev see BaseHandler for implementation including necessary pre-checks
    /// @param emittingChain - The chainId the message was sent from
    /// @param sender - The sender of the message on the origin chain
    /// @param replayHash The unique identifier of the message which was
    ///        delivered.
    ///        Security notice: It is RECOMMENDED that applications prevent
    ///        replaying messages using `acceptableDelivery` to reject
    ///        previously delivered messages, and marking messages' replayHash
    ///        as delivered in `handle` (*before* calling any external contracts,
    ///        if there is no blanket replay protection; otherwise,
    ///        messages could be replayed via reentrancy).
    ///        This recommendation can be safely ignored if there are
    ///        application-specific reasons to allow replays.
    /// @param message - the contents of the message, used for application
    ///        logic. The contents of the message have been authenticated prior
    ///        to delivery.
    function handle(uint16 emittingChain, bytes calldata sender, bytes32 replayHash, bytes calldata message) external;

    /// @notice Handle a message, extended with
    ///         extra un-verified data.
    /// @dev Security notice: true to its name, the unverifiedRelayerData
    ///      is not verified or otherwise validated.
    ///      Applications MUST perform verification as necessary.
    /// @dev Security notice: the handler MUST check that msg.sender is an
    ///      approved Messenger contract. Application developers are
    ///      responsible for selecting approved Messengers.
    /// @dev Security notice: The handler MUST correctly implement
    ///      `approvedVerifier` and `approvedMessenger`
    ///      to prevent unauthorized messages.
    /// @dev Security notice: The handler SHOULD check the (emitter chain,
    ///      sender) information to prevent unauthorized messages. The
    ///      functionality of this check is application-specific.
    /// @dev see BaseHandler for implementation including necessary pre-checks
    /// @dev Security notice: The Messenger contract does not prevent reentrance
    ///      when calling `handleExt`.
    ///      This choice was made to enable the delivery of
    ///      additional messages contained in `unverifiedRelayerData` data.
    ///      App devs MUST ensure that their `handleExt`
    ///      function is secure against re-entry by the `Messenger` contract.
    /// @param emittingChain - The chainId the message was sent from.
    /// @param sender - The sender of the message on the origin chain.
    /// @param replayHash The unique identifier of the message which was
    ///        delivered.
    ///        Security notice: It is RECOMMENDED that applications prevent
    ///        replaying messages using `acceptableDelivery` to reject
    ///        previously delivered messages, and marking messages' replayHash
    ///        as delivered in `handleExt` (*before* calling any external
    ///        contracts, if there is no blanket replay protection; otherwise,
    ///        messages could be replayed via reentrancy).
    ///        This recommendation can be safely ignored if there are
    ///        application-specific reasons to allow replays.
    /// @param message - the contents of the message, used for application
    ///        logic. The contents of the message have been authenticated prior
    ///        to delivery.
    /// @param relaySignal - Application-defined bytes meant to signal
    ///        to relayers what type of ExtendedMessage this is,
    ///        so that relayers can construct appropriate
    ///        `unverifiedRelayerData` for delivery. May also be used by
    ///        receiving applications to verify `unverifiedRelayerData.`
    /// @param unverifiedRelayerData - un-verified extra data passed by the
    ///        relayer that delivers the message.
    ///        NOTE: this field affords flexibility to add information not
    ///        available at the time of sending the message, but application
    ///        developers should take caution, as this is un-trusted data
    ///        passed by  the relayer.
    function handleExt(
        uint16 emittingChain,
        bytes calldata sender,
        bytes32 replayHash,
        bytes calldata message,
        bytes calldata relaySignal,
        bytes calldata unverifiedRelayerData
    ) external;

    /// @notice Handle a message with
    ///         a transfer of tokens.
    /// @dev Security notice: the token transfer is not verified
    ///      or otherwise validated.
    ///      Applications MUST submit it to the specified bridge
    ///      and verify that it results in a transfer
    ///      of the correct type and amount of tokens.
    /// @dev Security notice: the handler MUST check that msg.sender is an
    ///      approved Messenger contract. Application developers are
    ///      responsible for selecting approved Messengers.
    /// @dev Security notice: The handler MUST correctly implement
    ///      `approvedVerifier` and `approvedMessenger`
    ///      to prevent unauthorized messages.
    /// @dev Security notice: The handler SHOULD check the (emitter chain,
    ///      sender) information to prevent unauthorized messages. The
    ///      functionality of this check is application-specific.
    /// @dev see BaseHandler for implementation including necessary pre-checks
    /// @dev Security notice: The Messenger contract does not prevent reentrance
    ///      when calling `handleWithTokens`.
    ///      This choice was made to enable the delivery of
    ///      additional messages contained in `transfer` data
    ///      (such as a message delivering a Burn-and-Mint token built on this
    ///      contract).
    ///      App devs MUST ensure that their `handleWithTokens`
    ///      function is secure against re-entry by the `Messenger` contract.
    /// @param emittingChain - The chainId the message was sent from
    /// @param sender - The sender of the message on the origin chain
    /// @param replayHash - The unique identifier of the message which was
    ///        delivered.
    ///        Security notice: It is RECOMMENDED that applications prevent
    ///        replaying messages using `acceptableDelivery` to reject
    ///        previously delivered messages, and marking messages' replayHash
    ///        as delivered in `handleWithTokens` (*before* calling any
    ///        external contracts, if there is no blanket replay protection;
    ///        otherwise, messages could be replayed via reentrancy).
    ///        This recommendation can be safely ignored if there are
    ///        application-specific reasons to allow replays.
    /// @param message - the contents of the message, used for application
    ///        logic. The contents of the message have been authenticated prior
    ///        to delivery.
    /// @param bridge - The token bridge used to send and receive the tokens.
    /// @param assetIdentifier - The app-defined identifier for the token being transferred.
    ///        Example: abi.encode(nativeTokenChainId, nativeTokenAddress);
    /// @param amount - The amount of the token being transferred.
    /// @param transfer - Data to pass to the specified bridge to complete the token transfer.
    function handleWithTokens(
        uint16 emittingChain,
        bytes calldata sender,
        bytes32 replayHash,
        bytes calldata message,
        uint8 bridge,
        bytes calldata assetIdentifier,
        uint256 amount,
        bytes calldata transfer
    ) external;

    /// @notice Check if a given verifier is acceptable to this handler.
    /// @dev Security notice: using `acceptableVerifier`,
    ///      the handler MUST auth the verifier.
    ///      A malicious or faulty verifier can pass arbitrary messages.
    ///      The verifier code MUST be trusted to verify messages.
    /// @dev This function is called by the Messenger contract prior to message
    ///      delivery. It allows the handler to reject messages from a verifier
    ///      which is not trusted.
    /// @dev MUST return true if the verifier is acceptable, false otherwise.
    /// @param verifier - The address of the contract which verifies the
    ///        message.
    /// @return acceptable - True if the verifier is acceptable, false
    ///         otherwise.
    function acceptableVerifier(address verifier) external view returns (bool acceptable);

    /// @notice Check if a given Messenger is acceptable to this handler.
    /// @dev Security notice: using `acceptableMessenger`,
    ///      the handler MUST auth that the (emitter chain, emitterAddress)
    ///      is the address of a remote Messenger contract.
    /// @dev This function is called by the Messenger contract prior to message
    ///      delivery. It allows the handler to reject messages from a remote
    ///      Messenger which is not trusted.
    /// @dev MUST return true if the Messenger is acceptable, false otherwise.
    /// @param chainId - The Wormhole chainId of the Messenger contract.
    /// @param messenger - The address of the Messenger contract.
    /// @return acceptable - True if the Messenger is acceptable, false
    ///         otherwise.
    function acceptableMessenger(uint16 chainId, bytes32 messenger) external view returns (bool acceptable);

    /// @notice Check if a given delivery is acceptable to this handler.
    /// @dev Security notice: using `acceptableDelivery`,
    ///      the handler MAY reject messages that have already been delivered.
    ///      If implementing relay protection, the handler MUST mark a message
    ///      as delivered within `handle` using replayHash.
    /// @dev This function is called by the Messenger contract prior to message
    ///      delivery. It allows the handler to reject messages. It is intended
    ///      to be used by the handler to prevent replays and to allow the
    ///      handler to denylist specific messages.
    /// @dev MUST return true if the delivery is acceptable, false otherwise.
    /// @param replayHash - The unique identifier of the message which was
    ///        delivered.
    ///        Security notice: It is RECOMMENDED that applications prevent
    ///        replaying messages using `acceptableDelivery` to reject
    ///        previously delivered messages, and marking messages as delivered
    ///        `handle`. This recommendation can be safely ignored if there are
    ///        application-specific reasons to allow replays.
    /// @return acceptable - True if the message is acceptable, false otherwise.
    function acceptableDelivery(bytes32 replayHash) external view returns (bool acceptable);
}
"
    },
    "src/shared/message/MessageLib.sol": {
      "content": "// SPDX-License-Identifier: MIT OR Apache-2.0
pragma solidity ^0.8.13;

import {BytesLib} from "./BytesLib.sol";
import {IHandler} from "../../interfaces/IHandler.sol";

import "./BaseMessage.sol";

/// @title MessageLib
/// @author The Warp Team <contact@warp.inc>
/// @notice A library for encoding and decoding cross-chain messages according
///         to the Basic Abstract Messaging specification.
library MessageLib {
    using BytesLib for bytes;

    /// @notice A cross-chain message.
    /// @dev The fields are as follows.
    ///      - index - The index of the message in its Outbox's message history.
    ///      - targetChain - The chain ID of the destination chain.
    ///      - targetAddress - The address of the recipient on the targetChain.
    ///      - sender - The address of the sender on the source chain.
    ///      - contents - The message contents.
    struct Message {
        uint64 index;
        uint16 targetChain;
        bytes targetAddress;
        bytes sender;
        bytes contents;
    }

    /// @notice A 1-byte message type indicator.
    uint8 public constant MESSAGE_TYPE = 0;

    /// @notice The minimum length of an encoded message.
    /// @dev Composed of:
    ///      - magicByte - 1 byte.
    ///      - version - 1 byte.
    ///      - messageType - 1 byte.
    ///      - index - 8 bytes.
    ///      - targetChain - 2 bytes.
    ///      - targetAddressLen - 2 bytes.
    ///      - smallest possible targetAddress - 1 byte.
    ///      - senderLen - 2 bytes.
    ///      - contentsLen - 2 bytes.
    /// @dev Equals 20 bytes.
    uint256 public constant MINIMUM_ENCODED_LEN = 1 + 1 + 1 + 8 + 2 + 2 + 1 + 2 + 2;

    /// @notice Details for the index in the encoding scheme.
    uint256 private constant INDEX_OFFSET = MESSAGE_TYPE_OFFSET + MESSAGE_TYPE_SIZE;
    uint256 private constant INDEX_SIZE = 8;

    /// @notice Details for the targetChain in the encoding scheme.
    uint256 private constant TARGET_CHAIN_OFFSET = INDEX_OFFSET + INDEX_SIZE;
    uint256 private constant TARGET_CHAIN_SIZE = 2;

    /// @notice Offset for the target address length-prefixed bytearray in the
    ///         encoding scheme.
    uint256 private constant TARGET_ADDRESS_OFFSET = TARGET_CHAIN_OFFSET + TARGET_CHAIN_SIZE;

    /// @notice Encode a message into bytes.
    /// @dev The message follows a simple specification:
    ///      HEADER
    ///      - magicByte - 1 byte
    ///      - version - 1 byte
    ///      - messageType - 1 byte
    ///      BODY
    ///      - index - 8 bytes
    ///      - targetChain - 2 bytes
    ///      - targetAddress - 2 bytes length + targetAddress bytearray
    ///      - sender - 2 bytes length + sender bytearray
    ///      - contents - 2 bytes length + contents bytearray
    /// @custom:reverts As encodeBody.
    /// @return The encoded message as a bytearray.
    function encode(Message memory message) internal pure returns (bytes memory) {
        return abi.encodePacked(encodeHeader(MESSAGE_TYPE), encodeBody(message));
    }

    /// @notice Encode a message body into bytes.
    /// @dev The message body follows a simple specification:
    ///      BODY
    ///      - index - 8 bytes
    ///      - targetChain - 2 bytes
    ///      - targetAddress - 2 bytes length + targetAddress bytearray
    ///      - sender - 2 bytes length + sender bytearray
    ///      - contents - 2 bytes length + contents bytearray
    /// @custom:reverts Errors when the message, targetAddress, or sender is too
    ///                 large; or if the targetAddress is empty.
    /// @return The encoded message body as a bytearray.
    function encodeBody(Message memory message) internal pure returns (bytes memory) {
        if (message.targetAddress.length > type(uint16).max || message.targetAddress.length == 0) {
            revert AddressLength();
        }
        if (message.contents.length > type(uint16).max) revert ContentsLength();
        if (message.sender.length > type(uint16).max) revert SenderLength();

        // length-prepend the bytearrays
        bytes memory targetAddress = abi.encodePacked(uint16(message.targetAddress.length), message.targetAddress);
        bytes memory sender = abi.encodePacked(uint16(message.sender.length), message.sender);
        bytes memory contents = abi.encodePacked(uint16(message.contents.length), message.contents);

        return abi.encodePacked(message.index, message.targetChain, targetAddress, sender, contents);
    }

    /// @notice Decode a message from a bytes representation, without checking
    ///         the header.
    /// @param encoded The encoded message as a bytearray.
    /// @return decoded The decoded Message.
    /// @return consumed The number of bytes consumed during decoding.
    /// @custom:reverts Errors if the input is not a valid encoded Message
    ///                 struct.
    function decodeBody(bytes memory encoded) internal pure returns (Message memory decoded, uint256 consumed) {
        // 8 bytes at position 3
        decoded.index = encoded.toUint64(INDEX_OFFSET);
        // 2 bytes at position 11
        decoded.targetChain = encoded.toUint16(TARGET_CHAIN_OFFSET);
        // length-prefixed bytearray at position 13
        decoded.targetAddress = readU16PrefixedBytes(encoded, TARGET_ADDRESS_OFFSET);

        // We are now in the dynamic section, so we start tracking our offset
        // in the encoded data.
        consumed = TARGET_ADDRESS_OFFSET + 2 + decoded.targetAddress.length;

        // computed as the targetOffset + 2 (the size of the length prefix in
        // bytes) + the value of the length prefix (the length of the
        // targetAddress in bytes)
        decoded.sender = readU16PrefixedBytes(encoded, consumed);
        consumed += 2 + decoded.sender.length;

        // computed as the senderOffset + 2 (the size of the length prefix in
        // bytes) + the value of the length prefix (the length of the
        // sender in bytes)
        decoded.contents = readU16PrefixedBytes(encoded, consumed);
        consumed += 2 + decoded.contents.length;
    }

    /// @notice Decode a message from a bytes representation,
    ///         checking the header and erroring if any bytes are left over.
    /// @param encoded The encoded message as a bytearray.
    /// @return decoded The decoded message.
    /// @custom:reverts as expectHeader()
    /// @custom:reverts as decodeBody()
    /// @custom:reverts Overrun() If the encoded message is too short.
    /// @custom:reverts LeftoverBytes() If decoding consumes less than the
    ///                 entire input.
    function decode(bytes memory encoded) internal pure returns (Message memory decoded) {
        if (encoded.length < MINIMUM_ENCODED_LEN) revert Overrun();
        expectHeader(MESSAGE_TYPE, encoded);
        uint256 consumed;
        (decoded, consumed) = decodeBody(encoded);
        if (consumed != encoded.length) revert LeftoverBytes();
    }

    /// @notice Extract the target address from a message, reverting if it is
    ///         not an EVM address.
    /// @dev Wrapper for `extractAddress(bytes)`
    /// @param message The message from which to extract the target address.
    /// @return decoded An IHandler interface at the decoded EVM address.
    /// @custom:reverts as `extractAddress(bytes)`
    function extractEvmTarget(Message memory message) internal pure returns (IHandler) {
        return extractAddress(message.targetAddress);
    }
}
"
    },
    "src/shared/message/TokenMessageLib.sol": {
      "content": "// SPDX-License-Identifier: MIT OR Apache-2.0
pragma solidity ^0.8.13;

import {BytesLib} from "./BytesLib.sol";
import {MessageLib} from "./MessageLib.sol";

import "./BaseMessage.sol";

/// @title TokenMessageLib
/// @author The Warp Team <contact@warp.inc>
/// @notice A library for encoding and decoding cross-chain messages with value
///         according to the Wormhole Abstract Messaging specification.
library TokenMessageLib {
    using BytesLib for bytes;

    /// @notice A cross-chain message with expected value.
    /// @dev The fields are as follows.
    ///      - index - The index of the message in its Outbox's message history.
    ///      - targetChain - The chain ID of the destination chain.
    ///      - targetAddress - The address of the recipient on the targetChain.
    ///      - sender - The address of the sender on the source chain.
    ///      - contents - The message contents.
    ///      - bridge - An application-specified identifier for a bridge.
    ///      - assetIdentifier - An application-specified identifier for an
    ///        asset, which may be in a bridge-specific format.
    ///      - amount - The amount of the asset to be transferred, which may be
    ///        in a bridge- or asset-specific format.
    struct TokenMessage {
        uint64 index;
        uint16 targetChain;
        bytes targetAddress;
        bytes sender;
        bytes contents;
        uint8 bridge;
        bytes assetIdentifier;
        uint256 amount;
    }

    /// @notice A 1-byte message type indicator.
    uint8 public constant MESSAGE_TYPE = 1;

    /// @notice The minimum length of an encoded message.
    /// @dev Composed of:
    ///      - MessageLib.MINIMUM_ENCODED_LEN - 20 bytes.
    ///      - bridge - 1 byte.
    ///      - assetIdentifierLen - 2 bytes.
    ///      - smallest possible assetIdentifier - 1 byte.
    ///      - amount - 32 bytes.
    /// @dev Equals 56 bytes.
    uint256 private constant MINIMUM_ENCODED_LEN = MessageLib.MINIMUM_ENCODED_LEN + 1 + 2 + 1 + 32;

    /// @notice An error indicating that the asset identifier is too large to
    /// encode OR the asset identifier is empty.
    /// @dev Selector 0x9b29342f.
    error AssetIdentifierLength();

    /// @notice An error indicating that the amount about to be encoded is zero.
    /// @dev Selector 0x1f2a2005.
    error ZeroAmount();

    /// @notice Encode a message into a bytes representation.
    /// @dev Because `MessageLib.Message` is a prefix of `TokenMessage`,
    ///      this function simply calls `MessageLib.encodeBody` to encode shared message fields.
    /// @dev The message with tokens follows a simple specification:
    ///      HEADER
    ///      - magicByte - 1 byte
    ///      - version - 1 byte
    ///      - messageType - 1 byte
    ///      BODY
    ///      - index - 8 bytes
    ///      - targetChain - 2 bytes
    ///      - targetAddress - 2 bytes length + targetAddress bytearray
    ///      - sender - 2 bytes length + sender bytearray
    ///      - contents - 2 bytes length + contents bytearray
    ///      WITH TOKENS
    ///      - bridge - 1 byte.
    ///      - assetIdentifier - 2 bytes + assetIdentifier bytearray
    ///      - amount - 32 bytes.
    /// @custom:reverts As MessageLib.encodeBody.
    /// @custom:reverts As encodeWithTokens
    /// @return The encoded message as a bytearray.
    function encode(TokenMessage memory message) internal pure returns (bytes memory) {
        return abi.encodePacked(
            encodeHeader(MESSAGE_TYPE), MessageLib.encodeBody(toMessage(message)), encodeWithTokens(message)
        );
    }

    /// @notice Encode the WithTokens portion of a message into bytes.
    /// @dev The WithTokens portion of a message follows a simple specification:
    ///      WITH TOKENS
    ///      - bridge - 1 byte.
    ///      - assetIdentifier - 2 bytes + assetIdentifier bytearray
    ///      - amount - 32 bytes.
    /// @custom:reverts AssetIdentifierLength() if the asset identifier is
    //                  too large or empty.
    /// @return The WithTokens portion of the message encoded as a bytearray.
    function encodeWithTokens(TokenMessage memory message) internal pure returns (bytes memory) {
        if (message.assetIdentifier.length > type(uint16).max || message.assetIdentifier.length == 0) {
            revert AssetIdentifierLength();
        }
        if (message.amount == 0) {
            revert ZeroAmount();
        }
        // length-prepend the assetIdentifier bytearray
        bytes memory assetIdentifier = abi.encodePacked(uint16(message.assetIdentifier.length), message.assetIdentifier);

        // encode the additional WithTokens fields on the end
        return abi.encodePacked(message.bridge, assetIdentifier, message.amount);
    }

    /// @notice Decode a message from a bytes representation, without checking
    ///         the header.
    /// @dev Because `MessageLib.Message` is a prefix of `TokenMessage`,
    ///      this function simply calls `MessageLib.decodeBody` to parse shared fields.
    /// @param encoded The encoded message as a bytearray.
    /// @return decoded The decoded message.
    /// @return consumed The number of bytes consumed during decoding.
    /// @custom:reverts Errors if the input is not a valid encoded
    ///                 MessageWithValue struct.
    function decodeBody(bytes memory encoded) internal pure returns (TokenMessage memory decoded, uint256 consumed) {
        // decode the shared fields using MessageLib
        MessageLib.Message memory message;
        (message, consumed) = MessageLib.decodeBody(encoded);
        decoded = fromMessage(message);

        // Decode the WithTokens fields added on the end
        // In this segment we use the `consumed` variable to track the offset
        // into the encoded message.
        decoded.bridge = encoded.toUint8(consumed);
        consumed += 1;
        decoded.assetIdentifier = readU16PrefixedBytes(encoded, consumed);
        consumed += decoded.assetIdentifier.length + 2;
        decoded.amount = encoded.toUint256(consumed);
        consumed += 32;
    }

    /// @notice Decode a message from a bytes representation,
    ///         checking header and erroring if any bytes are left over.
    /// @param encoded The encoded message as a bytearray.
    /// @return decoded The decoded message.
    /// @custom:reverts as expectHeader()
    /// @custom:reverts as decodeBody()
    /// @custom:reverts Overrun() If the encoded message is too short.
    /// @custom:reverts LeftoverBytes() If decoding consumes less than the
    ///                 entire input.
    function decode(bytes memory encoded) internal pure returns (TokenMessage memory decoded) {
        if (encoded.length < MINIMUM_ENCODED_LEN) revert Overrun();
        expectHeader(MESSAGE_TYPE, encoded);
        uint256 consumed;
        (decoded, consumed) = decodeBody(encoded);
        if (consumed != encoded.length) revert LeftoverBytes();
    }

    /// @notice Extract the target address from a message, reverting if it is
    ///         not an EVM address.
    /// @dev Wrapper for `extractAddress(bytes)`
    /// @param message The message from which to extract the target address.
    /// @return decoded An IHandler interface at the decoded EVM address.
    /// @custom:reverts as `extractAddress(bytes)`
    function extractEvmTarget(TokenMessage memory message) internal pure returns (IHandler) {
        return extractAddress(message.targetAddress);
    }

    /// @notice Transcribe shared fields from a Message into a TokenMessage
    /// @param message The Message.
    /// @return tokMessage The transcribed TokenMessage.
    /// @dev In the returned TokenMessage, fields bridge, assetIdentifier, and amount will be empty
    function fromMessage(MessageLib.Message memory message) private pure returns (TokenMessage memory tokMessage) {
        tokMessage.index = message.index;
        tokMessage.targetChain = message.targetChain;
        tokMessage.targetAddress = message.targetAddress;
        tokMessage.sender = message.sender;
        tokMessage.contents = message.contents;
    }

    /// @notice Transcribe shared fields from a TokenMessage into a Message
    /// @param tokMessage The TokenMessage.
    /// @return message The transcribed Message.
    /// @dev In the returned Message, fields bridge, assetIdentifier, and amount will have been erased
    function toMessage(TokenMessage memory tokMessage) private pure returns (MessageLib.Message memory message) {
        message.index = tokMessage.index;
        message.targetChain = tokMessage.targetChain;
        message.targetAddress = tokMessage.targetAddress;
        message.sender = tokMessage.sender;
        message.contents = tokMessage.contents;
    }
}
"
    },
    "src/shared/message/ExtendedMessageLib.sol": {
      "content": "// SPDX-License-Identifier: MIT OR Apache-2.0
pragma solidity ^0.8.13;

import {BytesLib} from "./BytesLib.sol";
import {MessageLib} from "./MessageLib.sol";

import "./BaseMessage.sol";

/// @title ExtendedMessageLib
/// @author The Warp Team <contact@warp.inc>
/// @notice A library for encoding and decoding cross-chain messages
///         meant to be delivered with extra data
///         according to the Better Abstract Messaging specification.
library ExtendedMessageLib {
    using BytesLib for bytes;

    /// @notice A cross-chain message meant to be delivered with extra data.
    /// @dev The fields are as follows.
    ///      - index - The index of the message in its Outbox's message history.
    ///      - targetChain - The chain ID of the destination chain.
    ///      - targetAddress - The address of the recipient on the targetChain.
    ///      - sender - The address of the sender on the source chain.
    ///      - contents - The message contents.
    ///      - relaySignal - Application-defined bytes meant to signal
    ///        to relayers what type of ExtendedMessage this is,
    ///        so that relayers can determine appropriate contents for
    ///        `unverifiedRelayerData` during delivery. May also be used by
    ///        applications to verify `unverifiedRelayerData.`
    struct ExtendedMessage {
        uint64 index;
        uint16 targetChain;
        bytes targetAddress;
        bytes sender;
        bytes contents;
        bytes relaySignal;
    }

    /// @notice A 1-byte message type indicator.
    uint8 public constant MESSAGE_TYPE = 2;

    /// @notice The minimum length of an encoded message.
    /// @dev Composed of:
    ///      - MessageLib.MINIMUM_ENCODED_LEN - 20 bytes.
    ///      - relaySignalLen - 2 bytes.
    ///      - smallest possible relaySignal - 1 byte.
    /// @dev Equals 56 bytes.
    uint256 private constant MINIMUM_ENCODED_LEN = MessageLib.MINIMUM_ENCODED_LEN + 2 + 1;

    /// @notice An error indicating that the relaySignal is too large to
    /// encode OR the relaySignal is empty.
    /// @dev Selector 0xd8d0c518.
    error RelaySignalLength();

    /// @notice Encode a message into a bytes representation.
    /// @dev Because `MessageLib.Message` is a prefix of `ExtendedMessage`,
    ///      this function simply calls `MessageLib.encodeBody` to encode
    ///      shared message fields.
    /// @dev The ExtendedMessage follows a simple specification:
    ///      HEADER
    ///      - magicByte - 1 byte
    ///      - version - 1 byte
    ///      - messageType - 1 byte
    ///      BODY
    ///      - index - 8 bytes
    ///      - targetChain - 2 bytes
    ///      - targetAddress - 2 bytes length + targetAddress bytearray
    ///      - sender - 2 bytes length + sender bytearray
    ///      - contents - 2 bytes length + contents bytearray
    ///      EXTENSION
    ///      - relaySignal - 2 bytes + relaySignal bytearray
    /// @custom:reverts As MessageLib.encodeBody.
    /// @custom:reverts As encodeSignal
    /// @return The encoded message as a bytearray.
    function encode(ExtendedMessage memory message) internal pure returns (bytes memory) {
        return abi.encodePacked(
            encodeHeader(MESSAGE_TYPE), MessageLib.encodeBody(toMessage(message)), encodeSignal(message)
        );
    }

    /// @notice Encode the signal portion of a message into bytes.
    /// @dev The signal portion of a message follows a simple specification:
    ///      EXTENSION
    ///      - signal - 2 bytes + signal bytearray
    /// @custom:reverts RelaySignalLength() if the relaySignal is
    //                  too large or empty.
    /// @return signal Application-defined bytes meant to signal to relayers
    ///         what type of ExtendedMessage this is, so that relayers can
    ///         determine appropriate contents for `unverifiedRelayerData`
    ///         during delivery.
    function encodeSignal(ExtendedMessage memory message) internal pure returns (bytes memory signal) {
        if (message.relaySignal.length > type(uint16).max || message.relaySignal.length == 0) {
            revert RelaySignalLength();
        }
        // length-prepend the relaySignal bytearray
        signal = abi.encodePacked(uint16(message.relaySignal.length), message.relaySignal);
    }

    /// @notice Decode a message from a bytes representation, without checking
    ///         the header.
    /// @dev Because `MessageLib.Message` is a prefix of `ExtendedMessage`,
    ///      this function simply calls `MessageLib.decodeBody` to parse the shared fields.
    /// @param encoded The encoded message as a bytearray.
    /// @return decoded The decoded message.
    /// @return consumed The number of bytes consumed during decoding.
    /// @custom:reverts Errors if the input is not a valid encoded
    ///                 ExtendedMessage struct.
    function decodeBody(bytes memory encoded)
        internal
        pure
        returns (ExtendedMessage memory decoded, uint256 consumed)
    {
        // decode the shared fields using MessageLib
        MessageLib.Message memory message;
        (message, consumed) = MessageLib.decodeBody(encoded);
        decoded = fromMessage(message);

        // Decode the relaySignal field added on the end
        decoded.relaySignal = readU16PrefixedBytes(encoded, consumed);
        consumed += decoded.relaySignal.length + 2;
    }

    /// @notice Decode a message from a bytes representation,
    ///         checking header and erroring if any bytes are left over.
    /// @param encoded The encoded message as a bytearray.
    /// @return decoded The decoded message.
    /// @custom:reverts as expectHeader()
    /// @custom:reverts as decodeBody()
    /// @custom:reverts Overrun() If the encoded message is too short.
    /// @custom:reverts LeftoverBytes() If decoding consumes less than the
    ///                 entire input.
    function decode(bytes memory encoded) internal pure returns (ExtendedMessage memory decoded) {
        if (encoded.length < MINIMUM_ENCODED_LEN) revert Overrun();
        expectHeader(MESSAGE_TYPE, encoded);
        uint256 consumed;
        (decoded, consumed) = decodeBody(encoded);
        if (consumed != encoded.length) revert LeftoverBytes();
    }

    /// @notice Extract the target address from a message, reverting if it is
    ///         not an EVM address.
    /// @dev Wrapper for `extractAddress(bytes)`
    /// @param message The message from which to extract the target address.
    /// @return decoded An IHandler interface at the decoded EVM address.
    /// @custom:reverts as `extractAddress(bytes)`
    function extractEvmTarget(ExtendedMessage memory message) internal pure returns (IHandler) {
        return extractAddress(message.targetAddress);
    }

    /// @notice Transcribe shared fields from a Message into an ExtendedMessage
    /// @param message The Message.
    /// @return extMessage The transcribed ExtendedMessage.
    /// @dev In the returned ExtendedMessage, the relaySignal field will be empty
    function fromMessage(MessageLib.Message memory message) private pure returns (ExtendedMessage memory extMessage) {
        extMessage.index = message.index;
        extMessage.targetChain = message.targetChain;
        extMessage.targetAddress = message.targetAddress;
        extMessage.sender = message.sender;
        extMessage.contents = message.contents;
    }

    /// @notice Transcribe shared fields from a ExtendedMessage into a Message
    /// @param extMessage The ExtendedMessage.
    /// @return message The transcribed Message.
    /// @dev In the returned Message, the relaySignal field will have been erased
    function toMessage(ExtendedMessage memory extMessage) private pure returns (MessageLib.Message memory message) {
        message.index = extMessage.index;
        message.targetChain = extMessage.targetChain;
        message.targetAddress = extMessage.targetAddress;
        message.sender = extMessage.sender;
        message.contents = extMessage.contents;
    }
}
"
    },
    "src/shared/message/BaseMessage.sol": {
      "content": "// SPDX-License-Identifier: MIT OR Apache-2.0
pragma solidity ^0.8.13;

import {BytesLib} from "./BytesLib.sol";
import {IHandler} from "../../interfaces/IHandler.sol";

/// @dev A 1-byte constant '0xbb' indicative of a message. This NEVER
///         changes in future versions.
uint8 constant MAGIC_BYTE = 0xbb;

/// @dev A 1-byte version identifier. Deployments MUST increment this
///         version number.
uint8 constant MESSAGE_VERSION = 0x0;

/// @dev Details for the Wormhole leading byte.
uint256 constant MAGIC_BYTE_OFFSET = 0;
uint256 constant MAGIC_BYTE_SIZE = 1;

/// @dev Details for the version offset.
uint256 constant VERSION_OFFSET = MAGIC_BYTE_OFFSET + MAGIC_BYTE_SIZE;
uint256 constant VERSION_SIZE = 1;

/// @dev Details for the message type.
uint256 constant MESSAGE_TYPE_OFFSET = VERSION_OFFSET + VERSION_SIZE;
uint256 constant MESSAGE_TYPE_SIZE = 1;

/// @notice Got a mismatch for the leading byte during message decoding.
///         Expected the value of `MAGIC_BYTE` but got `unexpected`.
/// @dev Selector 0x981b4e39.
error MagicByteMismatch(uint8 unexpected);

/// @notice Got a version mismatch during message decoding. Expected the
///         value of `MESSAGE_VERSION` but got `unexpected`.
/// @dev Selector 0xc6147f41.
error VersionMismatch(uint8 unexpected);

/// @notice Got a message type mismatch during message decoding. Expected the
///         value of `expected` but got `unexpected`.
/// @dev Selector 0x6cf016d3.
error TypeMismatch(uint8 expected, uint8 unexpected);

/// @notice An error indicating that the targetAddress is too large
/// to encode OR the address is empty.
/// @dev Selector 0x2a2a06b2.
error AddressLength();

/// @notice An error indicating that the sender address is too large to
/// encode.
/// @dev Selector 0x496d8313.
error SenderLength();

/// @notice An error indicating that the message contents are too large to
/// encode.
/// @dev Selector 0xbe3cbd10.
error ContentsLength();

/// @notice Failed message decode because there were not enough bytes in
///         the input.
/// @dev Selector 0xb8d8f0f4.
error Overrun();

/// @notice Failed message decode because there were leftover bytes in the
///         input after decodig.
/// @dev Selector 0x94701070.
error LeftoverBytes();

/// @notice Target address is not a valid EVM address. The app that
///         emitted the message may have been misconfigured or
///         misimplemented.
/// @dev Selector 0x3e95d7cb.
error InvalidTargetAddress(bytes targetAddress);

/// @notice Encode the message header.
/// @dev The header follows a simple specification:
///      HEADER
///      - magic - 1 byte
///      - version - 1 byte
///      - messageType - 1 byte
/// @param messageType The message type. 0 for Message, 1 for MessageWithTokens.
/// @return header The encoded header.
function encodeHeader(uint8 messageType) pure returns (bytes memory header) {
    header = abi.encodePacked(MAGIC_BYTE, MESSAGE_VERSION, messageType);
}

/// @notice Check that an encoded message has the expected header.
/// @dev Checks the leading byte, version, and message type.
/// @custom:reverts MagicByteMismatch() If the leading byte is not
///                 "0xbb"
/// @custom:reverts VersionMistmatch() If the version is not expected.
///                 MessageLib supports any equal-or-lower version.
/// @custom:reverts TypeMismatch() If the message type is not expected.
///                 Type supported varies by decoding function.
function expectHeader(uint8 messageType, bytes memory encoded) pure {
    uint8 magic = BytesLib.toUint8(encoded, MAGIC_BYTE_OFFSET);
    if (magic != MAGIC_BYTE) revert MagicByteMismatch(magic);
    // 1 byte at position 1
    uint8 version = BytesLib.toUint8(encoded, VERSION_OFFSET);
    if (version > MESSAGE_VERSION) revert VersionMismatch(version);
    // 1 byte at position 2
    uint8 ty = BytesLib.toUint8(encoded, MESSAGE_TYPE_OFFSET);
    if (ty != messageType) revert TypeMismatch(messageType, ty);
}

/// @notice Convert a 20-byte array to an EVM address.
/// @dev This function loads the 20 bytes of address data from the
///      provided array, and converts it to an EVM address by shifting
///      it right by 96 bits.
/// @dev We chose not to check extcode here, as that will be handled
///      by the compiler when the address is used to make a call.
/// @param encoded The 20-byte array containing the address data.
/// @return decoded An IHandler interface at the decoded EVM address.
/// @custom:reverts InvalidTargetAddress(encoded) if the encoded array is
///                 not exactly 20-bytes long.
function extractAddress(bytes memory encoded) pure returns (IHandler decoded) {
    if (encoded.length != 20) revert InvalidTargetAddress(encoded);
    uint256 word;
    assembly ("memory-safe") {
        // This assemply loads 1 word from the body of the encoded array.
        // This word is known to consist of 20 bytes of address data,
        // followed by 12 bytes of anything. `bytes` are 0-padded to the
        // next word by Solidity convention, but the contents must be
        // shifted out regardless.
        word := mload(add(encoded, 0x20))
    }
    // We shift the word right by 96 bits, to remove the 12 low bytes
    word = word >> 96;
    decoded = IHandler(address(uint160(word)));
}

/// @notice Calculates a hash of a message used to check for replay.
/// @param emitterChainId emitterChainId The chain id of the remote Outbox.
/// @param emitterAddress The address of the remote Outbox.
/// @param index The index of the message in the remote Outbox.
/// @return A protocol-specific unique identifier of the message
function calculateReplayHash(uint16 emitterChainId, bytes32 emitterAddress, uint64 index) pure returns (bytes32) {
    return keccak256(abi.encodePacked(emitterChainId, emitterAddress, index));
}

/// @notice Read a length-prefixed bytearray from the encoded message. The
///         bytearray is prefixed with a uint16 that specifies its length.
/// @dev First reads the length prefix, and then reads that many bytes
///      after the prefix. Performs bounds checking before each read.
/// @param encoded The encoded message as a bytearray.
/// @param offset The offset in the encoded messages to start reading from.
/// @return contents The contents of the byte array (without the length
///         prefix).
/// @custom:reverts Overrun() If the encoded message is not long enough to
///                 read the length-prefixed bytearray.
function readU16PrefixedBytes(bytes memory encoded, uint256 offset) pure returns (bytes memory contents) {
    // ensure that the input is long enough to read a length-prefix from
    if (encoded.length < offset + 2) revert Overrun();
    uint16 len = BytesLib.toUint16(encoded, offset);
    uint256 contentsOffset = offset + 2;
    // ensure that the input is long enough to read from
    if (encoded.length < contentsOffset + len) revert Overrun();
    contents = BytesLib.slice(encoded, contentsOffset, len);
}
"
    },
    "src/shared/UsesCore.sol": {
      "content": "// SPDX-License-Identifier: MIT OR Apache-2.0
pragma solidity ^0.8.13;

import {IWormhole} from "../interfaces/IWormhole.sol";

/// @title UsesCore
/// @author The Warp Team <contact@warp.inc>
/// @notice This contract manages a eip1967 stored Wormhole core contract
///         address.
/// @dev This contract is intended to be inherited by any contract that needs
///      to access the Wormhole core system.
abstract contract UsesCore {
    /// @notice Contract is already init, and cannot be initialized again.
    /// @dev Selector 0xef34ca5c.
    error AlreadyInit();

    /// @notice Attempting to set Core address to zero.
    /// @dev Selector 0xf71b2b01.
    error NullCore();

    /// @notice The slot at which the core contract address is stored.
    /// @dev 0xc6a342a8a7e6992aa2a41ce922cb4520fcb72f46fb4f9e41cdd65930adc9ca4c.
    bytes32 public constant CORE_SLOT = bytes32(uint256(keccak256("eip1967.proxy.accumulate.core")) - 1);

    /// @notice Read the core contract address.
    /// @return _core The Wormhole core contract.
    function core() public view returns (IWormhole _core) {
        uint256 slot = uint256(CORE_SLOT);
        assembly ("memory-safe") {
            _core := sload(slot)
        }
    }

    /// @notice Set the core contract address.
    /// @dev This function can only be called once.
    /// @param _core The new core contract.
    /// @custom:reverts AlreadyInit() when core has already been initialized.
    /// @custom:reverts NullCore() when core is address(0).
    /// @custom:reverts if core doesn't implement chainId() function.
    function setCore(address _core) internal {
        if (address(core()) != address(0)) revert AlreadyInit();
        // assert _core is not zero
        if (_core == address(0)) revert NullCore();
        // call chainId() to check that _core implements IWormhole
        IWormhole(_core).chainId();
        // store _core in CORE_SLOT
        uint256 slot = uint256(CORE_SLOT);
        assembly ("memory-safe") {
            sstore(slot, _core)
        }
    }
}
"
    },
    "src/shared/NonReentrant.sol": {
      "content": "// SPDX-License-Identifier: MIT OR Apache-2.0
pragma solidity ^0.8.13;

abstract contract NonReentrant {
    /// @notice The slot used for re-entrance protection.
    /// @dev
    /// 0xa2df719fa6ce35dee70f7a6f6c8087eb6e4e1e302979ab963fd34848c3a589af
    bytes32 public constant ENTERED_SLOT = bytes32(uint256(keccak256("eip1967.proxy.accumulate.re-entrance")) - 1);

    /// @notice Indicates a re-entrance
    /// @dev Selector 0xc07c7e13
    error Reentrance();

    /// @notice Modifier to prevent re-entrance.
    modifier nonReentrant() {
        if (isEntered()) revert Reentrance();
        setEntered();
        _;
        clearEntered();
    }

    /// @notice Set the re-entrance guard.
    function setEntered() internal {
        bytes32 slot = ENTERED_SLOT;
        assembly ("memory-safe") {
            sstore(slot, 1)
        }
    }

    /// @notice Clear the re-entrance guard.
    function clearEntered() internal {
        bytes32 slot = ENTERED_SLOT;
        assembly ("memory-safe") {
            sstore(slot, 0)
        }
    }

    /// @notice Retrieve the entrance status.
    /// @return status True if entered guard is set, false otherwise.
    function isEntered() internal view returns (bool status) {
        bytes32 slot = ENTERED_SLOT;
        assembly ("memory-safe") {
            status := sload(slot)
        }
    }
}
"
    },
    "src/outbox/RingBufferStorage.sol": {
      "content": "// SPDX-License-Identifier: MIT OR Apache-2.0
pragma solidity ^0.8.13;

/// @title RingBufferStorage
/// @author The Warp Team <contact@warp.inc>
/// @notice This contract manages a eip1967 stored ring buffer of messages.
/// @dev This contract is intended to be inherited by the Outbox. It stores
///      messages in a ring buffer, and allows for reading messages from the
///      buffer until it is overwritten. Messages may also be proven in state
///      while they are in the ring buffer via `eth_getProof`. This contract
///      functions as a simple accumulator in the EVM merkle-patricia trie. By
///      re-using state slots, we can reduce gas costs for sending messages.
abstract contract RingBufferStorage {
    /// @notice Size of the ring buffer, in messages. Gas savings kick in once
    ///         the ring buffer is full.
    /// @dev This value is set at construction time and cannot be changed.
    uint64 public immutable RING_BUFFER_SIZE;

    /// @notice Stores the next index within the message vector.
    /// @dev The index is a count of all past messages. It is also the value
    ///      used to calculate the storage slot when storing a message in the
    ///      ring buffer.
    /// @dev 0x9c4ac79203be492bdf9cdcb9cf6c26723bb061bc7c89edaf285249fd105e6fa0
    bytes32 public constant INDEX_SLOT = bytes32(uint256(keccak256("eip1967.proxy.accumulate.index")) - 1);

    /// @notice The slot at which the first element of the ring buffer is
    ///         stored.
    /// @dev This is the first slot in the ring buffer. It stores the message
    ///      at index 0, later to be overwritten by the message at index
    ///      RING_BUFFER_SIZE.
    /// @dev Individual message slots are computed as follows:
    ///      MESSAGES_SLOT + (INDEX % RING_BUFFER_SIZE)
    /// @dev 0x0f6ff53b226acb501abb7521a3041f9642de1094e1d74e547a4dbd4bbe9693d3
    bytes32 public constant MESSAGES_SLOT = bytes32(uint256(keccak256("eip1967.proxy.accumulate.messages")) - 1);

    /// @notice Thrown when attempting to instantiate RING_BUFFER_SIZE to zero.
    ///         (This would make messageAt(0) unusable due to arithmetic underflow).
    /// @dev selector 0xc345df04.
    error ZeroRingBufferSize();

    /// @notice Thrown when attempting to read a message that is not in the
    ///         ring buffer.
    /// @param minRead - The smallest index still present in the ring buffer.
    /// @param maxRead - The largest index present in the ring buffer.
    /// @dev selector 0xc5a6beed.
    error OutOfBoundsRead(uint256 minRead, uint256 maxRead);

    /// @notice Construct a new RingBufferStorage contract.
    /// @dev Sets the ring buffer size.
    /// @param _ringBufferSize - The size of the ring buffer, in messages.
    constructor(uint64 _ringBufferSize) {
        if (_ringBufferSize == 0) revert ZeroRingBufferSize();
        RING_BUFFER_SIZE = _ringBufferSize;
    }

    /// @notice Get the number of messages in the vector.
    /// @dev This is an alias for nextIndex(). It allows clearer code when
    ///      reading from the contract.
    /// @return _count The number of messages in the vector.
    function count() public view returns (uint256 _count) {
        return nextIndex();
    }

    /// @notice Get the next message index.
    /// @return _index The next message index.
    function nextIndex() public view returns (uint64 _index) {
        uint256 slot = uint256(INDEX_SLOT);
        assembly ("memory-safe") {
            _index := sload(slot)
        }
    }

    /// @notice Get the message hash at the given index, if still present in
    ///         the ring buffer. Reverts if the message is no longer present.
    /// @dev Reads are checked, and the readable range is included in the
    ///      revert data. This is intended for off-chain access. It aids clients
    ///      in determining whether a given state contains the message they are
    ///      looking for.
    /// @custom:reverts OutOfBoundsRead(minRead, maxRead) if the message hash
    ///                 is no longer stored in the ring buffer.
    /// @param _index The index of the message in the history of all messages.
    /// @return h The message hash.
    function messageAt(uint64 _index) public view returns (bytes32 h) {
        uint256 _count = count();
        if (_count == 0) revert OutOfBoundsRead(0, 0);

        uint256 maxRead = _count - 1;
        uint256 minRead = maxRead >= RING_BUFFER_SIZE ? maxRead - (RING_BUFFER_SIZE - 1) : 0;

        if (_index < minRead || _index > maxRead) {
            revert OutOfBoundsRead(minRead, maxRead);
        }

        uint256 slot = messageSlot(_index);
        assembly ("memory-safe") {
            h := sload(slot)
        }
    }

    /// @notice Get the next message index, then increment it
    /// @dev This function is used when storing a message in the ring buffer to
    ///      reserve the spot.
    /// @return _index The next message index.
    function incrIndex() internal returns (uint64 _index) {
        _index = nextIndex();
        uint256 slot = uint256(INDEX_SLOT);
        assembly ("memory-safe") {
            sstore(slot, add(_index, 1))
        }
    }

    /// @notice Append a message hash to the vector.
    /// @dev The vector is backed by a ring buffer, so new messages overwrite
    ///      old ones in a FIFO fashion. The returned index is the index of the
    ///      message in the history of all messages sent from this Outbox. The
    ///      slot at which it is stored is calculated using messageSlot(_index).
    /// @param h The message hash to append.
    /// @return _index The index of the message in the history of all messages
    function pushMessage(bytes32 h) internal returns (uint64 _index) {
        _index = incrIndex();
        uint256 slot = messageSlot(_index);
        assembly ("memory-safe") {
            sstore(slot, h)
        }
    }

    /// @notice Get the slot storing the message hash at the given index.
    /// @dev This is calculated from the index and the ring buffer size.
    /// @dev This is intended for off-chain use. When proving a specific
    ///      message in the state trie, the output of this function can be
    ///      passed to eth_getProof directly as a storage key.
    /// @param _index The index of the message in the history of all messages
    ///        sent from this Outbox.
    /// @return slotNum The slot number of the message hash.
    function messageSlot(uint64 _index) internal view returns (uint256 slotNum) {
        slotNum = uint256(MESSAGES_SLOT) + (_index % RING_BUFFER_SIZE);
    }
}
"
    },
    "lib/forge-std/src/interfaces/IMulticall3.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity >=0.6.2 <0.9.0;

pragma experimental ABIEncoderV2;

interface IMulticall3 {
    struct Call {
        address target;
        bytes callData;
    }

    struct Call3 {
        address target;
        bool allowFailure;
        bytes callData;
    }

    struct Call3Value {
        address target;
        bool allowFailure;
        uint256 value;
        bytes callData;
    }

    struct Result {
        bool success;
        bytes returnData;
    }

    function aggregate(Call[] calldata calls)
        external
        payable
        returns (uint256 blockNumber, bytes[] memory returnData);

    function aggregate3(Call3[] calldata calls) external payable returns (Result[] memory returnData);

    function aggregate3Value(Call3Value[] calldata calls) external payable returns (Result[] memory returnData);

    function blockAndAggregate(Call[] calldata calls)
        external
        payable
        returns (uint256 blockNumber, bytes32 blockHash, Result[] memory returnData);

    function getBasefee() external view returns (uint256 basefee);

    function getBlockHash(uint256 blockNumber) external view returns (bytes32 blockHash);

    function getBlockNumber() external view returns (uint256 blockNumber);

    function getChainId() external view returns (uint256 chainid);

    function getCurrentBlockCoinbase() external view returns (address coinbase);

    function getCurrentBlockDifficulty() external view returns (uint256 difficulty);

    function getCurrentBlockGasLimit() external view returns (uint256 gaslimit);

    function getCurrentBlockTimestamp() external view returns (uint256 timestamp);

    function getEthBalance(address addr) external view returns (uint256 balance);

    function getLastBlockHash() external view returns (bytes32 blockHash);

    function tryAggregate(bool requireSuccess, Call[] calldata calls)
        external
        payable
        returns (Result[] memory returnData);

    function tryBlockAndAggregate(bool requireSuccess, Call[] calldata calls)
        external
        payable
        returns (uint256 blockNumber, bytes32 blockHash, Result[] memory returnData);
}
"
