contract Wrap {
    using SafeMath for uint256;
    address private _owner;
    using SafeERC20 for IERC20;
    constructor(){
        _owner = msg.sender;
    }
    
    function transfer(address token, address typeAddress, address marketAddress) external{
        uint allAmount = IERC20(token).balanceOf(address(this));
        uint typeAmount = allAmount.mul(2).div(3);
        IERC20(token).safeTransfer(typeAddress, typeAmount);
        IERC20(token).safeTransfer(marketAddress, allAmount.sub(typeAmount));
    }

    function transferBnb(uint256 amount) external{
        payable(_owner).transfer(amount);
    }
}

