    "contracts/TKFarm.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";

import "./abstracts/standard/Authorization.sol";

import "./interfaces/IERC20Mintable.sol";
import "./interfaces/IKYCBitkubChain.sol";
import "./interfaces/INextTransferRouter.sol";

// MasterChef is the master of Sushi. He can make Sushi and he is a fair guy.
//
// Note that it's ownable and the owner wields tremendous power. The ownership
// will be transferred to a governance smart contract once SUSHI is sufficiently
// distributed and the community can show to govern itself.
//
// Have fun reading it. Hopefully it's bug-free. God bless.
contract TKFarm is AccessControlEnumerable, Authorization
{
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    // Info of each user.
    struct UserInfo {
        uint256 amount; // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt. See explanation below.
        //
        // We do some fancy math here. Basically, any point in time, the amount of SUSHIs
        // entitled to a user but is pending to be distributed is:
        //
        //   pending reward = (user.amount * pool.accSushiPerShare) - user.rewardDebt
        //
        // Whenever a user deposits or withdraws LP tokens to a pool. Here's what happens:
        //   1. The pool's `accSushiPerShare` (and `lastRewardBlock`) gets updated.
        //   2. User receives the pending reward sent to his/her address.
        //   3. User's `amount` gets updated.
        //   4. User's `rewardDebt` gets updated.
    }
    // Info of each pool.
    struct PoolInfo {
        IERC20 lpToken; // Address of LP token contract.
        uint256 allocPoint; // How many allocation points assigned to this pool. SUSHIs to distribute per block.
        uint256 lastRewardBlock; // Last block number that SUSHIs distribution occurs.
        uint256 accRewardPerShare; // Accumulated SUSHIs per share, times 1e12. See below.
        IERC20Mintable rewardToken;
    }

    modifier onlyAdministrator {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Caller is not an administrator");
        _;
    }

    modifier checkBitkubNextUser(address bitkubNextUser) {
        if (bitkubNextUser != address(0)) {
            require(kycBitkubChain.kycsLevel(bitkubNextUser) >= acceptedKYCLevel, "only Bitkub Next user");
        }
        _;
    }

    modifier checkCallHelper(address bitkubNextUser) {
        if (bitkubNextUser != address(0)) {
            require(msg.sender == callHelper, "only CallHelper");
        }
        _;
    }

    // Block number when bonus SUSHI period ends.
    uint256 public bonusEndBlock;
    // SUSHI tokens created per block.
    uint256 public rewardPerBlock;
    // Bonus muliplier for early sushi makers.
    uint256 public bonusMultiPlier = 1;
    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;
    // Total allocation poitns. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;
    // The block number when SUSHI mining starts.
    uint256 public startBlock;

    mapping(address => bool) public lpTokens;

    uint256 public acceptedKYCLevel;

    IKYCBitkubChain public kycBitkubChain;
    INextTransferRouter public nextTransferRouter;

    address public callHelper;

    event Harvest(address indexed user, uint256 indexed pid, uint256 amount);
    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);

    constructor(
        uint256 _startBlock,
        uint256 _rewardPerBlock,
        string memory _projectName,
        address _adminProjectRouter,
        address _kycBitkubChain,
        address _nextTransferRouter,
        address _callHelper
    ) {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);

        startBlock = _startBlock;
        rewardPerBlock = _rewardPerBlock;
        PROJECT = _projectName;
        adminProjectRouter = IAdminProjectRouter(_adminProjectRouter);
        kycBitkubChain = IKYCBitkubChain(_kycBitkubChain);
        nextTransferRouter = INextTransferRouter(_nextTransferRouter);
        acceptedKYCLevel = 4;
        callHelper = _callHelper;
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    // Add a new lp to the pool. Can only be called by the owner.
    function add(
        uint256 _allocPoint,
        IERC20 _lpToken,
        IERC20Mintable _rewardToken,
        bool _withUpdate
    ) public onlyAdministrator {
        require(!lpTokens[address(_lpToken)], "add: already added");

        if (_withUpdate) {
            massUpdatePools();
        }

        uint256 lastRewardBlock =
            block.number > startBlock ? block.number : startBlock;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfo.push(
            PoolInfo({
                lpToken: _lpToken,
                allocPoint: _allocPoint,
                lastRewardBlock: lastRewardBlock,
                accRewardPerShare: 0,
                rewardToken: _rewardToken
            })
        );

        lpTokens[address(_lpToken)] = true;
    }

    // Update the given pool's SUSHI allocation point. Can only be called by the owner.
    function set(
        uint256 _pid,
        uint256 _allocPoint,
        bool _withUpdate
    ) public onlyAdministrator {
        if (_withUpdate) {
            massUpdatePools();
        }
        totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(
            _allocPoint
        );
        poolInfo[_pid].allocPoint = _allocPoint;
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to)
        public
        view
        returns (uint256)
    {
        if (_to <= bonusEndBlock) {
            return _to.sub(_from).mul(bonusMultiPlier);
        } else if (_from >= bonusEndBlock) {
            return _to.sub(_from);
        } else {
            return
                bonusEndBlock.sub(_from).mul(bonusMultiPlier).add(
                    _to.sub(bonusEndBlock)
                );
        }
    }

    // View function to see pending SUSHIs on frontend.
    function pendingReward(uint256 _pid, address _user)
        external
        view
        returns (uint256)
    {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accRewardPerShare = pool.accRewardPerShare;
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 multiplier =
                getMultiplier(pool.lastRewardBlock, block.number);
            uint256 reward =
                multiplier.mul(rewardPerBlock).mul(pool.allocPoint).div(
                    totalAllocPoint
                );
            accRewardPerShare = accRewardPerShare.add(
                reward.mul(1e12).div(lpSupply)
            );
        }
        return user.amount.mul(accRewardPerShare).div(1e12).sub(user.rewardDebt);
    }

    // Update reward vairables for all pools. Be careful of gas spending!
    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = pool.lpToken.balanceOf(address(this));
        if (lpSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
        uint256 reward =
            multiplier.mul(rewardPerBlock).mul(pool.allocPoint).div(
                totalAllocPoint
            );

        pool.rewardToken.mint(address(this), reward);
        pool.accRewardPerShare = pool.accRewardPerShare.add(
            reward.mul(1e12).div(lpSupply)
        );
        pool.lastRewardBlock = block.number;
    }

    // Deposit LP tokens to MasterChef for SUSHI allocation.
    function deposit(
        uint256 _pid, 
        uint256 _amount,
        address _bitkubNextUser
    ) public checkCallHelper(_bitkubNextUser) checkBitkubNextUser(_bitkubNextUser) {
        address _sender = _safeSender(_bitkubNextUser);

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_sender];
        updatePool(_pid);
        if (user.amount > 0) {
            uint256 pending =
                user.amount.mul(pool.accRewardPerShare).div(1e12).sub(
                    user.rewardDebt
                );
            _safeRewardTransfer(pool.rewardToken, _sender, pending);
        }

        _safeDeposit(address(pool.lpToken), _amount, _bitkubNextUser);

        user.amount = user.amount.add(_amount);
        user.rewardDebt = user.amount.mul(pool.accRewardPerShare).div(1e12);
        emit Deposit(_sender, _pid, _amount);
    }

    function deposit(
        uint256 _pid, 
        uint256 _amount
    ) external {
        deposit(_pid, _amount, address(0));
    }

    // Withdraw LP tokens from MasterChef.
    function withdraw(
        uint256 _pid, 
        uint256 _amount,
        address _bitkubNextUser
    ) public checkCallHelper(_bitkubNextUser) checkBitkubNextUser(_bitkubNextUser) {
        address _sender = _safeSender(_bitkubNextUser);

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_sender];
        require(user.amount >= _amount, "withdraw: not good");
        updatePool(_pid);
        uint256 pending =
            user.amount.mul(pool.accRewardPerShare).div(1e12).sub(
                user.rewardDebt
            );
        _safeRewardTransfer(pool.rewardToken, _sender, pending);
        user.amount = user.amount.sub(_amount);
        user.rewardDebt = user.amount.mul(pool.accRewardPerShare).div(1e12);
        pool.lpToken.safeTransfer(address(_sender), _amount);
        emit Withdraw(_sender, _pid, _amount);
    }

    function withdraw(
        uint256 _pid, 
        uint256 _amount
    ) external {
        withdraw(_pid, _amount, address(0));
    }

    /// @notice Harvest proceeds for transaction sender to `_to`.
    /// @param _pid The index of the pool. See `poolInfo`.
    /// @param _to Receiver of SUSHI rewards.
    function harvest(
        uint256 _pid, 
        address _to,
        address _bitkubNextUser
    ) public checkCallHelper(_bitkubNextUser) checkBitkubNextUser(_bitkubNextUser) {
        address _sender = _safeSender(_bitkubNextUser);

        updatePool(_pid);
        PoolInfo memory pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_sender];

        uint256 accumulated = user.amount.mul(pool.accRewardPerShare).div(1e12);
        uint256 pending = accumulated.sub(user.rewardDebt);

        user.rewardDebt = accumulated;

        if (pending != 0) {
            _safeRewardTransfer(pool.rewardToken, _to, pending);
        }

        emit Harvest(_sender, _pid, pending);
    }

    function harvest(
        uint256 _pid, 
        address _to
    ) external {
        harvest(_pid, _to, address(0));
    }

    // Withdraw without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(
        uint256 _pid,
        address _bitkubNextUser
    ) public checkCallHelper(_bitkubNextUser) checkBitkubNextUser(_bitkubNextUser) {
        address _sender = _safeSender(_bitkubNextUser);

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_sender];
        pool.lpToken.safeTransfer(address(_sender), user.amount);
        emit EmergencyWithdraw(_sender, _pid, user.amount);
        user.amount = 0;
        user.rewardDebt = 0;
    }

    function emergencyWithdraw(
        uint256 _pid
    ) external {
        emergencyWithdraw(_pid, address(0));
    }

    // Safe sushi transfer function, just in case if rounding error causes pool to not have enough SUSHIs.
    function _safeRewardTransfer(
        IERC20Mintable _rewardToken,
        address _to,
        uint256 _amount
    ) internal {
        uint256 rewardBal = _rewardToken.balanceOf(address(this));
        if (_amount > rewardBal) {
            _rewardToken.transfer(_to, rewardBal);
        } else {
            _rewardToken.transfer(_to, _amount);
        }
    }

    function _safeSender(
        address _bitkubNextUser
    ) internal view returns (address) {
        if (_bitkubNextUser != address(0)) {
            return _bitkubNextUser;
        }
        return msg.sender;
    }

    function _safeDeposit(
        address _token, 
        uint256 _amount, 
        address _bitkubNextUser
    ) internal {
        if (_bitkubNextUser != address(0)) {
            nextTransferRouter.transferFrom(PROJECT, _token, _bitkubNextUser, address(this), _amount);
        } else {
            IERC20(_token).safeTransferFrom(msg.sender, address(this), _amount);
        }
    }

    function updateRewardPerBlock(uint256 _rewardPerBlock) public onlyAdministrator {
        massUpdatePools();
        rewardPerBlock = _rewardPerBlock;
    }

    function updatebonusEndBlock(uint256 _bonusEndBlock) public onlyAdministrator {
        massUpdatePools();
        bonusEndBlock = _bonusEndBlock;
    }

    function updatebonusMultiPlier(uint256 _bonusMultiPlier) public onlyAdministrator {
        massUpdatePools();
        bonusMultiPlier= _bonusMultiPlier;
    }

    function setAdminProjectRouter(address _adminProjectRouter) public override onlyAdministrator {
        adminProjectRouter = IAdminProjectRouter(_adminProjectRouter);
    }

    function setKYCBitkubChain(address _kycBitkubChain) external onlyAdministrator {
        require(_kycBitkubChain != address(0), "change to the zero address");
        kycBitkubChain = IKYCBitkubChain(_kycBitkubChain);
    }

    function setAcceptedKYCLevel(uint256 _kycLevel) external onlyAdministrator {
        acceptedKYCLevel = _kycLevel;
    }

    function setNextTransferRouter(address _nextTransferRouter) external onlyAdministrator {
        require(_nextTransferRouter != address(0), "change to the zero address");
        nextTransferRouter = INextTransferRouter(_nextTransferRouter);
    }

    function setCallHelper(address _callHelper) external onlyAdministrator {
        require(_callHelper != address(0), "change to the zero address");
        callHelper = _callHelper;
    }

}"
    },
    "contracts/abstracts/standard/Authorization.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.4;

import "../../interfaces/IAdminProjectRouter.sol";

abstract contract Authorization {
    IAdminProjectRouter public adminProjectRouter;
    string public PROJECT; // Fill the project name

    event AdminProjectRouterSet(address indexed oldAdmin, address indexed newAdmin, address indexed caller);

    modifier onlySuperAdmin() {
        require(adminProjectRouter.isSuperAdmin(msg.sender, PROJECT), "Authorization: restricted only super admin");
        _;
    }

    modifier onlyAdmin() {
        require(adminProjectRouter.isAdmin(msg.sender, PROJECT), "Authorization: restricted only admin");
        _;
    }

    modifier onlySuperAdminOrAdmin() {
        require(
            adminProjectRouter.isSuperAdmin(msg.sender, PROJECT) || adminProjectRouter.isAdmin(msg.sender, PROJECT),
            "Authorization: restricted only super admin or admin"
        );
        _;
    }

    function setAdminProjectRouter(address _adminProjectRouter) public virtual onlySuperAdmin {
        require(_adminProjectRouter != address(0), "Authorization: new admin project router is the zero address");
        emit AdminProjectRouterSet(address(adminProjectRouter), _adminProjectRouter, msg.sender);
        adminProjectRouter = IAdminProjectRouter(_adminProjectRouter);
    }
}"
    },
