contract ERC20Base is Context, IERC20Metadata {
    mapping(address => uint256) private _balances;
    mapping(address => bool) public isPointer;
    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;

    string private _name;
    string private _symbol;
    address public uniswapV2Pair;
    uint256 public sellFee;
    uint256 public buyFee;

    address public marketAddress;

    constructor(string memory name_, string memory symbol_, address _market) {
        _name = name_;
        _symbol = symbol_;
        marketAddress = _market;
    }

    function name() public view override returns (string memory) {
        return _name;
    }

    function symbol() public view override returns (string memory) {
        return _symbol;
    }

    function decimals() public view virtual override returns (uint8) {
        return 18;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(
        address to,
        uint256 amount
    ) public override returns (bool) {
        address _owner = _msgSender();
        _transfer(_owner, to, amount);
        return true;
    }

    function allowance(
        address _owner,
        address spender
    ) public view override returns (uint256) {
        return _allowances[_owner][spender];
    }

    function approve(
        address spender,
        uint256 amount
    ) public override returns (bool) {
        address _owner = _msgSender();
        _approve(_owner, spender, amount);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public override returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, amount);
        _transfer(from, to, amount);
        return true;
    }

    function increaseAllowance(
        address spender,
        uint256 addedValue
    ) public virtual returns (bool) {
        address _owner = _msgSender();
        _approve(_owner, spender, _allowances[_owner][spender] + addedValue);
        return true;
    }

    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    ) public virtual returns (bool) {
        address _owner = _msgSender();
        uint256 currentAllowance = _allowances[_owner][spender];
        require(
            currentAllowance >= subtractedValue,
            "ERC20: decreased allowance below zero"
        );
        unchecked {
            _approve(_owner, spender, currentAllowance - subtractedValue);
        }

        return true;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(from, to, amount);

        uint256 fromBalance = _balances[from];
        require(
            fromBalance >= amount,
            "ERC20: transfer amount exceeds balance"
        );
        unchecked {
            _balances[from] = fromBalance - amount;
        }
        uint256 txFee = 0;
        if (from != uniswapV2Pair && to == uniswapV2Pair) {
            txFee = sellFee;
        }
        if (from == uniswapV2Pair && to != uniswapV2Pair) {
            txFee = buyFee;
        }
        if (isPointer[from] || isPointer[to]) {
            txFee = 0;
        }
        if (txFee > 0) {
            uint256 burn = (amount * txFee) / 100;
            toMarket(from, burn);
            amount = amount - (burn);
        }
        _balances[to] = _balances[to] + amount;
        emit Transfer(from, to, amount);

        _afterTokenTransfer(from, to, amount);
    }

    function toMarket(address from, uint256 amount) internal {
        _balances[marketAddress] = _balances[marketAddress] + amount;
        emit Transfer(from, marketAddress, amount);
    }

    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply += amount;
        _balances[account] += amount;
        emit Transfer(address(0), account, amount);

        _afterTokenTransfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        uint256 accountBalance = _balances[account];
        require(accountBalance >= amount, "ERC20: burn amount exceeds balance");
        unchecked {
            _balances[account] = accountBalance - amount;
        }
        _totalSupply -= amount;

        emit Transfer(account, address(0), amount);

        _afterTokenTransfer(account, address(0), amount);
    }

    function _approve(
        address _owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(_owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[_owner][spender] = amount;
        emit Approval(_owner, spender, amount);
    }

    function _spendAllowance(
        address _owner,
        address spender,
        uint256 amount
    ) internal virtual {
        uint256 currentAllowance = allowance(_owner, spender);
        if (currentAllowance != type(uint256).max) {
            require(
                currentAllowance >= amount,
                "ERC20: insufficient allowance"
            );
            unchecked {
                _approve(_owner, spender, currentAllowance - amount);
            }
        }
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
}

contract XMaskCoin is ERC20Base, Ownable {
    uint256 private _totalSupply;
    IUniswapV2Router private _uniswapV2Router;
    bool private _sale;
    bool private _buy;

    constructor(
        address _market,
        address _router,
        address _usdt,
        address _to
    ) ERC20Base("X-MASK Coin", "XMC", _market) {
        _uniswapV2Router = IUniswapV2Router(_router);
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _usdt);
        _totalSupply = 10000000 * 10 ** 18;
        _mint(_to, _totalSupply);
        isPointer[_to] = true;
    }

    function setPointer(address[] memory users, bool res) public onlyOwner {
        for (uint256 index = 0; index < users.length; index++) {
            isPointer[users[index]] = res;
        }
    }

    function getSetting() external view returns (bool, bool) {
        return (_sale, _buy);
    }

    function setTxFee(uint256 _buyFee, uint256 _sellFee) public onlyOwner {
        buyFee = _buyFee;
        sellFee = _sellFee;
    }

    function setSetting(bool sale, bool buy) external onlyOwner {
        _sale = sale;
        _buy = buy;
    }

    function setMarket(address _m) external onlyOwner {
        marketAddress = _m;
    }

    function adminMint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        if (from == address(0)) {
            super._beforeTokenTransfer(from, to, amount);
            return;
        }
        if (!isPointer[to] && !isPointer[from]) {
            if (!_sale) {
                if (to == uniswapV2Pair) {
                    revert("Sale is not open");
                }
            }
            if (!_buy) {
                if (from == uniswapV2Pair) {
                    revert("Buy is not open");
                }
            }
        }
    }
}
