pragma solidity 0.8.12;

import { IERC20, IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import { IStrategy } from "../interfaces/commons/IStrategy.sol";
import { IVault } from "../interfaces/commons/IVault.sol";

import { ERC20Initializable } from "../helpers/ERC20Initializable.sol";
import { OwnableInitializable } from "../helpers/OwnableInitializable.sol";
import { ReentrancyGuardInitializable } from "../helpers/ReentrancyGuardInitializable.sol";
import { PausableInitializable } from "../helpers/PausableInitializable.sol";

import { Checks } from "../libraries/Checks.sol";

contract VaultStargateUSDT is
  ERC20Initializable,
  OwnableInitializable,
  ReentrancyGuardInitializable,
  PausableInitializable
{
  using SafeERC20 for IERC20;
  using Checks for *;

  // Strategy that will responsible for generating yield
  // vaults best friend.
  IStrategy public strategy;

  // Underlying token that the vault/strategy is managing
  // token that is accepted on deposits and withdrawals and also profit/loss accounted on this asset.
  IERC20 public want;

  // Helper contract for users. Users can zap in to the vault with one of the underlying tokens of the lp token
  // Zapper contract will automatically convert the lp token components in proportion to pool balances then adds liquidity
  // to get LP tokens. Then it will deposit to the vault behalf of the user and sends the vault token to user.
  address public zapper;

  // for cloning
  bool private initialized;

  uint256 private minDeposit;

  event Deposited(uint256 depositedWant);
  event Withdrawn(uint256 withdrawnWant);
  event StrategySwitched(address newStrategy, address oldStrategy);
  event NewZapper(address newZapper);
  event Cloned(address indexed clone);

  constructor(
    IStrategy _strategy,
    string memory _name,
    string memory _symbol,
    address _zapper,
    IERC20 _want
  ) {
    _initialize(_strategy, _name, _symbol, _zapper, _want, msg.sender);
  }

  function _initialize(
    IStrategy _strategy,
    string memory _name,
    string memory _symbol,
    address _zapper,
    IERC20 _want,
    address owner_ // a.k.a vault manager
  ) internal {
    want = _want;
    strategy = _strategy;
    zapper = _zapper;
    address(want).validateWantOrNot(strategy.want());

    minDeposit = 10**IERC20Metadata(address(_want)).decimals();

    // Initialize inherited contracts
    initializeERC20(_name, _symbol);
    initializeOwnable(owner_);
    initializePausable();
    initializeReentrancyGuard();

    // initialize only once
    initialized = true;
  }

  function initializeVault(
    IStrategy _strategy,
    string memory _name,
    string memory _symbol,
    address _zapper,
    IERC20 _want,
    address owner_
  ) external {
    require(!initialized, "Already initialized!");
    _initialize(_strategy, _name, _symbol, _zapper, _want, owner_);
  }

  modifier onlyZapper() {
    require(msg.sender == zapper, "Only zapper");
    _;
  }

  /**
   * @notice Custom Checks how much the vault has on idle + strategy is managing
   * @dev Returns the total assets the vault managing.
   */
  function balance() public view returns (uint256) {
    return want.balanceOf(address(this)) + (strategy.totalAssets());
  }

  // pauses deposits to the vault
  // strategy pause function also pauses the deposits of vault and harvests
  function pause() external onlyOwner {
    _pause();
  }

  function unpause() external onlyOwner {
    _unpause();
  }

  /**
   * @notice Returns the total available assets (idle) in vault.
   * @dev Ideally this should always be 0 because we do not want want standing idle not generating yield.
   */
  function available() public view returns (uint256) {
    return want.balanceOf(address(this));
  }

  /**
   * @dev Function for various UIs to display the current value of one of our yield tokens.
   * Returns an uint256 with 18 decimals of how much underlying asset one vault share represents.
   */
  function getPricePerFullShare() external view returns (uint256) {
    return totalSupply() == 0 ? 1e18 : (balance() * 1e18) / totalSupply();
  }

  /**
   * @notice Deposits to strategy and mints new shares for the user
   * @dev This function can only be called from Zapper due to Shariya. Zapper will sent the
   * shares automatically to the caller.
   */
  function deposit(uint256 _amount)
    public
    onlyZapper
    nonReentrant
    whenNotPaused
    returns (uint256)
  {
    _amount.validateAmount();

    uint256 _pool = balance();
    want.safeTransferFrom(msg.sender, address(this), _amount);
    earn();
    uint256 _after = balance();
    _amount = _after - _pool; // Additional check for deflationary tokens
    uint256 shares = 0;
    if (totalSupply() == 0) {
      require(_amount >= minDeposit, "min deposit");
      shares = _amount;
    } else {
      shares = (_amount * totalSupply()) / _pool;
    }
    _mint(msg.sender, shares);
    emit Deposited(_amount);
    return shares;
  }

  /**
   * @dev Function to send funds into the strategy and put them to work. It's primarily called
   * by the vault's deposit() function.
   */
  function earn() public whenNotPaused {
    uint256 _bal = available();
    want.safeTransfer(address(strategy), _bal);
    strategy.deposit();
  }

  /**
   * @dev Function to exit the system. The vault will withdraw the required tokens
   * from the strategy and pay up the token holder. A proportional number of vault
   * tokens are burned in the process.
   */
  function withdraw(uint256 _shares) external nonReentrant returns (uint256) {
    _shares.validateAmount();

    uint256 r = (balance() * _shares) / totalSupply();
    _burn(msg.sender, _shares);

    uint256 b = want.balanceOf(address(this));
    if (b < r) {
      uint256 _withdraw = r - b;
      strategy.withdraw(_withdraw);
      uint256 _after = want.balanceOf(address(this));
      uint256 _diff = _after - b;
      if (_diff < _withdraw) {
        r = b + _diff;
      }
    }
    want.safeTransfer(msg.sender, r);
    emit Withdrawn(r);
    return r;
  }

  /**
   * @notice Function to set the new strategy in case of depreceation of the old strategy.
   * @dev New strategy's want must be identical to vaults want
   */
  function setNewStrategy(address _newStrategy) external onlyOwner {
    _newStrategy.validateAddress();
    address(want).validateWantOrNot(IStrategy(_newStrategy).want());
    strategy.retire();

    address _oldStrategy = address(strategy);
    strategy = IStrategy(_newStrategy);
    earn();

    emit StrategySwitched(_oldStrategy, _newStrategy);
  }

  /**
   * @notice Function to set the new zapper address for vaults deposits.
   */
  function setNewZapper(address _zapper) external onlyOwner {
    _zapper.validateAddress();
    zapper = _zapper;

    emit NewZapper(_zapper);
  }

  function clone(
    IStrategy _strategy,
    string memory _name,
    string memory _symbol,
    address _zapper,
    IERC20 _want,
    address owner_
  ) external onlyOwner {
    address _clonedVault = _clone(address(this));
    IVault(_clonedVault).initializeVault(
      _strategy,
      _name,
      _symbol,
      _zapper,
      _want,
      owner_
    );

    emit Cloned(_clonedVault);
  }

  function _clone(address implementation) internal returns (address instance) {
    /// @solidity memory-safe-assembly
    assembly {
      // Cleans the upper 96 bits of the `implementation` word, then packs the first 3 bytes
      // of the `implementation` address with the bytecode before the address.
      mstore(
        0x00,
        or(
          shr(0xe8, shl(0x60, implementation)),
          0x3d602d80600a3d3981f3363d3d373d3d3d363d73000000
        )
      )
      // Packs the remaining 17 bytes of `implementation` with the bytecode after the address.
      mstore(
        0x20,
        or(shl(0x78, implementation), 0x5af43d82803e903d91602b57fd5bf3)
      )
      instance := create(0, 0x09, 0x37)
    }
    require(instance != address(0), "ERC1167: create failed");
  }
}
