    "contracts/VerifyCollection.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/interfaces/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/introspection/ERC165Upgradeable.sol";
import "./interfaces/IOpenworld.sol";

contract VerifyCollection is
    ReentrancyGuardUpgradeable,
    OwnableUpgradeable,
    PausableUpgradeable
{
    event Verify(
        address userAddress,
        address nftAddressVerified,
        address tokenPaymentAddresses,
        uint256 tokenPaymentAmount,
        uint256 type_collection_index
    );

    uint256 constant BUSINESS_INDEX = 0;
    uint256 constant PERSONAL_INDEX = 1;

    struct PaymentToken {
        address tokenAddress;
        uint256[] amounts;
    }

    bytes4 public constant ERC1155_INTERFACE_ID = 0xd9b67a26;
    bytes4 public constant ERC721_INTERFACE_ID = 0x80ac58cd;

    mapping(uint256 => PaymentToken) public paymentTokens;
    mapping(address => address) public nftVerifiedByAddresses;
    address payable public feeRecipient;

    receive() external payable {}

    fallback() external payable {}

    function initialize(
        address _feeRecipient,
        address _tokenAddress0,
        address _tokenAddress1,
        uint256[] memory amounts0,
        uint256[] memory amounts1
    ) public initializer {
        __ReentrancyGuard_init_unchained();
        __Context_init_unchained();
        __Ownable_init_unchained();
        __Pausable_init_unchained();

        feeRecipient = payable(_feeRecipient);
        paymentTokens[0].tokenAddress = _tokenAddress0;
        paymentTokens[0].amounts = amounts0; 
        paymentTokens[1].tokenAddress = _tokenAddress1;
        paymentTokens[1].amounts = amounts1; 
    }

    //---------------------------ADMIN FUNCTIONS-------------------------------------
    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function depositToken(
        address tokenAddress,
        uint256 amount
    ) external onlyOwner {
        executeFundsTransfer(tokenAddress, msg.sender, address(this), amount);
    }

    function setFeeRecipient(address recipient) external onlyOwner {
        require(
            recipient != address(0),
            "Recipient address cannot be address 0"
        );
        feeRecipient = payable(recipient);
    }

    function rescueStuckErc20(
        address _tokenAddress,
        address _to
    ) external onlyOwner {
        uint256 _amount = IERC20Upgradeable(_tokenAddress).balanceOf(
            address(this)
        );
        executeFundsTransfer(_tokenAddress, address(this), _to, _amount);
    }

    function editPaymentToken(
        uint256 _paymentTokenIndex,
        address _tokenAddress,
        uint256[] memory _amounts
    ) external onlyOwner {
        paymentTokens[_paymentTokenIndex].tokenAddress = _tokenAddress;
        paymentTokens[_paymentTokenIndex].amounts = _amounts;
    }

    //---------------------------PUBLIC FUNCTIONS--------------------------------

    function verify(
        address _nftAddress,
        uint256 _tokenPaymentIndex,
        uint256 _type_collection_index
    ) external payable nonReentrant whenNotPaused {
        address userAddress = _msgSender();

        require(nftIsVerified(_nftAddress) == false, "NFT is verified");
        require(
            getOwnerOfCollection((_nftAddress)) == userAddress,
            "User address is not owner of collection"
        );
        PaymentToken memory paymentToken = paymentTokens[_tokenPaymentIndex];

        nftVerifiedByAddresses[_nftAddress] = userAddress;
        executeFundsTransfer(
            paymentToken.tokenAddress,
            userAddress,
            feeRecipient,
            paymentToken.amounts[_type_collection_index]
        );

        emit Verify(
            userAddress,
            _nftAddress,
            paymentToken.tokenAddress,
            paymentToken.amounts[_type_collection_index],
            _type_collection_index
        );
    }

    ///---------------------------VIEW FUINCTIONS------------------------------------

    function nftIsVerified(
        address _nftAddress
    ) public view returns (bool isVerified) {
        return (nftVerifiedByAddresses[_nftAddress] != address(0));
    }

    function getOwnerOfCollection(
        address _tokenAddress
    ) public view returns (address owner) {
        if (
            IERC165Upgradeable(_tokenAddress).supportsInterface(
                ERC1155_INTERFACE_ID
            )
        ) {
            return IERC1155Owner(_tokenAddress).owner();
        } else if (
            IERC165Upgradeable(_tokenAddress).supportsInterface(
                ERC721_INTERFACE_ID
            )
        ) {
            return IERC721Owner(_tokenAddress).owner();
        }
        return address(0);
    }

    function getPrices(uint256 tokenIndex) public view returns (uint256[] memory prices) {
        return paymentTokens[tokenIndex].amounts;
    }

    ///---------------------------INTERNAL FUINCTIONS------------------------------------
    /**
     * @dev Execute all token / native coin transfers associated with an order match (fees and from => to transfer)
     */
    function executeFundsTransfer(
        address paymentToken,
        address from,
        address to,
        uint256 amount
    ) internal virtual returns (uint256) {
        /* Only payable in the special case of unwrapped Native coin. */
        if (paymentToken != address(0)) {
            // If buy with bnb -> need msg.value
            if (from != address(0)) {
                require(msg.value == 0, "No need to transfer native coin");
            }
        }

        require(amount > 0, "Price must be greater than 0");

        if (paymentToken != address(0)) {
            if (from == address(this)) {
                uint256 balance = IERC20Upgradeable(paymentToken).balanceOf(
                    from
                );
                require(balance >= amount, "Not enough coin");
                bool sent = IERC20Upgradeable(paymentToken).transfer(
                    to,
                    amount
                );
                require(sent, "Failed to transfer token");
            } else {
                transferTokens(paymentToken, from, to, amount);
            }
        } else {
            require(msg.value >= amount, "Not enough native coin");

            if (to != address(this)) {
                require(msg.value >= amount, "Not enough coin");
                payable(to).transfer(amount);
            }
        }

        return amount;
    }

    /**
     * @dev Transfer tokens
     * @param token Token to transfer
     * @param from Address to charge fees
     * @param to Address to receive fees
     * @param amount Amount of protocol tokens to charge
     */
    function transferTokens(
        address token,
        address from,
        address to,
        uint256 amount
    ) internal virtual {
        if (amount > 0) {
            bool sent = IERC20Upgradeable(token).transferFrom(from, to, amount);
            require(sent, "Failed to transfer token");
        }
    }
}
