abstract contract TokenMinter is ERC165, Ownable {
    uint256 constant NO_LIMIT = 0;

    function supportsInterface(bytes4 interfaceId)
        public
        pure
        override
        returns (bool)
    {
        return interfaceId == type(ITokenMinter).interfaceId;
    }

    function tokenLimit() external pure virtual returns (uint256) {
        return NO_LIMIT;
    }
}

contract Active is TokenMinter {
    constructor(
        IToken _token,
        address _account,
        address _operator,
        uint256 _writeLimit
    ) {
        token = _token;
        account = _account;
        changeOperator(_operator);
        setWriteLimit(_writeLimit);
    }

    modifier onlyOperator() {
        require(operator == _msgSender(), "caller is not the operator");
        _;
    }

    function tokenLimit() external pure override returns (uint256) {
        return 1000 * 100000000 * 10**18;
    }

    using SafeMath for uint256;

    mapping(address => uint256) public claims;
    IToken token;

    address public account;
    address public operator;

    uint256 public writeLimit;
    uint256 public lastWriteAt;

    event AdminWrite(uint256 num);
    event Claim(uint256 num);
    event ClaimAll(uint256 num);

    function claimAll() public {
        claim(claims[_msgSender()]);
        emit ClaimAll(claims[_msgSender()]);
    }

    function claim(uint256 num) public {
        uint256 myClaim = claims[_msgSender()];
        require(myClaim >= num, "num err");

        claims[_msgSender()] = myClaim.sub(num);
        token.mint(_msgSender(), num.mul(2000).div(10000));
        token.mint(account, num.mul(6000).div(10000));

        emit Claim(num);
    }

    function write(address[] memory _ads, uint256[] memory _num)
        public
        onlyOperator
    {
        require(_ads.length == _num.length, "length err");

        require(block.timestamp > lastWriteAt.add(12 hours), "too often");

        uint256 totalNum = 0;
        for (uint256 i = 0; i < _num.length; i++) {
            totalNum = totalNum.add(_num[i]);
        }

        require(totalNum <= writeLimit, "upper than limit");

        for (uint256 i = 0; i < _ads.length; i++) {
            claims[_ads[i]] = claims[_ads[i]].add(_num[i]);
        }

        lastWriteAt = block.timestamp;
        emit AdminWrite(_ads.length);
    }

    function changeAccount(address newAccount) public onlyOwner {
        account = newAccount;
    }

    function changeOperator(address newOperator) public onlyOwner {
        require(newOperator != address(0));
        operator = newOperator;
    }

    function setWriteLimit(uint256 newLimit) public onlyOwner {
        require(newLimit <= 100 * 100000000 * 10**18); //10 billion
        writeLimit = newLimit;
    }
}
