// SPDX-License-Identifier: GPLv3

pragma solidity >=0.8.0;

interface IERC20 {
    
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256); 
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool); 
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

pragma solidity >=0.8.0;

contract UBCDevelopment {
    IERC20 public USDT = IERC20(0x55d398326f99059fF775485246999027B3197955);
    address[10] public admin = [0x6A997B495Ab0266e434c7192bb2ac369bb7E15d2,0x676aA69653A3556D608d306fAAE3E1Be440424Ae,0xDBcE98540c3ebDc28E1F69e6F26D196d7A1f7aAd,0x6cA4796133944D12D70B7287EdEd5Fd0CBD42c89,0x678eCeEC83564eA21c5361aCd6C0C175bB794423,0x96F80b3DB4739B014f9AfAA3f31a7De169066fF6,0x178781cfbFFfc740e4BBC02ECCEca07C769c1C03,0xC9128836F70a0a6C8fdbbf7c95B4c5Ecb362b939,0x2F6a05f477F2474A80A823B76E83605936f4bffD,0x622a13ee07BFeF2a1715d99e83C959Fe11F124d7];
    address public autodistributor;
   
      constructor(){ 
         autodistributor = msg.sender;     
     }

    function distribute() public {
      if(msg.sender==autodistributor)  //  automatic distribute in every week 
      {
        uint256 _bal = USDT.balanceOf(address(this));
        uint256 _amt = _bal/10;
        USDT.transfer(admin[0], _amt);
        USDT.transfer(admin[1], _amt);
        USDT.transfer(admin[2], _amt);
        USDT.transfer(admin[3], _amt);
        USDT.transfer(admin[4], _amt);
        USDT.transfer(admin[5], _amt);
        USDT.transfer(admin[6], _amt);
        USDT.transfer(admin[7], _amt);
        USDT.transfer(admin[8], _amt);
        USDT.transfer(admin[9], _amt);
               
      }
    }

     
    function setfeeReceivers(address[10] calldata _address) external {     // no one  can change  in this  contract and  address
        require(_address.length==10, "invalid");
         if(msg.sender==autodistributor) 
         {
            admin = _address;
            emit SetfeeReceivers(_address);  
         }   

          
    }
     event SetfeeReceivers(address[10]  Address);
}
