    modifier onlyOwner() {
        require(msg.sender == owner, "Only the contract owner can perform this action");
        _;
    }

    function claim() external {
        require(claimAmount[msg.sender] > 0, "No claimable amount for this address");
        require(block.timestamp >= lastClaimed[msg.sender] + claimInterval, "Already claimed today");

        // Update last claimed timestamp
        lastClaimed[msg.sender] = block.timestamp;

        // Transfer claimable USDT amount to the caller
        uint256 usdtAmount = claimAmount[msg.sender]; // Get the claimable USDT amount

        require(usdtToken.transfer(msg.sender, usdtAmount), "USDT transfer failed");

        emit AmountClaimed(msg.sender, usdtAmount);
    }

    function depositUSDT(uint256 amount) external onlyOwner {
        require(amount > 0, "Deposit amount should be greater than 0");

        // Transfer USDT tokens to the contract from the owner
        require(usdtToken.transferFrom(owner, address(this), amount), "USDT transfer to contract failed");

        emit USDTDeposited(amount);
    }

    function withdrawUSDT(uint256 amount) external onlyOwner {
        // Admin (owner) can withdraw USDT tokens from the contract
        require(usdtToken.transfer(owner, amount), "USDT transfer from contract failed");

        emit USDTWithdrawn(amount);
    }
      function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Invalid new owner address");
