contract Ownable is Context {
  address private _owner;
  address private _oracleManager;
  address private _previousOwner;
  uint256 private _lockTime;
  address private _presalecontract;

  event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

  constructor () internal {
    address msgSender = _msgSender();
    _owner = msgSender;
    _oracleManager = msgSender;
    emit OwnershipTransferred(address(0), msgSender);
  }

  function owner() public view returns (address) {
    return _owner;
  }

  function presaleContract() public view returns (address) {
    return _presalecontract;
  }

  function oracleManager() public view returns (address) {
    return _oracleManager;
  }

  modifier onlyOwner() {
    require(_owner == _msgSender(), "Ownable: caller is not the owner");
    _;
  }

  modifier onlyOracleManager() {
    require(_oracleManager == _msgSender(), "Ownable: caller is not the Oracle Manager");
    _;
  }

  modifier onlyPresaleContract() {
    require(_presalecontract == _msgSender(), "Ownable: caller is not the Presale Contract");
    _;
  }

  function renounceOwnership() public onlyOwner {
    emit OwnershipTransferred(_owner, address(0));
    _owner = address(0);
  }

  function transferOwnership(address newOwner) public onlyOwner {
    _transferOwnership(newOwner);
  }

  function _transferOwnership(address newOwner) internal {
    require(newOwner != address(0), "Ownable: new owner is the zero address");
    emit OwnershipTransferred(_owner, newOwner);
    _owner = newOwner;
  }


  function transferOracleManager(address newOracleManager) public onlyOwner {
    _transferOracleManager(newOracleManager);
  }

  function _transferOracleManager(address newOracleManager) internal {
    require(newOracleManager != address(0), "Ownable: new oracle manager is the zero address");
    _oracleManager = newOracleManager;
  }


  function transferPresaleContract(address newPresaleContract) public onlyOwner {
    _transferPresaleContract(newPresaleContract);
  }

  function _transferPresaleContract(address newPresaleContract) internal {
    require(newPresaleContract != address(0), "Ownable: new presale contract is the zero address");
    _presalecontract = newPresaleContract;
  }


    function geUnlockTime() public view returns (uint256) {
        return _lockTime;
    }

    //Locks the contract for owner for the amount of time provided
    function lockOwnerForTime(uint256 time) public virtual onlyOwner {
        _previousOwner = _owner;
        _owner = address(0);
        _lockTime = block.timestamp + time;
        emit OwnershipTransferred(_owner, address(0));
    }

    //Unlocks the contract for owner when _lockTime is exceeds
    function unlockOwner() public virtual {
        require(_previousOwner == msg.sender, "You don't have permission to unlock");
        require(block.timestamp > _lockTime , "Contract is locked until 7 days");
        emit OwnershipTransferred(_owner, _previousOwner);
        _owner = _previousOwner;
    }

}



