abstract contract ERC20Burnable is Context, ERC20 {


    uint256 public destroyFinalQuantity = 1000000 * 10 ** 18;

    modifier burnCheck(uint256 amount){
        if((totalSupply() - amount) < destroyFinalQuantity){
            amount = totalSupply() - destroyFinalQuantity;
        }
        if(amount == 0) return;
        _;
    }


    /**
     * @dev Destroys `amount` tokens from the caller.
     *
     * See {ERC20-_burn}.
     */
    function burn(uint256 amount) public burnCheck(amount) virtual {
        _burn(_msgSender(), amount);
    }

    /**
     * @dev Destroys `amount` tokens from `account`, deducting from the caller's
     * allowance.
     *
     * See {ERC20-_burn} and {ERC20-allowance}.
     *
     * Requirements:
     *
     * - the caller must have allowance for ``accounts``'s tokens of at least
     * `amount`.
     */
    function burnFrom(address account, uint256 amount) public burnCheck(amount) virtual {
        _spendAllowance(account, _msgSender(), amount);
        _burn(account, amount);
    }
}

abstract contract WhiteList is Ownable {
    mapping (address => bool) private _whiteList;
    event LogWhiteListChanged(address indexed _user, bool _status);
    modifier onlyWhiteList() {
        require(_whiteList[_msgSender()], "White list");_;
    }
    function isWhiteListed(address _maker) public view returns (bool) {
        return _whiteList[_maker];
    }
    function setWhiteList (address _evilUser, bool _status) public virtual onlyOwner returns (bool){
        _whiteList[_evilUser] = _status;
        emit LogWhiteListChanged(_evilUser, _status);
        return _whiteList[_evilUser];
    }
    function setWhiteListBatch (address[] memory _evilUserBatch, bool _status) public virtual onlyOwner{
        for(uint256 i = 0; i < _evilUserBatch.length;i++){
            setWhiteList(_evilUserBatch[i], _status);
        }
    }
}

contract Token is ERC20,ERC20Burnable,Ownable,WhiteList {
    using SafeMath for uint256;

    uint256 public total = 10000000 * 10 ** 18;
    address public marketAddress;
    address public maskAddress;
    address public uniswapV2Pair;
    uint256 distributorGas = 1000000;
    uint256 _swapIterativeSubscript = 1;//swap iteration subscript
    IUniswapV2Router02 public uniswapV2Router;

    uint256 public bonusStartNum = 500 * 10 ** 18;
    uint256 public buyMarketRatio = 2;
    uint256 public buyBurnRatio = 1;
    uint256 public buyLpRatio = 2;
    uint256 public sellMarketRatio = 2;
    uint256 public sellLpRatio = 2;
    uint256 public sellBurnRatio = 1;
    bool public sellStatus = false; 
    bool public buyStatus = false; 

    mapping(address=>bool) public uniswapV2PairMapping;
    mapping(address => uint256) public lpAddressMapping; // lp combination 
    address[] public lpAddressArray;//LP owner address , lp combination 


    constructor(address addr,address routerAddress_,address maskAddress_,address marketAddress_) ERC20("JOHO","JOHO"){
        super._mint(addr, total);
        uniswapV2Router = IUniswapV2Router02(routerAddress_);
        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory())
            .createPair(address(this),  maskAddress_);
        maskAddress = maskAddress_;
        marketAddress = marketAddress_;
        uniswapV2PairMapping[uniswapV2Pair] = true;
        //seize a seat
        lpAddressMapping[address(this)] = 0;
        lpAddressArray.push(address(this));
    }

    
    function setSellStatus(bool flag)public onlyOwner{
        sellStatus = flag;
    }
    function setbuyStatus(bool flag)public onlyOwner{
        buyStatus = flag;
    }
    function setUniswapV2PairMapping(address addr,bool flag)public onlyOwner{
        uniswapV2PairMapping[addr] = flag;
    }
    function setMarketAddress(address addr)public onlyOwner{
        marketAddress = addr;
    }
    function setBonusStartNum(uint256 num)public onlyOwner{
        bonusStartNum = num;
    }
    function setBuyMarketRatio(uint256 ratio)public onlyOwner{
        buyMarketRatio = ratio;
    }
    function setBuyBurnRatio(uint256 ratio)public onlyOwner{
        buyBurnRatio = ratio;
    }
    function setBuyLpRatio(uint256 ratio)public onlyOwner{
        buyLpRatio = ratio;
    }
    function setSellMarketRatio(uint256 ratio)public onlyOwner{
        sellMarketRatio = ratio;
    }
    function setSellLpRatio(uint256 ratio)public onlyOwner{
        sellLpRatio = ratio;
    }
    function setSellBurnRatio(uint256 ratio)public onlyOwner{
        sellBurnRatio = ratio;
    }

    function setSwapIterativeSubscript(uint256 index)public onlyOwner{
        _swapIterativeSubscript = index;
    }

    function setDistributorGas(uint256 gas) public onlyOwner{
        distributorGas = gas;
    }


    function _transfer(address sender,address recipient,uint256 amount) internal virtual override{
        _beforeTokenTransfer(sender, recipient, amount);

        uint256 senderBalance = _balances[sender];
        require(senderBalance >= amount, "ERC20: transfer amount exceeds balance");
        uint256 arrivalAmount = amount;
        
        if((uniswapV2PairMapping[sender] || uniswapV2PairMapping[recipient]) && (sender != address(this))){
            if(!isWhiteListed(sender) && !isWhiteListed(recipient)){
                //buy
                if(uniswapV2PairMapping[sender]){
                    require(buyStatus, "stop buy");
                    uint256 marketAmount = calculateRatio(amount,buyMarketRatio);
                    _balances[marketAddress] += marketAmount;
                    emit Transfer(sender, marketAddress, marketAmount);

                    uint256 lpAmount = calculateRatio(amount,buyLpRatio);
                    _balances[address(this)] += lpAmount;
                    emit Transfer(sender, address(this), lpAmount);
                    
                    uint256 burnAmount = calculateRatio(amount,buyBurnRatio);
                    _balances[address(0)] += burnAmount;
                    emit Transfer(sender, address(0), burnAmount);

                    arrivalAmount = amount - lpAmount - marketAmount - burnAmount;
                }else{
                    require(sellStatus, "stop sell");
                    uint256 marketAmount = calculateRatio(amount,sellMarketRatio);
                    _balances[marketAddress] += marketAmount;
                    emit Transfer(sender, marketAddress, marketAmount);
                    
                    uint256 lpAmount = calculateRatio(amount,sellLpRatio);
                    _balances[address(this)] += lpAmount;
                    emit Transfer(sender, address(this), lpAmount);
                    
                    uint256 burnAmount = calculateRatio(amount,sellBurnRatio);
                    _balances[address(0)] += burnAmount;
                    emit Transfer(sender, address(0), burnAmount);
                    
                    arrivalAmount = amount - burnAmount - marketAmount - lpAmount;
                }
                if(balanceOf(address(this)) >= bonusStartNum && (sender == uniswapV2Pair || recipient == uniswapV2Pair)){
                    lpProfitHandle(balanceOf(address(this)));
                }
            }
        }

        unchecked {
            _balances[sender] = senderBalance - amount;
        }
        _balances[recipient] += arrivalAmount;
        emit Transfer(sender, recipient, arrivalAmount);
        lpRelation(sender,recipient);
        _afterTokenTransfer(sender, recipient, amount);
    }

    function lpProfitHandle(uint256 lpAmount)internal {
        uint256 gasUsed = 0;
        uint256 gasLeft = gasleft();
        IUniswapV2Pair pair = IUniswapV2Pair(uniswapV2Pair);
        uint256 lpTotalSupply = pair.totalSupply();
        uint256 startIndex = _swapIterativeSubscript;
        while(gasUsed < distributorGas && lpAddressArray.length > 1) {
            address account = lpAddressArray[_swapIterativeSubscript];
            uint256 profit = getLpProfitAmount(lpAmount,lpTotalSupply,pair.balanceOf(account));
            _balances[address(this)] -= profit;
            _balances[account] += profit;
            emit Transfer(address(this), account, profit);

            gasUsed = gasUsed.add(gasLeft.sub(gasleft()));
            gasLeft = gasleft();
            //Start over
            if(_swapIterativeSubscript == lpAddressArray.length-1){
                _swapIterativeSubscript = 1;
            }else{
                _swapIterativeSubscript++;
            }
            if(_swapIterativeSubscript == startIndex){
                break;
            }
        }
        
    }

    function getLpProfitAmount(uint256 cake,uint256 totalSupply,uint256 balance) internal pure returns(uint256){
      uint256 ratio = balance.mul(10 ** 18).div(totalSupply);
      return cake.mul(ratio).div(10**18);
    }
    

    function calculateRatio(uint256 _amount,uint256 ratio) private pure returns (uint256) {
        return _amount.mul(ratio).div(
            10**2
        );
    }

    
    function swapTokensForToken(uint256 tokenAmount,address callbackAddress) private {
        super._approve(address(this), address(uniswapV2Router), tokenAmount);
        // generate the uniswap pair path of token1 -> token2
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = maskAddress;

        // // make the swap
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of token2
            path,
            callbackAddress,
            block.timestamp
        );
    }
    
    function lpRelation(address from,address to) internal {
        if(from == address(0) || isContractThis(from,to) || balanceOf(uniswapV2Pair) == 0) return;
        if(from == uniswapV2Pair || to == uniswapV2Pair){
            IUniswapV2Pair pair = IUniswapV2Pair(uniswapV2Pair);
            //Add liquidity dividend activation
            address accountAddress = from;
            if(from == uniswapV2Pair){
                accountAddress = to;
            }
            uint256 activationLpBalance = pair.balanceOf(accountAddress);
            
            if(lpAddressMapping[accountAddress] > 0 && activationLpBalance == 0){
                lpAddressArray[lpAddressMapping[accountAddress]] = lpAddressArray[lpAddressArray.length-1];
                lpAddressArray.pop();
                delete lpAddressMapping[accountAddress];
            }
            if(lpAddressMapping[accountAddress] == 0 && activationLpBalance > 0){
                lpAddressMapping[accountAddress] = lpAddressArray.length;
                lpAddressArray.push(accountAddress);
            }
        }
    }
    function isContractThis(address from,address to)internal view returns(bool){
        if(from == address(this) || to == address(this)){
            return true;
        }
        return false;
    }

}
