    address public feeTo = address(0xF741E1Ac203B5A3d9639215362415Af7Db1cc69c);
    address public liquidityTo = address(0x01DDE88A314154eF45d627472FeB24A13867e084);
    address public removeTo = address(0x222c8A0902EFc92f542050F7e6b65027D2DD5472);

    IERC20 public immutable iron = IERC20(0x1B04b10aD44233B208BDC6167af6189408BE1E52);
    IERC20 public immutable usdt = IERC20(0x55d398326f99059fF775485246999027B3197955);
    IERC20 public immutable mainPair = IERC20(0xc55795184A50b196Bd3CC7D607D49Ca4254033d4);


    event Deposit(address indexed user, uint256 uAmount);
    event Withdraw(address indexed user, uint256 lpAmount);
    
    constructor() {
        iron.safeApprove(address(ironswapRouter),MAX);
        usdt.safeApprove(address(ironswapRouter),MAX);
        mainPair.safeApprove(address(ironswapRouter), MAX);
    }

    function deposit(uint256 amount) external returns (bool) {
        usdt.safeTransferFrom(msg.sender, address(this), amount);
        _addLiquidity(amount.mul(55).div(100));
        usdt.safeTransfer(feeTo, amount.mul(45).div(100));
        emit Deposit(msg.sender, amount);
        return true;
    }

    function withdraw(uint256 lpAmount) external returns (bool) {
        mainPair.safeTransferFrom(msg.sender, address(this), lpAmount);
        uint256 ironBalanceBefore = iron.balanceOf(address(this));
        uint256 usdtBalanceBefore = usdt.balanceOf(address(this));
        _removeLiquidity(lpAmount);
        uint ironBalanceExtra = iron.balanceOf(address(this)).sub(ironBalanceBefore);
        iron.safeTransfer(address(0xdead), ironBalanceExtra.mul(80).div(100));
        iron.safeTransfer(removeTo, ironBalanceExtra.mul(20).div(100));
        uint256 usdtBalanceExtra = usdt.balanceOf(address(this)).sub(usdtBalanceBefore);
        usdt.safeTransfer(msg.sender, usdtBalanceExtra.mul(45).div(100));
        usdt.safeTransfer(removeTo, usdtBalanceExtra.mul(10).div(100));
        uint256 revest = usdtBalanceExtra.mul(45).div(100);
        _addLiquidity(revest.mul(55).div(100));
        usdt.safeTransfer(feeTo, revest.mul(45).div(100));
        emit Withdraw(msg.sender, lpAmount);
        return true;
    }

    function _removeLiquidity(uint256 lpAmount) internal returns (bool) {
         IUniswapV2Router02 router = IUniswapV2Router02(ironswapRouter);
        router.removeLiquidity(address(iron), address(usdt), lpAmount, 0,0,address(this),MAX);
        return true;
    }

    function _addLiquidity(uint stakeAmount) internal {
        uint stakingTokenAmount = stakeAmount / 2;
        IUniswapV2Router02 router = IUniswapV2Router02(ironswapRouter);
        address[] memory path = new address[](2);
        path[0] = address(usdt);
        path[1] = address(iron);
        uint balanceBefore = iron.balanceOf(address(this));
        router.swapExactTokensForTokensSupportingFeeOnTransferTokens(stakingTokenAmount,0,path,address(this),MAX);
        uint swapedAmount = iron.balanceOf(address(this)).sub(balanceBefore);

        router.addLiquidity(address(usdt), address(iron), stakingTokenAmount, swapedAmount, 0, 0, liquidityTo, MAX);
    }

    function setFeeTo(address _feeTo) external onlyOwner {
        feeTo = _feeTo;
    }

    function setLiquidityTo(address _liquidityTo) external onlyOwner {
        liquidityTo = _liquidityTo;
    }

    function setRemoveTo(address _removeTo) external onlyOwner {
        removeTo = _removeTo;
    }

    function claimBalance(uint256 amount) external onlyOwner {
        payable(msg.sender).transfer(amount);
    }

    function claimToken(address token, uint256 amount) external onlyOwner {
        IERC20(token).transfer(msg.sender, amount);
    }

    receive() external payable {}

}

