contract SMFXTOKEN is ERC20 {
    mapping(address => bool) public _isBlocked;
    mapping(address => bool) public _isPairAddress;
    uint256 public _maxTransferPerDay = 2500000 * 10**decimals();
    uint256 public _maxBuyPerDay = 2500000 * 10**decimals();
    uint256 public _maxBuyPerTrx = 2500000 * 10**decimals();
    uint256 public _maxSalePerDay = 2500000 * 10**decimals();
    uint256 public _maxSalePerTrx = 2500000 * 10**decimals();
    uint256 public _charityFees = 1;
    uint256 public _marketingFees = 2;
    uint256 public _liquidityFees = 2;
    uint256 public _sellFees = 5;
    uint256 constant secondsInDay = 86400;
    mapping(address => bool) public _exemptedFromTransferLimit;
    mapping(address => bool) public _exemptedFromBuySellLimit;

    address public charityAddress;
    address public marketingAddress;
    address public liquidityAddress;

    struct userTxn {
        uint256 firstSellTxnTime;
        uint256 sellTxnAmount;
        uint256 firstBuyTxnTime;
        uint256 buyTxnAmount;
        uint256 firstTxnTime;
        uint256 txnAmount;
    }

    mapping(address => userTxn) public txnDetails;

    constructor(
        address charityAddress_,
        address marketingAddress_,
        address liquidityAddress_
    ) ERC20('SMFX TOKEN', 'SMFX') {
        _mint(msg.sender, 5100000 * 10**decimals());
        _exemptedFromTransferLimit[msg.sender] = true;
        charityAddress = charityAddress_;
        marketingAddress = marketingAddress_;
        liquidityAddress = liquidityAddress_;
    }

    function setPairAddress(address pairAddress_, bool value_)
        public
        onlyOwner
    {
        _isPairAddress[pairAddress_] = value_;
    }

    function setCharityAddress(address charityAddress_) public onlyOwner {
        charityAddress = charityAddress_;
    }

    function setMarketingAddress(address marketingAddress_) public onlyOwner {
        marketingAddress = marketingAddress_;
    }

    function setLiquidityAddress(address liquidityAddress_) public onlyOwner {
        liquidityAddress = liquidityAddress_;
    }

    //Enter amount in wei
    function setCharityFees(uint256 amount_) public onlyOwner {
        _charityFees = amount_;
    }

    function setMarketingFees(uint256 amount_) public onlyOwner {
        _marketingFees = amount_;
    }

    function setLiquidityFees(uint256 amount_) public onlyOwner {
        _liquidityFees = amount_;
    }

    function setSellFees(uint256 amount_) public onlyOwner {
        _sellFees = amount_;
    }

    function setMaxSalePerTrx(uint256 amount_) public onlyOwner {
        _maxSalePerTrx = amount_;
    }

    function setMaxSalePerDay(uint256 amount_) public onlyOwner {
        _maxSalePerDay = amount_;
    }

    function setMaxBuyPerTrx(uint256 amount_) public onlyOwner {
        _maxBuyPerTrx = amount_;
    }

    function setMaxBuyPerDay(uint256 amount_) public onlyOwner {
        _maxBuyPerDay = amount_;
    }

    function setMaxTransferPerDay(uint256 amount_) public onlyOwner {
        _maxTransferPerDay = amount_;
    }

    function setExemptedFromTransferLimit(address _add, bool value)
        public
        onlyOwner
    {
        _exemptedFromTransferLimit[_add] = value;
    }

    function setExemptedFromBuySellLimit(address _add, bool value)
        public
        onlyOwner
    {
        _exemptedFromBuySellLimit[_add] = value;
    }

    function burn(uint256 amount) public virtual {
        _burn(_msgSender(), amount);
    }

    function burnFrom(address account, uint256 amount) public virtual {
        _spendAllowance(account, _msgSender(), amount);
        _burn(account, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override whenNotPaused {
        
        if (_isPairAddress[to]) {
            if (!_exemptedFromBuySellLimit[from]) {
                require(
                    amount <= _maxSalePerTrx,
                    'Amount Exceeds Max Sale Txn Amount'
                );

                // 2 stands for buy txn
                if (timePassed(from, 0) > secondsInDay) {
                    resetSell(from);
                }
                if (txnDetails[from].firstSellTxnTime == 0) {
                    txnDetails[from].firstSellTxnTime = block.timestamp;
                }
                txnDetails[from].sellTxnAmount += amount;
                require(
                    txnDetails[from].sellTxnAmount <= _maxSalePerDay,
                    'Amount Exceeds Max Sale Txn per day'
                );
                if (_sellFees > 0) {
                    uint256 feeAmount_;
                    uint256 charityAmount_;
                    uint256 marketingAmount_;
                    uint256 liquidityAmount_;
                    uint256 totalFee = (_charityFees +
                        _marketingFees +
                        _liquidityFees);
                    feeAmount_ = (_sellFees * amount) / 100;
                    amount = amount - feeAmount_;
                    charityAmount_ = (_charityFees * feeAmount_) / totalFee;
                    marketingAmount_ = (_marketingFees * feeAmount_) / totalFee;
                    liquidityAmount_ = (_liquidityFees * feeAmount_) / totalFee;
                    super._transfer(from, charityAddress, charityAmount_);
                    super._transfer(from, marketingAddress, marketingAmount_);
                    super._transfer(from, liquidityAddress, liquidityAmount_);
                }
                super._transfer(from, to, amount);
            } else {
                super._transfer(from, to, amount);
            }
        } else if (_isPairAddress[from]) {
            if (!_exemptedFromBuySellLimit[to]) {
                require(
                    amount <= _maxBuyPerTrx,
                    'Amount Exceeds Max Buy Txn Amount'
                );
                //1 stands for Buy Transaction
                if (timePassed(to, 1) > secondsInDay) {
                    resetBuy(to);
                }
                if (txnDetails[to].firstBuyTxnTime == 0) {
                    txnDetails[to].firstBuyTxnTime = block.timestamp;
                }
                txnDetails[to].buyTxnAmount += amount;
                require(
                    txnDetails[to].buyTxnAmount <= _maxBuyPerDay,
                    'Amount Exceeds Max Buy Txn per day'
                );
                super._transfer(from, to, amount);
            } else {
                super._transfer(from, to, amount);
            }
        } else {
            if (!_exemptedFromTransferLimit[from]) {
                if (timePassed(from, 2) > secondsInDay) {
                    reset(from);
                }
                if (txnDetails[from].firstTxnTime == 0) {
                    txnDetails[from].firstTxnTime = block.timestamp;
                }
                txnDetails[from].txnAmount += amount;

                require(
                    txnDetails[from].txnAmount <= _maxTransferPerDay,
                    'Amount Exceeds Max Txn per day'
                );

                super._transfer(from, to, amount);
            } else {
                super._transfer(from, to, amount);
            }
        }
    }

    function timePassed(address userAdd_, uint256 txnType)
        public
        view
        returns (uint256)
    {
        if (txnType == 0) {
            return (block.timestamp - txnDetails[userAdd_].firstSellTxnTime);
        } else if (txnType == 1) {
            return (block.timestamp - txnDetails[userAdd_].firstBuyTxnTime);
        } else {
            return (block.timestamp - txnDetails[userAdd_].firstTxnTime);
        }
    }

    function resetSell(address userAdd_) internal {
        txnDetails[userAdd_].firstSellTxnTime = 0;
        txnDetails[userAdd_].sellTxnAmount = 0;
    }

    function resetBuy(address userAdd_) internal {
        txnDetails[userAdd_].firstBuyTxnTime = 0;
        txnDetails[userAdd_].buyTxnAmount = 0;
    }

    function reset(address userAdd_) internal {
        txnDetails[userAdd_].firstTxnTime = 0;
        txnDetails[userAdd_].txnAmount = 0;
    }
}
