pragma solidity ^0.8.0;


interface IBEP20 {
    function totalSupply() external view returns (uint256);
    function decimals() external view returns (uint8);
    function symbol() external view returns (string memory);
    function name() external view returns (string memory);
    function getOwner() external view returns (address);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address _owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

abstract contract Auth {
    address internal owner;
    mapping (address => bool) internal authorizations;

    constructor(address _owner) {
        owner = _owner;
        authorizations[_owner] = true;
    }
    modifier onlyOwner() {
        require(isOwner(msg.sender), "!OWNER"); _;
    }
    modifier authorized() {
        require(isAuthorized(msg.sender), "!AUTHORIZED"); _;
    }
    function authorize(address adr) public onlyOwner {
        authorizations[adr] = true;
    }
    function unauthorize(address adr) public onlyOwner {
        authorizations[adr] = false;
    }
    function isOwner(address account) public view returns (bool) {
        return account == owner;
    }
    function isAuthorized(address adr) public view returns (bool) {
        return authorizations[adr];
    }
    function renounceOwnership() public virtual authorized {
        emit OwnershipTransferred(address(0));
        owner = address(0);
    }
    function transferOwnership(address payable adr) public onlyOwner {
        owner = adr;
        authorizations[adr] = true;
        emit OwnershipTransferred(adr);
    }

    event OwnershipTransferred(address owner);
}

contract WisdomScapeToken is IBEP20, Auth {
    using SafeMath for uint256;
    mapping (address => uint256) public _balances;
    mapping (address => mapping (address => uint256)) public _allowances;

    address public mintAddress = 0x3848A09eAd4Bf6b1B1fEEDA130cde39f7E7912f9;
    address public burnAddress = 0xD073ec0Cf31329CC211a145A5F68CE69889d3575;
    address public nodeAddress = 0xae72206eacDB0096033B076409aEf1e3F6EcCA52;
    address public lockAddress = 0xB8E74C33280055Db0E1c7e68Cf0a8CA8806F15Df;
    address public withdrawAddress = 0x00E1AFBDDf0A94b65A7ab4ED4898F84DE2Ff8b20;
    
    string public _name;
    string public _symbol;
    uint8 public _decimals;
    uint256 public _totalSupply;

    constructor() Auth(msg.sender){
        owner = msg.sender;
        _name = "WisdomScape";
        _symbol = "WSW";
        _decimals = 6;  
        _totalSupply = 1_000_000_000 * (10 ** _decimals);

        _distribution(2,burnAddress);
        _distribution(3,nodeAddress);
        _distribution(5,lockAddress);
        _distribution(90,address(this));

        _allowances[address(this)][withdrawAddress] = ~uint256(0);
        _allowances[address(this)][mintAddress] = ~uint256(0);
        
    }
    function _distribution(uint256 rate,address to) internal {
        _balances[to] = _totalSupply.mul(rate).div(100);
        emit Transfer(address(this), to, _balances[to]);
    }
    function setWithdrawAddress(address _new)public onlyOwner{
        withdrawAddress = _new;
        _allowances[address(this)][withdrawAddress] = ~uint256(0);
    }
    function setMintAddress(address _new)public onlyOwner{
        mintAddress = _new;
        _allowances[address(this)][mintAddress] = ~uint256(0);
    }
    receive() external payable { }

    function totalSupply() external view override returns (uint256) { return _totalSupply; }
    function decimals() external view override returns (uint8) { return _decimals; }
    function symbol() external view override returns (string memory) { return _symbol; }
    function name() external view override returns (string memory) { return _name; }
    function getOwner() external view override returns (address) { return owner; }
    function balanceOf(address account) public view override returns (uint256) { return _balances[account]; }
    function allowance(address holder, address spender) external view override returns (uint256) { return _allowances[holder][spender]; }

   function transfer(address recipient, uint256 amount) external override returns (bool) {
        return _transferFrom(msg.sender, recipient, amount);
    }

    function transferFrom(address sender, address recipient, uint256 amount) external override returns (bool) {
 
        if(_allowances[sender][msg.sender] != _totalSupply){
            _allowances[sender][msg.sender] = _allowances[sender][msg.sender].sub(amount, "Insufficient Allowance");
        }

        return _transferFrom(sender, recipient, amount);
    }

    function _transferFrom(address _from, address _to, uint _value) private returns (bool) {
        _balances[_from] = _balances[_from].sub(_value);
        _balances[_to] = _balances[_to].add(_value);
        emit Transfer(_from, _to, _value);
        return true;
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _allowances[msg.sender][spender] = amount;
        emit Approval(msg.sender, spender, amount);
        return true;
    }
    
}

