/**
 * @title ERC721Mintable
 * @dev ERC721 minting logic
 */
contract ERC721Mintable is ERC721, MinterRole {

    struct Auction {
    
        address seller;
    
        uint128 startingPrice;
    
        uint16 status;
    }

    uint256 public ownerCut=10;
    
    address public cutAddress=address(0);
  
    mapping (uint256 => Auction) tokenIdToAuction;

    mapping (uint256 => address) claims;
 
    event AuctionCreated(uint256 tokenId, uint256 startingPrice);
 
    event AuctionSuccessful(uint256 tokenId, uint256 totalPrice, address winner);
   
    event AuctionCancelled(uint256 tokenId);
    
    /**
    * @dev Function to mint tokens
    * @param to The address that will receive the minted tokens.
    * @return A boolean that indicates if the operation was successful.
    */
    function mint(address to,uint256 tokenId)
      public
      onlyMinter
    {
      _mint(to, tokenId);
    }

    function togame(uint256 tokenId) external {
        require(_owns(msg.sender, tokenId));
        _togame(tokenId);
    }

    function claim(address to, uint256 tokenId) external onlyMinter {
        require(_owns(this, tokenId));
        _claim(to, tokenId);
    }

    function setCut(uint256 _cut) external onlyMinter {
        require(_cut <= 10000);
        ownerCut = _cut;
    }
    
    function setFeeAddress(address account) public onlyMinter {
        require(account != address(0));
        require(account != address(this));
        cutAddress=address(account);
    }
    
    function getCutBalance() external onlyMinter view returns (uint256) {
         uint256 balance=address(this).balance;
         return balance;
    }
    
    function withdrawBalance() external onlyMinter {
            uint256 balance=address(this).balance;
            cutAddress.transfer(balance);
    }
    
    function getAuction(uint256 _tokenId)
        external
        view
        returns
    (
        address seller,
        uint256 startingPrice,
        uint16 status
    ) {
        Auction storage auction = tokenIdToAuction[_tokenId];
        return (
            auction.seller,
            auction.startingPrice,
            auction.status
        );
    }
    
    function createAuction(
        uint256 _tokenId,
        uint256 _startingPrice
    )
        external
    {
      
        require(_startingPrice == uint256(uint128(_startingPrice)));
       
        require(_owns(msg.sender, _tokenId));
      
        _escrow(msg.sender, _tokenId);
      
        Auction memory auction = Auction(
            msg.sender,
            uint128(_startingPrice),
            uint16(0)
        );
   
        _addAuction(_tokenId, auction);
    }


    function bid(uint256 _tokenId)
        external
        payable
    {
      
        _bid(_tokenId, msg.value);
      
        _transfer(msg.sender, _tokenId);
    }
   
    function cancelAuction(uint256 _tokenId)
        external
    {
     
        Auction storage auction = tokenIdToAuction[_tokenId];
      
        address seller = auction.seller;
       
        require(msg.sender == seller);
        
        _cancelAuction(_tokenId, seller);
    }

    function _claim(address to, uint256 _tokenId) internal {
        _transfer(to, _tokenId);
        delete claims[_tokenId];
    }
    function _togame(uint256 _tokenId) internal {
        transferFrom(msg.sender, this, _tokenId);
        claims[_tokenId] = msg.sender;
    }
    // batch burn
    function _burns(uint256[] tokenIds) internal {
        for (uint256 i=0; i < tokenIds.length; i++) {
            address owner = ownerOf(tokenIds[i]);
            require(owner != address(0), "can not burn token from address(0).");
            require(owner != address(this), "can not burn token from address(this).");
        }
        for (uint256 j=0; j < tokenIds.length; j++) {
            address to = ownerOf(tokenIds[j]);
            super._burn(to, tokenIds[j]);
        }
    }

    function _owns(address _claimant, uint256 _tokenId) internal view returns (bool) {
        return (ownerOf(_tokenId) == _claimant);
    }

 
    function _escrow(address _owner, uint256 _tokenId) internal {
       
        transferFrom(_owner, this, _tokenId);
    }

    function _addAuction(uint256 _tokenId, Auction _auction) internal {
        
        tokenIdToAuction[_tokenId] = _auction;
       
        emit AuctionCreated(
            uint256(_tokenId),
            uint256(_auction.startingPrice)
        );
    }
    

    function _computeCut(uint256 _price) internal view returns (uint256) {
        
        return _price * ownerCut / 10000;
    }
    
    function _cancelAuction(uint256 _tokenId, address _seller) internal {
       
         _removeAuction(_tokenId);
   
        _transfer(_seller, _tokenId);
      
        emit AuctionCancelled(_tokenId);
    }
    
    function _transfer(address _receiver, uint256 _tokenId) internal {
        // it will throw if transfer fails
        this.transfer(_receiver, _tokenId);
    }

    function _bid(uint256 _tokenId, uint256 _bidAmount)
        internal
        returns (uint256)
    {
        Auction storage auction = tokenIdToAuction[_tokenId];
        

        require(_isOnAuction(auction));
        
        auction.status=uint16(1);


        uint256 price = auction.startingPrice;
    
        require(_bidAmount >= price);

    
        address seller = auction.seller;

    
        _removeAuction(_tokenId);

    
        if (price > 0) {
    
            uint256 auctioneerCut = _computeCut(price);
            
    
            uint256 sellerProceeds = price-auctioneerCut;
            
    
            seller.transfer(sellerProceeds);
        }

    
        uint256 bidExcess = _bidAmount - price;

    
        msg.sender.transfer(bidExcess);

    
        emit AuctionSuccessful(_tokenId, price, msg.sender);

        return price;
    }
    
    function _removeAuction(uint256 _tokenId) internal {
        delete tokenIdToAuction[_tokenId];
    }
    
    function _isOnAuction(Auction storage _auction) internal view returns (bool) {
        return (_auction.status==uint16(0));
    }

}

