    "contracts/MasterChef.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

// MasterChef is the master of Cake. He can make Cake and he is a fair guy.
//
// Note that it's ownable and the owner wields tremendous power. The ownership
// will be transferred to a governance smart contract once CAKE is sufficiently
// distributed and the community can show to govern itself.
//
// Have fun reading it. Hopefully it's bug-free. God bless.
contract MasterChef is Ownable {
    using SafeMath for uint256;

    // Info of each user.
    struct UserInfo {
        uint256 amount; // How many LP tokens the user has provided.
        uint256 rewardDebt; // Reward debt. See explanation below.
    }

    // Info of each pool.
    struct PoolInfo {
        address owner;
        uint256 lastRewardBlock;
        uint256 endBlock;
        uint256 amount;
        bool isWithdrawn;
    }

    // The PNN TOKEN!
    ERC20 public pnn;
    // The PG TOKEN!
    ERC20 public pg;
    // CAKE tokens created per block.
    uint256 public pgPerBlock;
    uint256 public nodeRewardPerBlock;
    uint256 public endBlock;
    uint256 public nodeRewardBlockCount;
    bool public canAdd = true;
    uint256 public totalAmount;

    uint256 public accCakePerShare;
    uint256 public lastRewardBlock;
    // Bonus muliplier for early cake makers.
    uint256 public BONUS_MULTIPLIER = 1;

    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping(address => UserInfo) public userInfo;
    mapping(address => uint256) public userStakeInfo;
    // The block number when CAKE mining starts.

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(
        address indexed user,
        uint256 indexed pid,
        uint256 amount
    );

    constructor(
        address _pnn,
        address _pg,
        uint256 _pgPerBlock,
        uint256 _nodeRewardPerBlock,
        uint128 _nodeRewardBlockCount,
        uint256 _startBlock,
        uint256 _endBlock
    ) payable {
        pnn = ERC20(_pnn);
        pg = ERC20(_pg);
        pgPerBlock = _pgPerBlock;
        nodeRewardPerBlock = _nodeRewardPerBlock;
        nodeRewardBlockCount = _nodeRewardBlockCount;
        lastRewardBlock = _startBlock;
        endBlock = _endBlock;
        poolInfo.push( //EmptyPool or default pool
            PoolInfo({
                owner: address(0),
                isWithdrawn: true,
                lastRewardBlock: 0,
                endBlock: 0,
                amount: 0
            })
        );
    }

    function updateCanAdd(bool _canAdd) public onlyOwner {
        canAdd = _canAdd;
    }

    function updateMultiplier(uint256 multiplierNumber) public onlyOwner {
        BONUS_MULTIPLIER = multiplierNumber;
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    function getAllNode() public view returns (PoolInfo[] memory nodes) {
        return poolInfo;
        // for (uint256 i = 0; i < poolInfo.length; i++) {
        //     if (poolInfo[i].owner == msg.sender) {
        //         nodes.push(poolInfo[i]);
        //     }
        // }
    }

    // Add a new lp to the pool. Can only be called by the owner.
    // XXX DO NOT add the same LP token more than once. Rewards will be messed up if you do.
    function add() public {
        require(
            pnn.transferFrom(msg.sender, address(this), 1),
            "can not get PNN"
        );
        require(canAdd, "can not add new Pool");
        poolInfo.push(
            PoolInfo({
                owner: msg.sender,
                lastRewardBlock: block.number,
                isWithdrawn: false,
                endBlock: block.number + nodeRewardBlockCount,
                amount: 0
            })
        );
    }

    function pendingNodeReward(
        uint _pid
    ) public view returns (uint256, uint256) {
        PoolInfo storage info = poolInfo[_pid];
        if (block.number < info.lastRewardBlock) {
            return (0, block.number);
        }
        uint256 blockNumber = block.number;
        if (blockNumber > info.endBlock) {
            blockNumber = info.endBlock;
        }
        return (
            ((blockNumber - info.lastRewardBlock) * nodeRewardPerBlock),
            blockNumber
        );
    }

    function claimNodeReward(uint _pid) public {
        PoolInfo storage info = poolInfo[_pid];
        require(info.owner == msg.sender, "only owner");
        (uint pending, uint blockNumber) = pendingNodeReward(_pid);
        if (pending > 0) {
            info.lastRewardBlock = blockNumber;
            pg.transfer(msg.sender, pending);
        }
    }

    function canWithdrawPNN(
        uint256 pid,
        address user
    ) public view returns (bool) {
        PoolInfo storage p = poolInfo[pid];
        return !p.isWithdrawn && p.owner == user && block.number > p.endBlock;
    }

    function withdrawPNN(uint256 pid) public {
        require(canWithdrawPNN(pid, msg.sender), "can not withdraw PNN");
        poolInfo[pid].isWithdrawn = true;
        pnn.transfer(msg.sender, 1);
    }

    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(
        uint256 _from,
        uint256 _to
    ) public view returns (uint256) {
        return _to.sub(_from).mul(BONUS_MULTIPLIER);
    }

    // View function to see pending CAKEs on frontend.
    function pendingCake(
        address _user
    )
        external
        view
        returns (uint256 pendingReward, UserInfo memory info, uint256 stakeInfo)
    {
        uint256 blockNumber = block.number;
        if (block.number > endBlock) {
            blockNumber = endBlock;
        }
        uint256 _accCakePerShare;
        if (blockNumber > lastRewardBlock && totalAmount != 0) {
            uint256 multiplier = getMultiplier(lastRewardBlock, blockNumber);
            uint256 cakeReward = multiplier.mul(pgPerBlock);
            _accCakePerShare = accCakePerShare.add(
                cakeReward.mul(1e12).div(totalAmount)
            );
        }
        UserInfo storage user = userInfo[_user];
        return (
            user.amount.mul(_accCakePerShare).div(1e12).sub(user.rewardDebt),
            user,
            userStakeInfo[_user]
        );
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool() public {
        if (block.number <= lastRewardBlock) {
            return;
        }
        if (totalAmount == 0) {
            lastRewardBlock = uint128(block.number);
            return;
        }
        uint256 blockNumber = block.number;
        if (block.number > endBlock) {
            blockNumber = endBlock;
        }
        uint256 multiplier = getMultiplier(lastRewardBlock, blockNumber);
        uint256 cakeReward = multiplier.mul(pgPerBlock);
        accCakePerShare = accCakePerShare.add(
            cakeReward.mul(1e12).div(totalAmount)
        );
        lastRewardBlock = uint128(blockNumber);
    }

    // Deposit LP tokens to MasterChef for CAKE allocation.
    function deposit(uint256 _pid) public {
        require(_pid != 0, "deposit CAKE by staking");
        require(poolInfo[_pid].owner != address(0), "pool not exsit");
        require(
            userStakeInfo[msg.sender] == 0 || userStakeInfo[msg.sender] == _pid,
            "only deposit one pool"
        );
        require(block.number < endBlock, "pool expired");
        UserInfo storage user = userInfo[msg.sender];
        if (userStakeInfo[msg.sender] == 0) {
            userStakeInfo[msg.sender] = _pid;
        }
        updatePool();
        if (user.amount > 0) {
            uint256 pending = user.amount.mul(accCakePerShare).div(1e12).sub(
                user.rewardDebt
            );
            if (pending > 0) {
                pg.transfer(msg.sender, pending);
            }
        }
        uint256 _amount = pg.balanceOf(msg.sender);
        if (_amount > 0) {
            totalAmount += _amount - user.amount;
            poolInfo[_pid].amount += _amount - user.amount;
            user.amount = _amount;
        }
        user.rewardDebt = user.amount.mul(accCakePerShare).div(1e12);
        emit Deposit(msg.sender, _pid, _amount);
    }

    function claimReward() public {
        UserInfo storage user = userInfo[msg.sender];
        if (user.amount == 0) {
            return;
        }
        updatePool();
        uint256 pending = user.amount.mul(accCakePerShare).div(1e12).sub(
            user.rewardDebt
        );
        if (pending > 0) {
            pg.transfer(address(msg.sender), pending);
        }
        user.rewardDebt = user.amount.mul(accCakePerShare).div(1e12);
    }

    // Withdraw LP tokens from MasterChef.
    function withdraw(uint256 _pid) public {
        require(_pid != 0, "withdraw CAKE by unstaking");
        UserInfo storage user = userInfo[msg.sender];
        uint256 userAmount = user.amount;
        require(userAmount >= 0, "withdraw: not good");

        updatePool();
        uint256 pending = user.amount.mul(accCakePerShare).div(1e12).sub(
            user.rewardDebt
        );
        if (pending > 0) {
            pg.transfer(address(msg.sender), pending);
        }

        user.amount = 0;
        totalAmount -= userAmount;
        poolInfo[_pid].amount -= userAmount;
        userStakeInfo[msg.sender] = 0;
        user.rewardDebt = user.amount.mul(accCakePerShare).div(1e12);
        emit Withdraw(msg.sender, _pid, userAmount);
    }
}
"
    },
