contract PNN is ERC20("Pego Node NFT token", "PNN"), Ownable {
    mapping(address => bool) public blackList;
    mapping(address => bool) public whiteList;

    constructor() {}

    function decimals() public view virtual override returns (uint8) {
        return 0;
    }

    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        require(!blackList[from] || whiteList[to], "can not transfer");
        super._transfer(from, to, amount);
    }

    function updateBlackList(address user, bool flag) public onlyOwner {
        blackList[user] = flag;
    }

    function updateWhiteList(address user, bool flag) public onlyOwner {
        whiteList[user] = flag;
    }
}
