contract UMI is IERC20, SafeOwnable {
    using SafeMath for uint256;
    using Address for address;
    uint256 public constant BASE_RATIO = 100;
    uint256 public startTime = 1751515041;
    mapping(address => bool) public isMarketPair;
    mapping(address => bool) public isExcludedFromFee;
    address public deadAddress = 0x000000000000000000000000000000000000dEaD;
    address public marketAddress = 0x000000000000000000000000000000000000dEaD;
    string private _name = "UMI";
    string private _symbol = "UMI";
    uint8 private _decimals = 18;
    uint256 private _totalSupply = 500 * 10 ** 8 * 10 ** _decimals;
    mapping(address => uint256) _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapPair;

    constructor(address router, address usdt, address market) {
        init(router, usdt, market);
    }

    function init(address router, address usdt, address market) private {
        uniswapV2Router = IUniswapV2Router02(router);
        uniswapPair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(
            address(this),
            usdt
        );
        isMarketPair[address(uniswapPair)] = true;
        marketAddress = market;
        _balances[_msgSender()] = _totalSupply;
        emit Transfer(address(0), _msgSender(), _totalSupply);

        isExcludedFromFee[owner()] = true;
        isExcludedFromFee[market] = true;
        isExcludedFromFee[router] = true;
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function allowance(
        address owner,
        address spender
    ) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function increaseAllowance(
        address spender,
        uint256 addedValue
    ) public virtual returns (bool) {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    ) public virtual returns (bool) {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }

    function approve(
        address spender,
        uint256 amount
    ) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) private returns (bool) {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        if (isExcludedFromFee[sender] || isExcludedFromFee[recipient]) {
            _basicTransfer(sender, recipient, amount);
        } else {
            require(block.timestamp > startTime, "Not yet open");
            if (isMarketPair[recipient]) {
                _balances[sender] = _balances[sender].sub(
                    amount,
                    "Insufficient Balance"
                );
                uint256 marketAmount = amount.mul(5).div(BASE_RATIO);
                _balances[marketAddress] = _balances[marketAddress].add(
                    marketAmount
                );
                emit Transfer(sender, marketAddress, marketAmount);

                uint256 deadAmount = amount.mul(5).div(BASE_RATIO);
                _balances[deadAddress] = _balances[deadAddress].add(deadAmount);
                emit Transfer(sender, deadAddress, deadAmount);

                uint256 realAmount = amount.sub(marketAmount).sub(deadAmount);
                _balances[recipient] = _balances[recipient].add(realAmount);
                emit Transfer(sender, recipient, realAmount);
            } else {
                _basicTransfer(sender, recipient, amount);
            }
        }
        return true;
    }

    function _basicTransfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal returns (bool) {
        _balances[sender] = _balances[sender].sub(
            amount,
            "Insufficient Balance"
        );
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
        return true;
    }

    function balanceOf(
        address account
    ) public view virtual override returns (uint256) {
        return _balances[account];
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function getCirculatingSupply() public view returns (uint256) {
        return totalSupply().sub(balanceOf(deadAddress)).sub(balanceOf(address(0)));
    }

    function setIsExcludedFromFee(
        address[] memory account,
        bool flag
    ) public onlyOwner {
        require(account.length > 0, "no account");
        for (uint256 i = 0; i < account.length; i++) {
            isExcludedFromFee[account[i]] = flag;
        }
    }

    function setStartTime(uint256 _startTime) public onlyOwner {
        startTime = _startTime;
    }

    function setMarketAddress(address _marketAddress) public onlyOwner {
        marketAddress = _marketAddress;
    }
}
