    "contracts/BIT.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

// Uncomment this line to use console.log
//import "hardhat/console.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol";
import "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

contract TokenDistributor {
    address public _owner;

    constructor(address token) {
        _owner = msg.sender;
        IERC20(token).approve(msg.sender, type(uint256).max);
    }

    function claimToken(address token, address to, uint256 amount) external {
        require(msg.sender == _owner, "not owner");
        IERC20(token).transfer(to, amount);
    }
}

contract BIT is IERC20, IERC20Metadata, Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    mapping(address => uint256) private _balances;

    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;

    string private _name = "BITDEX";
    string private _symbol = "BIT";

    mapping(address => bool) public pairs;

    mapping(address => bool) public excludeFee;

    uint256 public buyFee = 100;

    uint256 public sellFee1 = 100;
    uint256 public sellFee2 = 400;

    uint256 public airdropFeeAmount = 3000000;

    address public fundationAdd;

    address public deadAdd1 = 0x000000000000000000000000000000000000dEaD;
    address public deadAdd2 = 0x0000000000000000000000000000000000000000;

    TokenDistributor public tokenDistributor;

    uint256 public numToSell = 500 * 1e18;

    IUniswapV2Router02 public router;
    IERC20 public eth;

    uint256 public lpRewardAmount;
    uint256 public holdersRewardAmount;
    uint256 public lastLpDistirbuteBlockNum;
    uint256 public lastHoldersDistirbuteBlockNum;

    address public mainPair;

    bool private inSwap;

    uint256 public startTradeTime;

    mapping(address => bool) public operatorList;

    modifier lockTheSwap() {
        inSwap = true;
        _;
        inSwap = false;
    }

    modifier onlyOperatorList() {
        address msgSender = msg.sender;
        require(
            operatorList[msgSender] || msgSender == owner(),
            "BIT: not access!"
        );
        _;
    }

    constructor(
        address _receiver,
        address _fundationAdd,
        IERC20 _eth,
        IUniswapV2Router02 _iUniswapV2Router02
    ) {
        fundationAdd = _fundationAdd;
        eth = _eth;
        router = _iUniswapV2Router02;
        IUniswapV2Factory iUniswapV2Factory = IUniswapV2Factory(
            _iUniswapV2Router02.factory()
        );
        // token1 = BIT , token2 = ETH
        address pair1 = iUniswapV2Factory.createPair(
            address(this),
            address(_eth)
        );
        require(
            address(this) == IUniswapV2Pair(pair1).token1(),
            "BIT: please retry!"
        );
        tokenDistributor = new TokenDistributor(address(_eth));
        _allowances[address(this)][address(_iUniswapV2Router02)] = type(uint256)
            .max;
        mainPair = pair1;
        pairs[pair1] = true;
        excludeFee[_receiver] = true;
        excludeFee[_fundationAdd] = true;
        excludeFee[deadAdd1] = true;
        excludeFee[deadAdd2] = true;

        excludeHolder[deadAdd1] = true;
        excludeHolder[deadAdd2] = true;

        operatorList[msg.sender] = true;
        operatorList[_fundationAdd] = true;

        _mint(_receiver, 100000000 * 10 ** decimals());
    }

    function setPair(address _pair, bool _state) public onlyOperatorList {
        require(_pair != address(0), "BIT:ZERO_ADDRESS");
        pairs[_pair] = _state;
    }

    function setExcludeFee(
        address _address,
        bool _state
    ) public onlyOperatorList {
        require(_address != address(0), "BIT:ZERO_ADDRESS");
        excludeFee[_address] = _state;
    }

    function batchSetExcludeFee(
        address[] calldata _address,
        bool _state
    ) public onlyOperatorList {
        for (uint i = 0; i < _address.length; i++) {
            excludeFee[_address[i]] = _state;
        }
    }

    function setFundationAdd(address _fundationAdd) public onlyOperatorList {
        fundationAdd = _fundationAdd;
    }

    function setRouter(IUniswapV2Router02 _router) public onlyOperatorList {
        router = _router;
    }

    function setMainPair(address _mainPair) public onlyOperatorList {
        mainPair = _mainPair;
    }

    function claimToken(
        address token,
        address to,
        uint256 amount
    ) public onlyOperatorList {
        tokenDistributor.claimToken(token, to, amount);
    }

    function setTokenDistributor(
        TokenDistributor _tokenDistributor
    ) public onlyOperatorList {
        tokenDistributor = _tokenDistributor;
    }

    function setNumToSell(uint _numToSell) public onlyOperatorList {
        numToSell = _numToSell;
    }

    function setOperatorList(address _operator) public onlyOperatorList {
        operatorList[_operator] = true;
    }

    function startTrade() external onlyOperatorList {
        startTradeTime = block.timestamp;
    }

    /**
     * @dev Moves `amount` of tokens from `from` to `to`.
     *
     * This internal function is equivalent to {transfer}, and can be used to
     * e.g. implement automatic token fees, slashing mechanisms, etc.
     *
     * Emits a {Transfer} event.
     *
     * Requirements:
     *
     * - `from` cannot be the zero address.
     * - `to` cannot be the zero address.
     * - `from` must have a balance of at least `amount`.
     */
    function _transfer(address from, address to, uint256 amount) internal {
        require(from != address(0), "ERC20: transfer from the zero address");
        //require(to != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(from, to, amount);

        uint256 fromBalance = _balances[from];
        require(
            fromBalance >= amount,
            "ERC20: transfer amount exceeds balance"
        );
        bool isSwap = pairs[from] || pairs[to];
        if (isSwap) {
            if (excludeFee[from] || excludeFee[to] || inSwap) {
                // whiteList
                _standardTransfer(from, to, amount);
            } else {
                if (pairs[to]) {
                    uint256 maxSellAmount = (fromBalance * 9999) / 10000;
                    if (amount >= maxSellAmount) {
                        amount = maxSellAmount;
                    }
                }
                if (pairs[to] && _isAddLiquidity()) {
                    _standardTransfer(from, to, amount);
                } else if (pairs[from] && _isRemoveLiquidity()) {
                    _standardTransfer(from, to, amount);
                } else {
                    require(startTradeTime > 0,"BIT: not start!");
                    require(
                        startTradeTime + 300 < block.timestamp,
                        "BIT: not start trade!"
                    );
                    _transferWithFee(from, to, amount);
                    if (from != address(this)) {
                        if (rewardTypeIsLp) {
                            processLpReward(rewardGas);
                        } else {
                            processReward(rewardGas);
                        }
                    }
                }
            }
        } else {
            // normal transfer
            _standardTransfer(from, to, amount);
        }
        _afterTokenTransfer(from, to, amount);
    }

    function _standardTransfer(
        address from,
        address to,
        uint256 amount
    ) internal {
        _balances[from] -= amount;
        _balances[to] += amount;
        emit Transfer(from, to, amount);
    }

    function _transferWithFee(
        address from,
        address to,
        uint256 amount
    ) internal {
        if (pairs[from]) {
            //buy
            _balances[from] -= amount;
            uint256 buyFeeAmount = amount.mul(buyFee).div(10000);
            buyFeeAmount -= airdropFeeAmount;
            _balances[deadAdd1] += buyFeeAmount;
            emit Transfer(from, deadAdd1, buyFeeAmount);

            _airdropAddress(from, to, airdropFeeAmount);

            uint256 tAmount = amount.sub(buyFeeAmount).sub(airdropFeeAmount);
            _balances[to] += tAmount;
            emit Transfer(from, to, tAmount);
        } else {
            //sell
            _balances[from] -= amount;
            uint256 sellFee1Amount = amount.mul(sellFee1).div(10000);
            _balances[fundationAdd] += sellFee1Amount;
            emit Transfer(from, fundationAdd, sellFee1Amount);

            uint256 sellFee2Amount = amount.mul(sellFee2).div(10000);
            sellFee2Amount -= airdropFeeAmount;
            _balances[address(this)] += sellFee2Amount;
            emit Transfer(from, address(this), sellFee2Amount);

            if (balanceOf(address(this)) >= numToSell && !inSwap) {
                _swapTokenToETH(balanceOf(address(this)));
            }

            _airdropAddress(from, to, airdropFeeAmount);

            uint256 tAmount = amount
                .sub(sellFee1Amount)
                .sub(sellFee2Amount)
                .sub(airdropFeeAmount);
            _balances[to] += tAmount;
            emit Transfer(from, to, tAmount);
        }
    }

    function _isAddLiquidity() internal view returns (bool isAdd) {
        IUniswapV2Pair iUniswapV2Pair = IUniswapV2Pair(mainPair);
        (uint r0, uint256 r1, ) = iUniswapV2Pair.getReserves();

        address tokenOther = address(eth);
        uint256 r;
        if (tokenOther < address(this)) {
            r = r0;
        } else {
            r = r1;
        }

        uint bal = IERC20(tokenOther).balanceOf(mainPair);
        isAdd = bal > r;
    }

    function _isRemoveLiquidity() internal view returns (bool isRemove) {
        IUniswapV2Pair iUniswapV2Pair = IUniswapV2Pair(mainPair);
        (uint r0, uint256 r1, ) = iUniswapV2Pair.getReserves();

        address tokenOther = address(eth);
        uint256 r;
        if (tokenOther < address(this)) {
            r = r0;
        } else {
            r = r1;
        }
        uint bal = IERC20(tokenOther).balanceOf(mainPair);
        isRemove = r > bal;
    }

    uint256 public rewardGas = 500000;

    function setRewardGas(uint _rewardGas) public onlyOwner {
        rewardGas = _rewardGas;
    }

    // lp
    address[] public LPholders;
    mapping(address => uint256) public LPholderIndex;
    mapping(address => bool) public LPexcludeHolder;

    function _addLpHolder(address account) private {
        if (0 == LPholderIndex[account]) {
            if (0 == LPholders.length || LPholders[0] != account) {
                uint256 size;
                assembly {
                    size := extcodesize(account)
                }
                if (size > 0) {
                    return;
                }
                LPholderIndex[account] = LPholders.length;
                LPholders.push(account);
            }
        }
    }

    function setLPexcludeHolder(
        address account,
        bool state
    ) public onlyOperatorList {
        LPexcludeHolder[account] = state;
    }

    // token
    address[] public holders;
    mapping(address => uint256) public holderIndex;
    mapping(address => bool) public excludeHolder;

    function _addHolder(address account) private {
        if (0 == holderIndex[account]) {
            if (0 == holders.length || holders[0] != account) {
                uint256 size;
                assembly {
                    size := extcodesize(account)
                }
                if (size > 0) {
                    return;
                }
                holderIndex[account] = holders.length;
                holders.push(account);
            }
        }
    }

    function setexcludeHolder(
        address account,
        bool state
    ) public onlyOperatorList {
        excludeHolder[account] = state;
    }

    // tokenRewards
    uint256 public currentIndex;
    uint256 public RewardCondition = 0.1 * 1e18;
    uint256 public progressRewardBlock;
    uint256 public progressRewardBlockInternal = 1;
    address public mainLPHoler;
    bool public rewardTypeIsLp;

    function processReward(uint256 gas) private {
        uint256 blockNum = block.number;

        if (progressRewardBlock + progressRewardBlockInternal > blockNum) {
            return;
        }

        if (holdersRewardAmount < RewardCondition) {
            return;
        }
        uint256 thisTokenTotal = totalSupply() -
            balanceOf(deadAdd1) -
            balanceOf(deadAdd2);
        uint256 tokenBalance;
        address shareHolder;
        uint256 amount;

        uint256 shareholderCount = holders.length;
        uint256 gasUsed = 0;
        uint256 iterations = 0;
        uint256 gasLeft = gasleft();

        while (gasUsed < gas && iterations < shareholderCount) {
            if (currentIndex >= shareholderCount) {
                currentIndex = 0;
            }
            shareHolder = holders[currentIndex];
            tokenBalance = balanceOf(shareHolder);
            if (
                !excludeHolder[shareHolder] && tokenBalance > minHolderForReward
            ) {
                amount = (RewardCondition * tokenBalance) / thisTokenTotal;
                if (amount > 0) {
                    eth.safeTransferFrom(
                        address(tokenDistributor),
                        shareHolder,
                        amount
                    );
                    holdersRewardAmount -= amount;
                }
            }
            gasUsed = gasUsed + (gasLeft - gasleft());
            gasLeft = gasleft();
            currentIndex++;
            iterations++;
        }
        progressRewardBlock = blockNum;
        rewardTypeIsLp = true;
    }

    // LPRewards
    uint256 public LPcurrentIndex;

    function processLpReward(uint256 gas) private {
        uint256 blockNum = block.number;

        if (progressRewardBlock + progressRewardBlockInternal > blockNum) {
            return;
        }

        if (lpRewardAmount < RewardCondition) {
            return;
        }

        IERC20 lpToken = IERC20(mainPair);
        uint lpTokenTotal = lpToken.totalSupply() -
            lpToken.balanceOf(mainLPHoler);

        address shareHolder;
        uint256 lpBalance;
        uint256 amount;

        uint256 shareholderCount = holders.length;
        uint256 gasUsed = 0;
        uint256 iterations = 0;
        uint256 gasLeft = gasleft();

        while (gasUsed < gas && iterations < shareholderCount) {
            if (LPcurrentIndex >= shareholderCount) {
                LPcurrentIndex = 0;
            }
            shareHolder = holders[LPcurrentIndex];
            lpBalance = lpToken.balanceOf(shareHolder);
            if (!LPexcludeHolder[shareHolder]) {
                amount = (RewardCondition * lpBalance) / lpTokenTotal;
                if (amount > 0) {
                    eth.safeTransferFrom(
                        address(tokenDistributor),
                        shareHolder,
                        amount
                    );
                    lpRewardAmount -= amount;
                }
            }
            gasUsed = gasUsed + (gasLeft - gasleft());
            gasLeft = gasleft();
            LPcurrentIndex++;
            iterations++;
        }
        progressRewardBlock = blockNum;
        rewardTypeIsLp = false;
    }

    function setMainLPHolder(address _mainLPHolder) public onlyOperatorList {
        mainLPHoler = _mainLPHolder;
    }

    function setRewardCondition(uint _rewardCondition) public onlyOperatorList {
        RewardCondition = _rewardCondition;
    }

    function setRewardInternal(uint _rewardInternal) public onlyOperatorList {
        progressRewardBlockInternal = _rewardInternal;
    }

    function _swapTokenToETH(uint tokenAmount) internal lockTheSwap {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = address(eth);
        uint256 balanceBefore = eth.balanceOf(address(tokenDistributor));
        router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(tokenDistributor),
            block.timestamp + 3600
        );
        uint256 swapBalance = eth.balanceOf(address(tokenDistributor)) -
            balanceBefore;
        lpRewardAmount += swapBalance / 2;
        holdersRewardAmount += swapBalance / 2;
    }

    address public lastAirdropAddress;

    function _airdropAddress(
        address from,
        address recipient,
        uint airdropAmount
    ) internal {
        uint256 seed = (uint160(lastAirdropAddress) | block.number) ^
            uint160(recipient);
        uint256 uintAirdropAmount = airdropAmount / 3;
        address airdropAddress;
        for (uint256 i; i < 3; ) {
            airdropAddress = address(uint160(seed | airdropAmount));
            _balances[airdropAddress] += uintAirdropAmount;
            emit Transfer(from, airdropAddress, uintAirdropAmount);
            unchecked {
                ++i;
                seed = seed >> 1;
            }
        }
        lastAirdropAddress = airdropAddress;
    }

    /**
     * @dev Destroys `amount` tokens from the caller.
     *
     * See {ERC20-_burn}.
     */
    function burn(uint256 amount) public virtual {
        _burn(_msgSender(), amount);
    }

    /**
     * @dev Destroys `amount` tokens from `account`, deducting from the caller's
     * allowance.
     *
     * See {ERC20-_burn} and {ERC20-allowance}.
     *
     * Requirements:
     *
     * - the caller must have allowance for ``accounts``'s tokens of at least
     * `amount`.
     */
    function burnFrom(address account, uint256 amount) public virtual {
        _spendAllowance(account, _msgSender(), amount);
        _burn(account, amount);
    }

    /**
     * @dev Returns the name of the token.
     */
    function name() public view virtual override returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

    /**
     * @dev Returns the number of decimals used to get its user representation.
     * For example, if `decimals` equals `2`, a balance of `505` tokens should
     * be displayed to a user as `5.05` (`505 / 10 ** 2`).
     *
     * Tokens usually opt for a value of 18, imitating the relationship between
     * Ether and Wei. This is the value {ERC20} uses, unless this function is
     * overridden;
     *
     * NOTE: This information is only used for _display_ purposes: it in
     * no way affects any of the arithmetic of the contract, including
     * {IERC20-balanceOf} and {IERC20-transfer}.
     */
    function decimals() public view virtual override returns (uint8) {
        return 18;
    }

    /**
     * @dev See {IERC20-totalSupply}.
     */
    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See {IERC20-balanceOf}.
     */
    function balanceOf(
        address account
    ) public view virtual override returns (uint256) {
        return _balances[account];
    }

    /**
     * @dev See {IERC20-transfer}.
     *
     * Requirements:
     *
     * - `to` cannot be the zero address.
     * - the caller must have a balance of at least `amount`.
     */
    function transfer(
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        address owner = _msgSender();
        _transfer(owner, to, amount);
        return true;
    }

    /**
     * @dev See {IERC20-allowance}.
     */
    function allowance(
        address owner,
        address spender
    ) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    /**
     * @dev See {IERC20-approve}.
     *
     * NOTE: If `amount` is the maximum `uint256`, the allowance is not updated on
     * `transferFrom`. This is semantically equivalent to an infinite approval.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function approve(
        address spender,
        uint256 amount
    ) public virtual override returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, amount);
        return true;
    }

    /**
     * @dev See {IERC20-transferFrom}.
     *
     * Emits an {Approval} event indicating the updated allowance. This is not
     * required by the EIP. See the note at the beginning of {ERC20}.
     *
     * NOTE: Does not update the allowance if the current allowance
     * is the maximum `uint256`.
     *
     * Requirements:
     *
     * - `from` and `to` cannot be the zero address.
     * - `from` must have a balance of at least `amount`.
     * - the caller must have allowance for ``from``'s tokens of at least
     * `amount`.
     */
    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, amount);
        _transfer(from, to, amount);
        return true;
    }

    /**
     * @dev Atomically increases the allowance granted to `spender` by the caller.
     *
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {IERC20-approve}.
     *
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function increaseAllowance(
        address spender,
        uint256 addedValue
    ) public virtual returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, allowance(owner, spender) + addedValue);
        return true;
    }

    /**
     * @dev Atomically decreases the allowance granted to `spender` by the caller.
     *
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {IERC20-approve}.
     *
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     * - `spender` must have allowance for the caller of at least
     * `subtractedValue`.
     */
    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    ) public virtual returns (bool) {
        address owner = _msgSender();
        uint256 currentAllowance = allowance(owner, spender);
        require(
            currentAllowance >= subtractedValue,
            "ERC20: decreased allowance below zero"
        );
        unchecked {
            _approve(owner, spender, currentAllowance - subtractedValue);
        }

        return true;
    }

    /** @dev Creates `amount` tokens and assigns them to `account`, increasing
     * the total supply.
     *
     * Emits a {Transfer} event with `from` set to the zero address.
     *
     * Requirements:
     *
     * - `account` cannot be the zero address.
     */
    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply += amount;
        unchecked {
            // Overflow not possible: balance + amount is at most totalSupply + amount, which is checked above.
            _balances[account] += amount;
        }
        emit Transfer(address(0), account, amount);

        _afterTokenTransfer(address(0), account, amount);
    }

    /**
     * @dev Destroys `amount` tokens from `account`, reducing the
     * total supply.
     *
     * Emits a {Transfer} event with `to` set to the zero address.
     *
     * Requirements:
     *
     * - `account` cannot be the zero address.
     * - `account` must have at least `amount` tokens.
     */
    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        uint256 accountBalance = _balances[account];
        require(accountBalance >= amount, "ERC20: burn amount exceeds balance");
        unchecked {
            _balances[account] = accountBalance - amount;
            // Overflow not possible: amount <= accountBalance <= totalSupply.
            _totalSupply -= amount;
        }

        emit Transfer(account, address(0), amount);

        _afterTokenTransfer(account, address(0), amount);
    }

