pragma solidity ^0.8.0;

contract VegasStaking is Ownable{
    uint public APR=1000000;
    uint constant DENOMINATOR=1000000;
    IBEP20 public token;
    ITokenPool pool;

    constructor(address _pool){

        pool=ITokenPool(_pool);
        token=IBEP20(pool.token());
    }
    uint LastProfitTimestamp;
    mapping(address=>uint) public stakedTokens;
    mapping(address=>uint) public redeemedShares;
    uint ProfitPerShare;
    uint public TotalShares;
    uint constant DividentMagnifier=10**32;

    event OnDeposit(address account, uint amount);
    event OnCompound(address account, uint amount);
    event OnWithdrawDividents(address account, uint amount);
    event OnWithdraw(address account, uint amount);
    event OnSetAPR(uint newAPR);

    bool locked;
    modifier lock{
        require(!locked);
        locked=true;
        _;
        locked=false;
    }

    function setAPR(uint _APR) external onlyOwner{
        APR=_APR;
        emit OnSetAPR(_APR);
    }
    function _addRewards() private{
        if(TotalShares==0){
            LastProfitTimestamp=block.timestamp;
            return;
        } 

        uint amount=_getPoolWithdrawAmount();
        if(amount==0) return;
        LastProfitTimestamp=block.timestamp;
        amount=pool.withdrawToken(amount);
        ProfitPerShare+=(amount*DividentMagnifier)/TotalShares;
    }

    function Deposit(uint amount) external lock{
        require(amount>0,"need to deposit tokens");
        _addRewards();
        _compoundDividents(msg.sender);
        token.transferFrom(msg.sender,address(this),amount);
        uint totalStaked=stakedTokens[msg.sender]+amount;
        stakedTokens[msg.sender]=totalStaked;
        TotalShares+=amount;
        redeemedShares[msg.sender]=totalStaked*ProfitPerShare;
        emit OnDeposit(msg.sender,amount);
    } 

    function WithdrawAll() external{
        WithdrawDeposit(stakedTokens[msg.sender]);
    }
    function WithdrawDeposit(uint amount)public lock{
        _addRewards();
        _withdrawDividents(msg.sender);
        uint staked=stakedTokens[msg.sender];
        require(staked>=amount,"not enough token staked");
        staked-=amount;
        TotalShares-=amount;
        stakedTokens[msg.sender]=staked;
        redeemedShares[msg.sender]=staked*ProfitPerShare;
        token.transfer(msg.sender,amount);
        emit OnWithdraw(msg.sender,amount);
    }

    function EmergencyWithdraw(uint amount) external lock{
        uint staked=stakedTokens[msg.sender];
        require(staked>=amount,"not enough token staked");
        staked-=amount;
        TotalShares-=amount;
        stakedTokens[msg.sender]=staked;
        redeemedShares[msg.sender]=staked*ProfitPerShare;
        token.transfer(msg.sender,amount);
        emit OnWithdraw(msg.sender,amount);
    }

    function WithdrawDividents() external lock{
        _addRewards();
        _withdrawDividents(msg.sender);
    }
    function Compound() external lock{
        _addRewards();
        _compoundDividents(msg.sender);
    }
    function _getPoolWithdrawAmount() private view returns(uint dividents){
        uint TimePassed=block.timestamp-LastProfitTimestamp;
        if(TimePassed==0)return 0;
        dividents= (TotalShares*APR*TimePassed)/(DENOMINATOR*356 days);
    }
    function getDividents(address account) external view returns(uint dividents){
        uint amount=_getPoolWithdrawAmount();
        uint poolBalance=token.balanceOf(address(pool));
        if(amount>poolBalance) amount=poolBalance;
        uint pps=ProfitPerShare+(amount*DividentMagnifier)/TotalShares;
        uint staked=stakedTokens[account];
        uint redeemed=redeemedShares[account];
        uint fullPayout=staked*pps;
        if(fullPayout<=redeemed) return 0;
        dividents=(fullPayout-redeemed)/DividentMagnifier;
    }
    function _getDividents(address account) private view returns(uint dividents){
        uint staked=stakedTokens[account];
        uint redeemed=redeemedShares[account];

        uint fullPayout=staked*ProfitPerShare;
        if(fullPayout<=redeemed) return 0;
        dividents=(fullPayout-redeemed)/DividentMagnifier;
    }

    function _withdrawDividents(address account) private{
        uint amount=_getDividents(account);
        if(amount==0)return;
        redeemedShares[account]=stakedTokens[account]*ProfitPerShare;
        token.transfer(account,amount);
        emit OnWithdrawDividents(account,amount);
    }
    function _compoundDividents(address account) private{
        uint amount=_getDividents(account);
        if(amount==0)return;
        stakedTokens[account]+=amount;
        TotalShares+=amount;
        redeemedShares[account]=stakedTokens[account]*ProfitPerShare;
        emit OnCompound(account,amount);
    }


}
