contract RIPHarambe is Context, IBEP20, Ownable {
  using SafeMath for uint256;
  using Address for address;

  mapping (address => uint256) private _balances;
  mapping (address => mapping (address => uint256)) private _allowances;
  mapping (address => uint256) private _voted;
  uint256 private _currentVote;

  uint256 private _totalSupply;
  uint256 public liquiditySupply;
  uint256 private _liquidityLimit;
  uint256 public  harambeSupply;
  uint256 public  specialSupply;
  uint256 private _burnSupply;
  uint256 private _winners;
  uint256 private _specialBuys;
  uint256 private _specialFreq;
  uint256 public specialPayout;
  uint8   private _decimals;
  string  private _symbol;
  string  private _name;
  IUniswapV2Router02 public immutable uniswapV2Router;
  address public immutable uniswapV2Pair;
  bool inSwapAndLiquify;
  mapping (address => bool) private _isExcludedFromFee;

  address public proposedHarambeWallet0 = address(0);
  address public proposedHarambeWallet1 = owner();
  address public proposedHarambeWallet2 = owner();
  uint256 public votesNeither;
  uint256 public votesWallet1;
  uint256 public votesWallet2;
  uint256 public voteExpiration;
  uint256 private B;
  bool private _initialized;
  bool private _specialActive;
  bool public swapAndLiquifyEnabled;
  uint256 public _maxTxAmount;

  modifier lockTheSwap {
      inSwapAndLiquify = true;
      _;
      inSwapAndLiquify = false;
  }

  constructor() {
    _name = "RIPHarambe";
    _symbol = "RIPH";
    _decimals = 9;
    B = 10**9 * 10**9;
    _initialized = false;

    _totalSupply = 1000*B;
    _burnSupply = 528*B;
    harambeSupply = 50*B;
    specialSupply = 51*B;
    specialPayout = specialSupply.div(17);
    _liquidityLimit = 1*B;
    
    //liquiditySupply = 10 * 10**6 * 10**9;
    //_liquidityLimit = 10 * 10**6 * 10**9;
    //IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0xD99D1c33F9fC3444f8101754aBC46c52416550D1);
    
    IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x05fF2B0DB69458A0750badebc4f9e13aDd608C7F);
     // Create a uniswap pair for this new token
         
    uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
        .createPair(address(this), _uniswapV2Router.WETH());
            
    // set the rest of the contract variables
    uniswapV2Router = _uniswapV2Router;

    //exclude owner and this contract from fee
    _isExcludedFromFee[_msgSender()] = true;

    _balances[_msgSender()] = _totalSupply;
    emit Transfer(address(0), _msgSender(), _totalSupply);
  }

    receive() external payable {}

    function initialize() public onlyOwner() {
        require(!_initialized, "Already initialized");
        _transfer(msg.sender, address(0xdead), _burnSupply);
        _transfer(msg.sender, address(this), harambeSupply.add(specialSupply).add(liquiditySupply));
        _isExcludedFromFee[uniswapV2Pair] = true;
        _isExcludedFromFee[address(this)] = true;
        _initialized = true;
        //swapAndLiquifyEnabled = true;
  }
  
  function _resetVotes() private {
    votesNeither = 0;
    votesWallet1 = 0;
    votesWallet2 = 0;
    _currentVote = _currentVote.add(1);
  }

  function propose(address harambeWallet1, address harambeWallet2, uint256 voteTime) public onlyOwner() {
    require(!_isMay(), "IERC20: Cannot start vote for Harambe in May");
    require((voteTime >= (7 * 86400) && voteTime < (30 * 86400)), "IERC20: Voting period must be between 7 and 30 days");
    _resetVotes();
    proposedHarambeWallet1 = harambeWallet1;
    proposedHarambeWallet2 = harambeWallet2;
    voteExpiration = block.timestamp.add(voteTime);
  }

  function vote(uint256 selection) public {
    require(selection < 3, "IERC20: Valid selections: 0, 1, 2");
    require(harambeSupply > 0, "IERC20: No Harambe FUND exists");
    require(_balances[_msgSender()] > 0, "IERC20: Must own token to vote");
    require(_voted[_msgSender()] != _currentVote, "IERC20: Already voted this round");

    if (selection == 0) {
      votesNeither = votesNeither.add(1);
    } else if (selection == 1) {
      votesWallet1 = votesWallet1.add(1);
    } else if (selection == 2) {
      votesWallet2 = votesWallet2.add(1);
    }

    _voted[_msgSender()] = _currentVote;
    
    if (block.timestamp > voteExpiration) {
      if (votesNeither >= votesWallet1 && votesNeither >= votesWallet2) {
        _resetVotes();
      } else if (votesWallet1 == votesWallet2) {
        voteExpiration = block.timestamp.add(1 * 86400);
      } else if (votesWallet1 > votesWallet2) {
        _isExcludedFromFee[proposedHarambeWallet1] = true;
        _transfer(address(this), proposedHarambeWallet1, harambeSupply);
        harambeSupply = 0;
        _resetVotes();
      } else {
        _isExcludedFromFee[proposedHarambeWallet2] = true;
        _transfer(address(this), proposedHarambeWallet2, harambeSupply);
        harambeSupply = 0;
        _resetVotes();
      }
    }
  }

  function _isMay() private view returns(bool) {
    if (DateTime.getMonth(block.timestamp) == 5) {
      return true;
    } else {
      return false;
    }
  }

  function _isHarambeDay() private view returns(bool) {
    if (_isMay() && DateTime.getDay(block.timestamp) == 28) {
      return true;
    } else {
      return false;
    }
  }

  /**
   * @dev Returns the bep token owner.
   */
  function getOwner() external view returns (address) {
    return owner();
  }

  /**
   * @dev Returns the token decimals.
   */
  function decimals() external view returns (uint8) {
    return _decimals;
  }

  /**
   * @dev Returns the token symbol.
   */
  function symbol() external view returns (string memory) {
    return _symbol;
  }

  /**
  * @dev Returns the token name.
  */
  function name() external view returns (string memory) {
    return _name;
  }

  /**
   * @dev See {BEP20-totalSupply}.
   */
  function totalSupply() external view override returns (uint256) {
    return _totalSupply;
  }

  /**
   * @dev See {BEP20-balanceOf}.
   */
  function balanceOf(address account) external view override returns (uint256) {
    return _balances[account];
  }

  /**
   * @dev See {BEP20-transfer}.
   *
   * Requirements:
   *
   * - `recipient` cannot be the zero address.
   * - the caller must have a balance of at least `amount`.
   */
  function transfer(address recipient, uint256 amount) external override returns (bool) {
    _transfer(_msgSender(), recipient, amount);
    return true;
  }

  /**
   * @dev See {BEP20-allowance}.
   */
  function allowance(address owner, address spender) external view override returns (uint256) {
    return _allowances[owner][spender];
  }

  /**
   * @dev See {BEP20-approve}.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   */
  function approve(address spender, uint256 amount) external override returns (bool) {
    _approve(_msgSender(), spender, amount);
    return true;
  }

  /**
   * @dev See {BEP20-transferFrom}.
   *
   * Emits an {Approval} event indicating the updated allowance. This is not
   * required by the EIP. See the note at the beginning of {BEP20};
   *
   * Requirements:
   * - `sender` and `recipient` cannot be the zero address.
   * - `sender` must have a balance of at least `amount`.
   * - the caller must have allowance for `sender`'s tokens of at least
   * `amount`.
   */
  function transferFrom(address sender, address recipient, uint256 amount) external override returns (bool) {
    _transfer(sender, recipient, amount);
    _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "BEP20: transfer amount exceeds allowance"));
    return true;
  }

  /**
   * @dev Atomically increases the allowance granted to `spender` by the caller.
   *
   * This is an alternative to {approve} that can be used as a mitigation for
   * problems described in {BEP20-approve}.
   *
   * Emits an {Approval} event indicating the updated allowance.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   */
  function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
    _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
    return true;
  }

  /**
   * @dev Atomically decreases the allowance granted to `spender` by the caller.
   *
   * This is an alternative to {approve} that can be used as a mitigation for
   * problems described in {BEP20-approve}.
   *
   * Emits an {Approval} event indicating the updated allowance.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   * - `spender` must have allowance for the caller of at least
   * `subtractedValue`.
   */
  function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
    _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "BEP20: decreased allowance below zero"));
    return true;
  }

  /**
   * @dev Moves tokens `amount` from `sender` to `recipient`.
   *
   * This is internal function is equivalent to {transfer}, and can be used to
   * e.g. implement automatic token fees, slashing mechanisms, etc.
   *
   * Emits a {Transfer} event.
   *
   * Requirements:
   *
   * - `sender` cannot be the zero address.
   * - `recipient` cannot be the zero address.
   * - `sender` must have a balance of at least `amount`.
   */
   
  function _approve(address owner, address spender, uint256 amount) private {
    require(owner != address(0), "ERC20: approve from the zero address");
    require(spender != address(0), "ERC20: approve to the zero address");

    _allowances[owner][spender] = amount;
    emit Approval(owner, spender, amount);
  }

  function _updateSpecialFreq() internal {
      _specialFreq = 150 + (block.timestamp % 40);
  }
  
  function _isSpecialActive() internal returns (bool) {
      if (_isHarambeDay()) {
          _specialActive = true;
      }
      
      return _specialActive;
  }

  function _transfer(address sender, address recipient, uint256 amount) internal {
    require(sender != address(0), "BEP20: transfer from the zero address");
    require(recipient != address(0), "BEP20: transfer to the zero address");
    require(amount > 0, "Transfer amount must be greater than zero");
    require(_balances[sender] >= amount, "Insufficient funds");
    
    if(sender != uniswapV2Pair && sender != address(this))
        require(amount <= _maxTxAmount, "Transfer amount exceeds the maxTxAmount.");
            
    if (liquiditySupply >= _liquidityLimit &&
        !inSwapAndLiquify &&
        sender != uniswapV2Pair &&
        swapAndLiquifyEnabled) {
      swapAndLiquify(liquiditySupply);
      liquiditySupply = 0;
    }
    
    uint256 liq = 0;
    uint256 harambe = 0;
    
    if (!_isExcludedFromFee[sender] && !inSwapAndLiquify) {
      (liq, harambe) = _takeFees(amount);
      _balances[address(this)] = _balances[address(this)].add(harambe).add(liq);
      emit Transfer(sender, address(this), liq.add(harambe));
    }
    
    if (_initialized && !inSwapAndLiquify && sender == uniswapV2Pair) {
      if (_isSpecialActive() && specialSupply >= specialPayout) {
        _specialBuys = _specialBuys.add(1);
        if (_specialBuys % _specialFreq == 0) {
          _balances[recipient] = _balances[recipient].add(specialPayout);
          _balances[address(this)] = _balances[address(this)].sub(specialPayout);
          specialSupply = specialSupply.sub(specialPayout);
          _updateSpecialFreq();
          _specialBuys = 0;
          emit Transfer(address(this), recipient, specialPayout);
        }
      }
    }

    _balances[sender] = _balances[sender].sub(amount);
    _balances[recipient] = _balances[recipient].add(amount).sub(liq).sub(harambe);
    emit Transfer(sender, recipient, amount.sub(liq).sub(harambe));


  }
  
    event SwapAndLiquifyEnabledUpdated(bool enabled);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );

    function setSwapAndLiquifyEnabled(bool _enabled) public onlyOwner {
        swapAndLiquifyEnabled = _enabled;
        emit SwapAndLiquifyEnabledUpdated(_enabled);
    }
    
    /*
    function testLP(uint256 amount) public onlyOwner() {
      bool orig;
      _balances[_msgSender()] = _balances[_msgSender()].sub(amount);
      _balances[address(this)] = _balances[address(this)].add(amount);
      liquiditySupply = liquiditySupply.add(amount);
      emit Transfer(_msgSender(), address(this), amount);
      orig = swapAndLiquifyEnabled;
      swapAndLiquifyEnabled = true;
      swapAndLiquify(liquiditySupply);
      swapAndLiquifyEnabled = orig;
      liquiditySupply = 0;
    }
    */
    
    function setMaxTxPercent(uint256 maxTxPercent) external onlyOwner() {
        _maxTxAmount = _totalSupply.mul(maxTxPercent).div(
            10**2
        );
    }

    function swapAndLiquify(uint256 contractTokenBalance) private lockTheSwap {
        // split the contract balance into halves
        uint256 half = contractTokenBalance.div(2);
        uint256 otherHalf = contractTokenBalance.sub(half);

        // capture the contract's current ETH balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract
        uint256 initialBalance = address(this).balance;

        // swap tokens for ETH1
        swapTokensForEth(half); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);

        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            //owner(),
            address(this),
            block.timestamp
        );
    }

  function _takeFees(uint256 amount) private returns (uint256, uint256) {
    uint256 harambe = 0;
    // 1% to LP
    uint256 liquidity = amount.mul(1).div(100);
    liquiditySupply = liquiditySupply.add(liquidity);

    if (_isMay()) {
      // 5.28% to Harambe Fund
      harambe = amount.mul(528).div(10**4);
      harambeSupply = harambeSupply.add(harambe);
    }

    return (liquidity, harambe);
  }
}
