    "contracts/POGBox.sol": {
      "content": "//SPDX-License-Identifier: Unlicense
pragma solidity 0.8.10;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC1155/presets/ERC1155PresetMinterPauser.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "./Readable.sol";

contract POGBox is Ownable, Readable {
    using SafeERC20 for IERC20;
    using ExtraMath for *;
    using Math for *;
    using EnumerableSet for EnumerableSet.UintSet;

    uint public constant POG = 1e18;
    uint public constant MAX_LOCK_PERIOD = 365 days;
    IERC20 public immutable POGToken;
    ERC1155PresetMinterPauser public immutable POGNFT;
    address public immutable TREASURY;

    struct Stake {
        uint192 item;
        uint32 amount;
        uint32 unlockDate;
    }

    struct User {
        EnumerableSet.UintSet stakeIds;
        mapping(uint => Stake) stakes;
    }

    struct Item {
        bool active;
        uint32 lockPeriod;
        uint88 stakePrice;
        uint88 openPrice;
    }

    struct DB {
        mapping(address => User) users;
        mapping(uint => Item) items;
        uint[] allItems;
        uint stakesCounter;
    }

    DB internal db;

    event Staked(address user, uint item, uint amount, uint payment, uint stakeId);
    event Claimed(address user, uint item, uint amount, uint payment, uint stakeId);
    event Open(address user, address receiver, uint item, uint amount, uint payment);
    event AddItem(uint item, uint lockPeriod, uint stakePrice, uint openPrice);
    event DeactivateItem(uint item);

    constructor(address _owner, address _treasury, IERC20 _pog, ERC1155PresetMinterPauser _pognft) {
        transferOwnership(_owner);
        TREASURY = _treasury;
        POGToken = _pog;
        POGNFT = _pognft;
    }

    function stake(uint _item, uint _amount, address _openTo) public {
        Item memory item = db.items[_item];
        require(item.active, 'POGBox: inactive item');
        POGNFT.mint(_msgSender(), _item, _amount, '');
        uint payment = item.stakePrice * _amount;
        POGToken.safeTransferFrom(_msgSender(), address(this), payment);
        User storage user = db.users[_msgSender()];
        uint stakeId = db.stakesCounter++;
        user.stakes[stakeId] =
            Stake(_item.toUInt192(), _amount.toUInt32(), (block.timestamp + item.lockPeriod).toUInt32());
        user.stakeIds.add(stakeId);
        emit Staked(_msgSender(), _item, _amount, payment, stakeId);
        _open(_msgSender(), _openTo, _item, _amount, item.openPrice);
    }

    function getStakeIds(address _user) external view returns(uint[] memory) {
        return db.users[_user].stakeIds.values();
    }

    function getStakes(address _user) external view returns(Stake[] memory) {
        uint[] memory stakeIds = db.users[_user].stakeIds.values();
        Stake[] memory stakes = new Stake[](stakeIds.length);
        for (uint i = 0; i < stakeIds.length; i++) {
            stakes[i] = db.users[_user].stakes[stakeIds[i]];
        }
        return stakes;
    }

    function claim(uint[] calldata _stakes, address _openTo) public {
        User storage user = db.users[_msgSender()];
        for (uint i = 0; i < _stakes.length; i++) {
            Stake memory _stake = user.stakes[_stakes[i]];
            require(_stake.unlockDate > 0, 'POGBox: invalid stake id');
            require(reached(_stake.unlockDate), 'POGBox: not unlocked yet');

            delete user.stakes[_stakes[i]];
            user.stakeIds.remove(_stakes[i]);

            Item memory item = db.items[_stake.item];
            uint payment = item.stakePrice * _stake.amount;
            POGToken.safeTransfer(_msgSender(), payment);
            uint extraItems = 0;
            if (item.active) {
                extraItems = (since(_stake.unlockDate) / item.lockPeriod) * _stake.amount;
                if (extraItems > 0) {
                    POGNFT.mint(_msgSender(), _stake.item, extraItems, '');
                    _open(_msgSender(), _openTo, _stake.item, extraItems, item.openPrice);
                }
            }
            emit Claimed(_msgSender(), _stake.item, extraItems, payment, _stakes[i]);
        }
    }

    function open(uint _item, uint _amount, address _to) external {
        require(db.items[_item].lockPeriod > 0, 'POGBox: invalid item');
        require(_open(_msgSender(), _to, _item, _amount, db.items[_item].openPrice),
            'POGBox: zero to address');
    }

    function _open(address _from, address _to, uint _item, uint _amount, uint _price) internal returns(bool) {
        if (_to != address(0)) {
            uint payment = _amount * _price;
            if (payment > 0) {
                POGToken.safeTransferFrom(_from, TREASURY, payment);
            }
            POGNFT.burn(_from, _item, _amount);
            emit Open(_from, _to, _item, _amount, payment);
            return true;
        }
        return false;
    }

    function addItem(uint _item, uint _lockPeriod, uint _stakePrice, uint _openPrice) external onlyOwner() {
        require(db.items[_item].lockPeriod == 0, 'POGBox: item already added');
        require(_lockPeriod > 0 && _lockPeriod <= MAX_LOCK_PERIOD, 'POGBox: invalid lock period');
        require(_stakePrice > 0, 'POGBox: invalid stake price');
        db.items[_item] = Item(true, _lockPeriod.toUInt32(), _stakePrice.toUInt88(), _openPrice.toUInt88());
        db.allItems.push(_item);
        emit AddItem(_item, _lockPeriod, _stakePrice, _openPrice);
    }

    function deactivateItem(uint _item) external onlyOwner() {
        require(db.items[_item].active, 'POGBox: item is not active');
        db.items[_item].active = false;
        emit DeactivateItem(_item);
    }

    function getItems() external view returns(uint[] memory) {
        return db.allItems;
    }

    function getItem(uint _item) external view returns(Item memory) {
        Item memory item = db.items[_item];
        require(item.lockPeriod > 0, 'POGBox: invalid item');
        return item;
    }

    function recoverERC20(IERC20 _token) external onlyOwner() {
        _token.safeTransfer(TREASURY, _token.balanceOf(address(this)));
    }

    function recoverERC1155(IERC1155 _token, uint _item) external onlyOwner() {
        _token.safeTransferFrom(address(this), TREASURY, _item, _token.balanceOf(address(this), _item), '');
    }
}
"
