      "content": "// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.20;

import "openzeppelin-contracts/utils/math/SafeMath.sol";
import "openzeppelin-contracts/token/ERC20/ERC20.sol";
import "openzeppelin-contracts/utils/Context.sol";
import "openzeppelin-contracts/access/Ownable.sol";
import "@uniswap/v2-core/interfaces/IUniswapV2Factory.sol";
import "@uniswap/v2-core/interfaces/IUniswapV2Pair.sol";
import "@uniswap/v2-periphery/interfaces/IUniswapV2Router02.sol";

contract TaxedToken is ERC20, Ownable {
    using SafeMath for uint256;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;

    bool private swapping;

    uint256 public swapTokensAtAmount;

    uint256 public centiSellTax;
    uint256 public centiBuyTax;

    address public marketingWallet1;
    address public marketingWallet2;
    address public liquidityWallet;
    address public buybackWallet;

    uint256 public maxTxAmount;
    uint256 public maxWalletAmount;

    // exlcude from fees and max transaction amount
    mapping(address => bool) private _isExcludedFromFees;

    // store addresses that a automatic market maker pairs. Any transfer *to* these addresses
    // could be subject to a maximum transfer amount
    mapping(address => bool) public automatedMarketMakerPairs;

    event UpdateUniswapV2Router(address indexed newAddress, address indexed oldAddress);

    event ExcludeFromFees(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);

    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);

    struct Parameters {
        uint256 centiBuyTax;
        uint256 centiSellTax;
        address marketingWallet1;
        address marketingWallet2;
        address buybackWallet;
        uint256 maxTxPercent;
        uint256 maxWalletPercent;
    }

    constructor(
        string memory name_,
        string memory symbol_,
        uint256 supply_,
        Parameters memory parameters,
        address uniswapV2Router_
    ) payable ERC20(name_, symbol_) {
        marketingWallet1 = parameters.marketingWallet1;
        marketingWallet2 = parameters.marketingWallet2;
        liquidityWallet = owner();
        centiBuyTax = parameters.centiBuyTax;
        centiSellTax = parameters.centiSellTax;

        uniswapV2Router = IUniswapV2Router02(uniswapV2Router_);

        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this), uniswapV2Router.WETH());

        _setAutomatedMarketMakerPair(uniswapV2Pair, true);

        // exclude from paying fees or having max transaction amount
        excludeFromFees(owner(), true);
        excludeFromFees(marketingWallet1, true);
        excludeFromFees(marketingWallet2, true);
        excludeFromFees(buybackWallet, true);
        excludeFromFees(address(this), true);
        excludeFromFees(address(uniswapV2Router), true);

        swapTokensAtAmount = (supply_.div(5000) + 1) * (10 ** decimals());

        maxTxAmount = parameters.maxTxPercent * supply_ * (10 ** decimals()).div(10000);
        maxWalletAmount = parameters.maxWalletPercent * supply_ * (10 ** decimals()).div(10000);

        /*
            _mint is an internal function in ERC20.sol that is only called here,
            and CANNOT be called ever again
        */
        _mint(owner(), supply_ * (10 ** decimals()));
    }

    receive() external payable {}

    function updateUniswapV2Router(address newAddress) public onlyOwner {
        require(newAddress != address(uniswapV2Router), "The router already has that address");
        emit UpdateUniswapV2Router(newAddress, address(uniswapV2Router));
        uniswapV2Router = IUniswapV2Router02(newAddress);
        address _uniswapV2Pair =
            IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this), uniswapV2Router.WETH());
        uniswapV2Pair = _uniswapV2Pair;
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        _isExcludedFromFees[account] = excluded;

        emit ExcludeFromFees(account, excluded);
    }

    function excludeMultipleAccountsFromFees(address[] memory accounts, bool excluded) public onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFees[accounts[i]] = excluded;
        }

        emit ExcludeMultipleAccountsFromFees(accounts, excluded);
    }

    function setMarketingWallet1(address payable wallet) external onlyOwner {
        marketingWallet1 = wallet;
    }

    function setMarketingWallet2(address payable wallet) external onlyOwner {
        marketingWallet2 = wallet;
    }

    function setBuybackWallet(address payable wallet) external onlyOwner {
        buybackWallet = wallet;
    }

    function setLiquidityWallet(address wallet) external onlyOwner {
        liquidityWallet = wallet;
    }

    function setSwapThreshold(uint256 amount) external onlyOwner {
        swapTokensAtAmount = amount;
    }

    function setAutomatedMarketMakerPair(address pair, bool value) public onlyOwner {
        require(pair != uniswapV2Pair, "The PanRewardSwap pair cannot be removed from automatedMarketMakerPairs");

        _setAutomatedMarketMakerPair(pair, value);
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        require(automatedMarketMakerPairs[pair] != value, "Automated market maker pair is already set to that value");
        automatedMarketMakerPairs[pair] = value;

        emit SetAutomatedMarketMakerPair(pair, value);
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }

    function _transfer(address from, address to, uint256 amount) internal override {
        if (
            (to == address(0) || to == address(0xdead)) || (_isExcludedFromFees[from] || _isExcludedFromFees[to])
                || amount == 0
        ) {
            super._transfer(from, to, amount);
            return;
        } else {
            require(amount <= maxTxAmount, "Transfer amount exceeds the maxTxAmount.");

            if (to != uniswapV2Pair) {
                uint256 contractBalanceRecepient = balanceOf(to);
                require(contractBalanceRecepient + amount <= maxWalletAmount, "Exceeds maximum wallet amount");
            }
        }

        // is the token balance of this contract address over the min number of
        // tokens that we need to initiate a swap + liquidity lock?
        // also, don't get caught in a circular liquidity event.
        // also, don't swap & liquify if sender is uniswap pair.
        uint256 contractTokenBalance = balanceOf(address(this));

        bool canSwap = contractTokenBalance >= swapTokensAtAmount;

        if (canSwap && !swapping && !automatedMarketMakerPairs[from]) {
            swapping = true;

            uint256 marketing1Tokens = (contractTokenBalance * 1) / 5;
            uint256 marketing2Tokens = (contractTokenBalance * 1) / 5;
            uint256 buybackTokens = (contractTokenBalance * 2) / 5;
            uint256 liquidityTokens = (contractTokenBalance * 1) / 5;

            if (marketing1Tokens > 0) {
                swapTokensForEth(marketing1Tokens, marketingWallet1);
                swapTokensForEth(marketing2Tokens, marketingWallet2);
                swapTokensForEth(buybackTokens, buybackWallet);
                swapAndLiquify(liquidityTokens);
            }

            swapping = false;
        }

        bool takeFee = !swapping;

        // if any account belongs to _isExcludedFromFee account then remove the fee
        if (_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }

        if (takeFee) {
            uint256 fees = amount.mul(centiBuyTax).div(10000);
            if (automatedMarketMakerPairs[to]) {
                fees = amount.mul(centiSellTax).div(10000);
            }
            amount = amount.sub(fees);

            super._transfer(from, address(this), fees);
        }

        super._transfer(from, to, amount);
    }

    function swapAndLiquify(uint256 tokens) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), 10 ** 9 * 10 ** 18);

        uint256 half = tokens.div(2);
        uint256 otherHalf = tokens.sub(half);

        uint256 initialBalance = address(this).balance;

        swapTokensForEth(half, address(this));

        uint256 newBalance = address(this).balance.sub(initialBalance);

        _approve(address(this), address(uniswapV2Router), 10 ** 9 * 10 ** 18);

        uniswapV2Router.addLiquidityETH{value: newBalance}(
            address(this), otherHalf, 0, 0, liquidityWallet, block.timestamp
        );
    }

    function swapTokensForEth(uint256 tokenAmount, address receiver) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            receiver,
            block.timestamp
        );
    }

    function setSellTax(uint256 _wholeNumber, uint256 _firstNumberAfterDecimal, uint256 _secondNumberAfterDecimal)
        public
        onlyOwner
    {
        require(_wholeNumber <= 50 && _firstNumberAfterDecimal <= 9 && _secondNumberAfterDecimal <= 9);
        centiSellTax = _wholeNumber * 100 + _firstNumberAfterDecimal * 10 + _secondNumberAfterDecimal;
    }

    function setBuyTax(uint256 _wholeNumber, uint256 _firstNumberAfterDecimal, uint256 _secondNumberAfterDecimal)
        public
        onlyOwner
    {
        require(_wholeNumber <= 50 && _firstNumberAfterDecimal <= 9 && _secondNumberAfterDecimal <= 9);
        centiBuyTax = _wholeNumber * 100 + _firstNumberAfterDecimal * 10 + _secondNumberAfterDecimal;
    }

    function getAllTaxes() external onlyOwner {
        swapTokensForEth(balanceOf(address(this)), marketingWallet1);
        swapTokensForEth(balanceOf(address(this)), marketingWallet2);
    }

    function setMaxTx(uint256 _wholeNumber, uint256 _firstNumberAfterDecimal, uint256 _secondNumberAfterDecimal)
        external
        onlyOwner
    {
        require(_wholeNumber < 100 && _firstNumberAfterDecimal <= 9 && _secondNumberAfterDecimal <= 9);
        maxTxAmount =
            (_wholeNumber * 100 + _firstNumberAfterDecimal * 10 + _secondNumberAfterDecimal) * totalSupply().div(10000);
    }

    function setMaxWallet(uint256 _wholeNumber, uint256 _firstNumberAfterDecimal, uint256 _secondNumberAfterDecimal)
        external
        onlyOwner
    {
        require(_wholeNumber < 100 && _firstNumberAfterDecimal <= 9 && _secondNumberAfterDecimal <= 9);
        maxWalletAmount =
            (_wholeNumber * 100 + _firstNumberAfterDecimal * 10 + _secondNumberAfterDecimal) * totalSupply().div(10000);
    }
}
