        dividendTracker = new ConfitDividendTracker();

        liquidityWallet = owner();
        
        marketingWallet = payable(0x58D15514846E0D6f4e9BcFFe389693Bb2579fa7A);
        _devWallet = payable(0x906e1a9aA8798C7F163e025E416fed9092501333);

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        //0x9Ac64Cc6e4415144C455BD8E4837Fea55603e5c3 Testnet
        //0x10ED43C718714eb63d5aA57B78B54704E256024E Mainnet
        //0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D Ropsten
