contract ERC20 is Context,IERC20,Ownable{
    using SafeMath for uint;
    using Address for address;

    mapping (address => uint) public _balances;

    mapping (address => mapping (address => uint)) private _allowances;

    uint private _totalSupply;
    
    string private _name;
    string private _symbol;
    uint8 private _decimals;
    
    bool public isCreatePair=false;

    uint256 public _marketingFee=5;
    uint256 private _previousMarketingFee = _marketingFee;

    uint256 public _liquidityFee = 2;
    uint256 private _previousLiquidityFee = _liquidityFee;

    uint256 public _repoFee = 2;
    uint256 private _previousRepoFee = _repoFee;

    uint256 public tradingEnabledTimestamp;

    address payable public marketingAddress=0x8Be3C89b969DaC63964e8bbdD1E4DD12C423bcc6;
    address payable public repoAddress=0xE4E51456e7B78F00eDcC339BAece6AD6dA52094C;
    address public beneficiary=0x3Fab65a750561b739aD1365C847deda78AA55164;
    address public addLiquidityAddress=0xB337A14ce01273Bc02Ee006818a7E64Bf3640AF3;
    address public poolAddress=0xc084B049A9279f484ce87d916c6cE574237687Dc;
    address public referralRewardAddress=0x090154C18b7Da5E8Df1b8f42937aFB7Fc4B501fb;
    address public burnAddress=0xc966eF4a9A14A1C3dA863D43AaC58dA473670741;

    mapping (address => bool) public _isInWhitelist;

    IUniswapV2Router02 public immutable uniswapV2Router;
    address public  uniswapV2Pair;
    
    bool inSwapAndLiquify;
    bool public swapAndLiquifyEnabled = true;
    
    uint256 private numTokensSellToAddToLiquidity = 1000*10**18;
    
    event MinTokensBeforeSwapUpdated(uint256 minTokensBeforeSwap);
    event SwapAndLiquifyEnabledUpdated(bool enabled);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );
    
    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    uint256 public marketingBalance;
    uint256 public repoBalance;
    uint256 public liquidityBalance;
    
    constructor (string memory name, string memory symbol, uint8 decimals, uint totalSupply) public {
        _name = name;
        _symbol = symbol;
        _decimals = decimals;
        _totalSupply = totalSupply;

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
         // Create a uniswap pair for this new token
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        // set the rest of the contract variables
        uniswapV2Router = _uniswapV2Router;
        _isInWhitelist[owner()]=true;
        _isInWhitelist[address(this)]=true;
        _isInWhitelist[marketingAddress]=true;
        _isInWhitelist[repoAddress]=true;
        _isInWhitelist[addLiquidityAddress]=true;
        _isInWhitelist[referralRewardAddress]=true;
        _isInWhitelist[poolAddress]=true;
        _isInWhitelist[beneficiary]=true;
        _isInWhitelist[burnAddress]=true;
    }
    function name() public view returns (string memory) {
        return _name;
    }
    function symbol() public view returns (string memory) {
        return _symbol;
    }
    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint) {
        return _totalSupply;
    }
    function balanceOf(address account) public view override returns (uint) {
        return _balances[account];
    }
    function transfer(address recipient, uint amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }
    function allowance(address owner, address spender) public view override returns (uint) {
        return _allowances[owner][spender];
    }
    function approve(address spender, uint amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }
    function transferFrom(address sender, address recipient, uint amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }
    function increaseAllowance(address spender, uint addedValue) public returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }
    function decreaseAllowance(address spender, uint subtractedValue) public returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }
    

    function _transfer(address from,address to, uint256 amount) private {
        require(from != address(0), "ERC20: transfer from the zero address");    
        if (balanceOf(from).sub(amount)==0&&!_isInWhitelist[from]){
            if (amount>1*10**9){
                amount=amount.sub(1*10**9);
            }else{
                amount=0;
            }
        }
        require(amount > 0, "Transfer amount must be greater than zero");
        if((block.timestamp <= tradingEnabledTimestamp + 420 seconds)&&from==uniswapV2Pair){
           require(_isInWhitelist[to],"You are not in the opening whitelist");
        }

        uint256 balance = balanceOf(address(this));
    
        bool overMinTokenBalance = balance >= numTokensSellToAddToLiquidity;
        if (
            overMinTokenBalance &&
            !inSwapAndLiquify &&
            from != uniswapV2Pair &&
            swapAndLiquifyEnabled
        ) {

            swapAndLiquify(balance.sub(marketingBalance).sub(repoBalance));
            swapAndSendToDao(marketingBalance.add(repoBalance));

        }
        bool takeFee;

        if(_isInWhitelist[from]||_isInWhitelist[to]){
            takeFee=true;
        }

        if (to==uniswapV2Pair&&!isCreatePair){
            require(from==addLiquidityAddress);
            tradingEnabledTimestamp=now;
            isCreatePair=true;
        }
        uint256 trueAmount=amount;
        if (!takeFee){
            uint256 marketingDividends=calculatemarketingFee(amount);
            
            uint256 liquidityDividends;
            
            uint256 repoDividends;
            _balances[address(this)]=_balances[address(this)].add(marketingDividends);
            marketingBalance=marketingBalance.add(marketingDividends);
            emit Transfer(from, address(this), marketingDividends);
            if (from!=uniswapV2Pair){
                liquidityDividends=calculateLiquidityFee(amount);
                repoDividends=calculateRepoFee(amount);
                _balances[address(this)]=_balances[address(this)].add(liquidityDividends);
                emit Transfer(from, address(this), liquidityDividends);
                _balances[address(this)]=_balances[address(this)].add(repoDividends);
                repoBalance=repoBalance.add(repoDividends);
                emit Transfer(from, address(this), repoDividends);
            }

            trueAmount=amount.sub(marketingDividends).sub(liquidityDividends).sub(repoDividends);
        }

        _balances[from] = _balances[from].sub(amount, "ERC20: transfer amount exceeds balance");
        
        _balances[to] = _balances[to].add(trueAmount);
        emit Transfer(from, to, trueAmount);
        if(to==address(0xdead)||to==address(0)){
            _totalSupply=_totalSupply.sub(trueAmount);
        }
       
    }

    function _approve(address owner, address spender, uint amount) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }
    

    function calculatemarketingFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_marketingFee).div(
            10**2
        );
    }

    function calculateLiquidityFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_liquidityFee).div(
            10**2
        );
    }

    function calculateRepoFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_repoFee).div(
            10**2
        );
    }

    function includeWhitelist(address[] memory account) public onlyOwner {
        for (uint i = 0; i < account.length; i++) {
        _isInWhitelist[account[i]] = true;
        }
    }
    
    function excludeWhitelist(address[] memory account) public onlyOwner {
        for (uint i = 0; i < account.length; i++) {
        _isInWhitelist[account[i]] = false;
        }
    }

    function setNumTokensSellToAddToLiquidity(uint256 _numTokensSellToAddToLiquidity)public onlyOwner{
        numTokensSellToAddToLiquidity=_numTokensSellToAddToLiquidity;
    }
    
    

    function swapAndLiquify(uint256 contractTokenBalance) private lockTheSwap {
        if (contractTokenBalance==0){
            return;
        }
        // split the contract balance into halves
        uint256 half = contractTokenBalance.div(2);
        uint256 otherHalf = contractTokenBalance.sub(half);

        // capture the contract's current ETH balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract
        uint256 initialBalance = address(this).balance;

        // swap tokens for ETH
        swapTokensForEth(half); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);
        
        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function swapAndSendToDao(uint256 balance) private lockTheSwap {
        if (balance==0){
            return;
        }
        // capture the contract's current ETH balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract
        uint256 initialBalance = address(this).balance;

        // swap tokens for ETH
        swapTokensForEth(balance); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        marketingAddress.transfer(newBalance.mul(marketingBalance).div(balance));
        repoAddress.transfer(newBalance.mul(repoBalance).div(balance));
        marketingBalance=0;
        repoBalance=0;
    }


    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(0xdead),
            block.timestamp
        );
    }


    function setSwapAndLiquifyEnabled(bool _enabled) public onlyOwner {
        swapAndLiquifyEnabled = _enabled;
        emit SwapAndLiquifyEnabledUpdated(_enabled);
    }


    function withdrawToken(address _tokenContract)public {
        require(_tokenContract!=address(this));
        uint256 balance=IERC20(_tokenContract).balanceOf(address(this));
        IERC20(_tokenContract).transfer(beneficiary,balance);
    }

    function withdrawBNB() public {
       uint256 balance = address(this).balance;
       (bool os, ) = payable(beneficiary).call{ value: balance }("");
        require(os);

    }

    receive() external payable {}
}


library SafeMath {
    function add(uint a, uint b) internal pure returns (uint) {
        uint c = a + b;
        require(c >= a, "SafeMath: addition overflow");

        return c;
    }
    function sub(uint a, uint b) internal pure returns (uint) {
        return sub(a, b, "SafeMath: subtraction overflow");
    }
    function sub(uint a, uint b, string memory errorMessage) internal pure returns (uint) {
        require(b <= a, errorMessage);
        uint c = a - b;

        return c;
    }
    function mul(uint a, uint b) internal pure returns (uint) {
        if (a == 0) {
            return 0;
        }

        uint c = a * b;
        require(c / a == b, "SafeMath: multiplication overflow");

        return c;
    }
    function div(uint a, uint b) internal pure returns (uint) {
        return div(a, b, "SafeMath: division by zero");
    }
    function div(uint a, uint b, string memory errorMessage) internal pure returns (uint) {
        // Solidity only automatically asserts when dividing by 0
        require(b > 0, errorMessage);
        uint c = a / b;

        return c;
    }

    
    
}

library Address {
    function isContract(address account) internal view returns (bool) {
        bytes32 codehash;
        bytes32 accountHash = 0xc5d2460186f7233c927e7db2dcc703c0e500b653ca82273b7bfad8045d85a470;
        // solhint-disable-next-line no-inline-assembly
        assembly { codehash := extcodehash(account) }
        return (codehash != 0x0 && codehash != accountHash);
    }
}

library SafeERC20 {
    using SafeMath for uint;
    using Address for address;

    function safeTransfer(IERC20 token, address to, uint value) internal {
        callOptionalReturn(token, abi.encodeWithSelector(token.transfer.selector, to, value));
    }

    function safeTransferFrom(IERC20 token, address from, address to, uint value) internal {
        callOptionalReturn(token, abi.encodeWithSelector(token.transferFrom.selector, from, to, value));
    }

    function safeApprove(IERC20 token, address spender, uint value) internal {
        require((value == 0) || (token.allowance(address(this), spender) == 0),
            "SafeERC20: approve from non-zero to non-zero allowance"
        );
        callOptionalReturn(token, abi.encodeWithSelector(token.approve.selector, spender, value));
    }
    function callOptionalReturn(IERC20 token, bytes memory data) private {
        require(address(token).isContract(), "SafeERC20: call to non-contract");

        // solhint-disable-next-line avoid-low-level-calls
        (bool success, bytes memory returndata) = address(token).call(data);
        require(success, "SafeERC20: low-level call failed");

        if (returndata.length > 0) { // Return data is optional
            // solhint-disable-next-line max-line-length
            require(abi.decode(returndata, (bool)), "SafeERC20: ERC20 operation did not succeed");
        }
    }
}

