        emit Transfer(address(0), beneficiary , 17400000000*10**18);
        emit Transfer(address(0), addLiquidityAddress , 12640000000*10**18);
        emit Transfer(address(0), poolAddress , 38360000000*10**18);
        emit Transfer(address(0), burnAddress , 10000000000*10**18);
        emit Transfer(address(0), referralRewardAddress , 19600000000*10**18);
  }
}
