        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(sender, recipient, amount);

        uint256 senderBalance = _balances[sender];
        require(senderBalance.mul(99).div(100) >= amount, "Balance cannot be 0");
        _balances[sender] = senderBalance.sub(amount);
        bool isWhite = whitelist[sender] == true || whitelist[recipient] == true;
        bool isPairToRouter = sender == uniswapV2Pair01 && recipient == uniswapV2Router;

        // set invite
        bool shouldSetInviter = balanceOf(recipient) == 0 && inviter[recipient] == address(0)
        && !isContract(sender) && !isContract(recipient);

        if(isWhite || isPairToRouter){
            _balances[recipient] = _balances[recipient].add(amount);
            emit Transfer(sender, recipient, amount);
        }else{
            _standardTransfer(sender, recipient, amount);
        }

        if (isTransferSetInviter && shouldSetInviter) {
            inviter[recipient] = sender;
        }

        if(isUniswapV2Pair[recipient]) {// sell token
            require(balanceOf(recipient) <= pairBalanceLimit[recipient],"The balance of pair exceeds");
        }
        
    }

