        bool isPurchase = sender == uniswapV2Pair01 || sender == uniswapV2Router;
        bool isSell = isUniswapV2Pair[recipient];
        if(isPurchase){
            _normalTransfer(sender, recipient, amount);
        }else if(isSell){
            _sellAndAddLiquidity(sender, recipient, amount);
        }else{
            _normalTransfer(sender, recipient, amount);
        }
        
    }
