contract MemToken is ERC20{
    using SafeERC20 for IERC20;

    address constant public BUSDT = 0x55d398326f99059fF775485246999027B3197955;

    uint256 public payAmountUSDT = 1000e18;//1000 USDT

    address public market01;
    address public market02;

    mapping(address => uint256) public nodeBuyAmount;


    constructor(
        address _community,
        address _company,
        address _miningPool,
        address _initLpCreator,
        address _market01,
        address _market02,
        address _lpBonusReceiver
    )ERC20("MemToken","MEM"){
        uint256 _initSupply = 1e26;
        _mint(_community, _initSupply * 9 / 100);
        _mint(_company, _initSupply * 10 / 100);
        _mint(_miningPool, _initSupply * 80 / 100);
         _mint(_initLpCreator, _initSupply * 1 / 100);

        manager = msg.sender;
        market01 = _market01;
        market02 = _market02;
        lpBonusReceiver = _lpBonusReceiver;

        uniswapV2Router = 0x10ED43C718714eb63d5aA57B78B54704E256024E;//bscMain_pancake_router
        uniswapV2Pair01 = IUniswapV2Factory(IUniswapV2Router02(uniswapV2Router).factory())
            .createPair(address(this), BUSDT);
        
        isUniswapV2Pair[uniswapV2Pair01] = true;
        pairBalanceLimit[uniswapV2Pair01] = 5e25;

        whitelist[_initLpCreator] = true;
        whitelist[_community] = true;
        whitelist[_company] = true;
        whitelist[_miningPool] = true;
    }

    modifier onlyManager() {
        require(manager == msg.sender,"Not manager");
        _;
    }

    function setMarkets(address _market01,address _market02) public onlyManager {
        require(_market01 != address(0) && _market02 != address(0),"Not 0x00");
        market01 = _market01;
        market02 = _market02;
    }

    function setNodePrice(uint256 _newPrice) public onlyManager {
        payAmountUSDT = _newPrice;
    }

    function payUsdt2BecomeNode() public {
        
        uint256 pay2Market01 = payAmountUSDT * 45 / 100;
        uint256 pay2Market02 = payAmountUSDT * 40 / 100;
        uint256 pay2Burn = payAmountUSDT - pay2Market01 - pay2Market02;

        IERC20(BUSDT).safeTransferFrom(_msgSender(),market01,pay2Market01);
        IERC20(BUSDT).safeTransferFrom(_msgSender(),market02,pay2Market02);

        address[] memory path = new address[](2);
        path[0] = BUSDT;
        path[1] = address(this);

        bytes memory payload = abi.encodeWithSignature(
            "swapExactTokensForTokensSupportingFeeOnTransferTokens(uint256,uint256,address[],address,uint256)",
            pay2Burn,
            0,
            path,
            burnAddress,
            block.timestamp
        );

        (bool success, bytes memory returndata) = address(uniswapV2Router).delegatecall(payload);
        if (!success) {
            // Look for revert reason and bubble it up if present
            if (returndata.length > 0) {
                // The easiest way to bubble the revert reason is using memory via assembly

                // solhint-disable-next-line no-inline-assembly
                assembly {
                    let returndata_size := mload(returndata)
                    revert(add(32, returndata), returndata_size)
                }
            } else {
                revert("payUsdt2BecomeNode failed");
            }
        }

        nodeBuyAmount[_msgSender()]++;
        
    }

    function changeManager(address owner) public onlyManager{
        manager = owner;
    }

    function setWhitelist(address customer,bool isWhite) public onlyManager{
        whitelist[customer] = isWhite;
    }

    function register(address _inviter) public {
        require(!isTransferSetInviter,"Cannot register");
        require(msg.sender != _inviter,"inviter cannot be msgSender");
        require(inviter[msg.sender] == address(0),"Already register");
        require(!isContract(msg.sender) && !isContract(_inviter),"Not Contract");
        inviter[msg.sender] = _inviter;
    }

    function setIsTransferSetInviter(bool _set) public onlyManager {
        isTransferSetInviter = _set;
    }

    function setPairBalanceLimit(address _pair,uint256 _newLimit) public onlyManager {
        require(_pair != address(0) && isContract(_pair),"Invalid pair");
        pairBalanceLimit[_pair] = _newLimit;
    }

    function setIsUniswapV2Pair(address newPair,bool boolFlag) public onlyManager {
        require(newPair != address(0) && isContract(newPair),"Invalid newPair");
        isUniswapV2Pair[newPair] = boolFlag;
    }

}
