      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts@4.9.0/access/Ownable.sol";
import "@openzeppelin/contracts@4.9.0/security/Pausable.sol";

import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";


pragma solidity 0.8.8;

contract BiBiDAO is Ownable {

    event BiBiDAOAdded(address indexed bibiDAO);

    event BiBiDAORemoved(address indexed bibiDAO);

    mapping(address => bool) public bibiDAOMap;

    function addBiBiDAO(address bibiDAO) external onlyOwner {
        bibiDAOMap[bibiDAO] = true;
        emit BiBiDAOAdded(bibiDAO);
    }

    function removeBiBiDAO(address bibiDAO) external onlyOwner {
        delete bibiDAOMap[bibiDAO];
        emit BiBiDAORemoved(bibiDAO);
    }

    modifier onlyBiBiDAO() {
        require(bibiDAOMap[_msgSender()], "BiBiDAO: caller is not the BiBiDAO");
        _;
    }
}

contract BiBiShareHolder is BiBiDAO, Pausable {

    struct Pool {
        uint256 totalAmount;
        uint256 bonusAmount;
        uint256 accPerShare;
        address bibiToken;
    }

    struct ShareHolder {
        uint256 amount;
        uint256 rewardDebt;
    }

    Pool public pool;
    mapping(address => ShareHolder) public shareHolderMap;

    event DepositShare(address indexed user, uint256 indexed amount);

    event WithdrawShare(address indexed user, uint256 indexed amount, uint256 indexed bonusAmount);

    constructor(address _bibiToken) {
        pool.bibiToken = _bibiToken;
    }

    function getBanlance() public view returns(uint256) {
        return IERC20(pool.bibiToken).balanceOf(address(this));
    }

    function addBonus(uint256 bonusAmount) external onlyBiBiDAO{
        pool.bonusAmount += bonusAmount;
        pool.accPerShare += bonusAmount*(1e16)/pool.totalAmount;
    }

    function withdrawReward(address to, uint256 amount) external onlyBiBiDAO{
        TransferHelper.safeTransfer(pool.bibiToken, to, amount);
    }

    function depositShare(uint256 amount) external whenNotPaused {
        TransferHelper.safeTransferFrom(pool.bibiToken, _msgSender(), address(this), amount);

        ShareHolder storage shareHolder = shareHolderMap[_msgSender()];
        shareHolder.amount += amount;
        shareHolder.rewardDebt += amount*pool.accPerShare;
        pool.totalAmount += amount;

        emit DepositShare(_msgSender(), amount);
    }

    function queryBonus(address user) external view returns(uint256) {
        ShareHolder memory shareHolder = shareHolderMap[user];
        return ((shareHolder.amount*pool.accPerShare)-shareHolder.rewardDebt)/(1e16);
    }

    function withdrawBonus() external whenNotPaused {
        ShareHolder storage shareHolder = shareHolderMap[_msgSender()];

        uint256 bonusAmount = (shareHolder.amount*pool.accPerShare) - shareHolder.rewardDebt;
        shareHolder.rewardDebt += bonusAmount;
        bonusAmount /=(1e16);
        pool.bonusAmount -= bonusAmount;

        TransferHelper.safeTransfer(pool.bibiToken, _msgSender(), bonusAmount);
    }

    function queryShare(address user) external view returns(uint256) {
        return shareHolderMap[user].amount;
    }

    function withdrawShare(uint256 amount) external {
        ShareHolder storage shareHolder = shareHolderMap[_msgSender()];

        require(shareHolder.amount >= amount, "BiBiShareHolder: Insufficient balance");

        uint256 bonusAmount = ((shareHolder.amount*pool.accPerShare)-shareHolder.rewardDebt)/(1e16);
        pool.bonusAmount -= bonusAmount;
        shareHolder.amount -= amount;
        pool.totalAmount -= amount;
        shareHolder.rewardDebt = shareHolder.amount*pool.accPerShare;

        TransferHelper.safeTransfer(pool.bibiToken, _msgSender(), amount+bonusAmount);

        emit WithdrawShare(_msgSender(), amount, bonusAmount);
    }

    function queryDevBonus() public view returns(uint256) {
        uint256 balance = getBanlance();
        return balance-pool.totalAmount-pool.bonusAmount;
    }

    function withdrawDevBonus(uint256 amount) external onlyOwner {
        require(amount <= queryDevBonus(), "BiBiShareHolder: Insufficient balance");

        TransferHelper.safeTransfer(pool.bibiToken, _msgSender(), amount);
    }

    function safeTransferToken(address token, address to, uint256 value) external onlyOwner {
        require(token != pool.bibiToken, "BiBiShareHolder: Token error");
        TransferHelper.safeTransfer(token, to, value);
    }

    function safeTransferETH(address to, uint value) external onlyOwner {
        TransferHelper.safeTransferETH(to, value);
    }

    function pause() external virtual onlyOwner whenNotPaused {
        _pause();
    }

    function unpause() external virtual onlyOwner whenPaused {
        _unpause();
    }

}"
