    "contracts/StakingPool.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.6.12;

import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Address.sol";

import "./xElpisBattle.sol";
import "./interfaces/IERC20Mintable.sol";

//StakingPool allows staking EBAs and getting xEBAs back as proof to get rewards from pool.
contract StakingPool is Context, Ownable {
    using SafeMath for uint256;

    // Info of lock.
    struct Lock {
        uint256 amount; // the amount of locked EBAs.
        uint256 releaseTime; // the time in which this amount of EBAs can be released
    }

    // The EBA TOKEN!
    IERC20Mintable public immutable EBA;
    // The xEBA TOKEN!
    xElpisBattle public immutable xEBA;
    // Emergency state.
    bool public emergency;
    uint256 public constant TOKEN_UNIT = 1e18;
    // EBAs created per block.
    uint256 public ebaPerBlock;
    // Maximum number of EBAs that can be minted.
    uint256 public maxMint = 5e25; // 5 % total supply of EBA
    // Total number of EBAs that have been minted.
    uint256 public totalMinted;
    // How long will the EBAs be locked when leaving the pool.
    uint256 public lockTime;
    // Total locked EBAs in pool.
    uint256 public totalLockedEBAs;
    // Last block number that EBAs distribution occurs.
    uint256 public lastRewardBlock;

    // Info of each lock.
    mapping(address => Lock) public locks;

    ///@dev Emited when `ebaPerBlock` is changed from `account`.
    event EbaPerBlockChanged(address account, uint256 ebaPerBlock);

    ///@dev Emited when `lockTime` is changed from `account`.
    event LockTimeChanged(address account, uint256 lockTime);

    ///@dev Emited when `maxMint` is changed from `account`.
    event MaxMintChanged(address account, uint256 maxMint);

    ///@dev Emited when `emergency` is changed from `account`.
    event EmergencyChanged(address account, bool emergency);

    ///@dev Emited when an `amount` of EBAs is entered into the pool.
    event Enter(address indexed user, uint256 amount);

    ///@dev Emited when an `amount` of xEBAs is leave the pool.
    event Leave(address indexed user, uint256 amount);

    ///@dev Emited when an `amount` of EBAs is released the pool.
    event Release(address indexed user, uint256 amoun);

    ///@dev Emited when an `amount` of EBAs is withdraw the pool.
    event EmergencyWithdraw(address indexed user, uint256 amount);

    constructor(
        IERC20Mintable _EBA,
        xElpisBattle _xEBA,
        uint256 _ebaPerBlock,
        uint256 _startBlock,
        uint256 _lockTime
    ) public {
        require(
            Address.isContract(address(_EBA)),
            "EBA is not contract account"
        );
        require(
            Address.isContract(address(_xEBA)),
            "xEBA is not contract account"
        );
        require(_ebaPerBlock > 0, "zero value");
        require(_startBlock > block.number, "can only start until future");

        EBA = _EBA;
        xEBA = _xEBA;
        ebaPerBlock = _ebaPerBlock;
        lastRewardBlock = _startBlock;
        lockTime = _lockTime;
    }

    ///@dev Sets new value for `ebaPerBlock` state variable.
    ///@dev Only callable by owner.
    function setEbaPerBlock(uint256 _ebaPerBlock, bool _withHarvest)
        external
        onlyOwner
    {
        require(ebaPerBlock != _ebaPerBlock, "setEbaPerBlock: identical");

        // only harvest when _withHarvest is true.
        if (_withHarvest) {
            harvest();
        }
        ebaPerBlock = _ebaPerBlock;
        emit EbaPerBlockChanged(_msgSender(), _ebaPerBlock);
    }

    ///@dev Sets new value for `lockTime` state variable.
    ///@dev Only callable by owner.
    function setLockTime(uint256 _lockTime) external onlyOwner {
        require(lockTime != _lockTime, "setLockTime: identical");

        lockTime = _lockTime;
        emit LockTimeChanged(_msgSender(), _lockTime);
    }

    ///@dev Sets new value for `maxMint` state variable.
    ///@dev Only callable by owner.
    function setMaxMint(uint256 _maxMint) external onlyOwner {
        require(maxMint != _maxMint, "setMaxMint: identical");

        maxMint = _maxMint;
        emit MaxMintChanged(_msgSender(), _maxMint);
    }

    ///@dev Sets new value for `emergency` state variable.
    ///@dev Only callable by owner.
    function setEmergency(bool _emergency) external onlyOwner {
        require(emergency != _emergency, "setEmergency: identical");

        emergency = _emergency;
        emit EmergencyChanged(_msgSender(), _emergency);
    }

    ///@dev Returns the amount of EBAs farmed from `_from` block to `_to` block.
    function _calculateRewards(uint256 _from, uint256 _to)
        internal
        view
        returns (uint256)
    {
        return _to.sub(_from).mul(ebaPerBlock);
    }

    ///@dev Returns the exchange rate xEBA to EBA.
    function exchangeRate() external view returns (uint256) {
        uint256 _lastRewardBlock = lastRewardBlock;
        uint256 _totalLockedEBAs = totalLockedEBAs;
        uint256 _totalShares = xEBA.totalSupply();
        if (_totalLockedEBAs == 0 || _totalShares == 0) {
            return TOKEN_UNIT;
        } else if (block.number > _lastRewardBlock) {
            uint256 ebaReward = _calculateRewards(
                _lastRewardBlock,
                block.number
            );
            _totalLockedEBAs = _totalLockedEBAs.add(ebaReward);
        }
        return (TOKEN_UNIT * _totalLockedEBAs) / _totalShares;
    }

    ///@dev Returns the amount of xEBAs received when exchanging `_amount` EBAs.
    function _toXEBA(uint256 _amount) internal view returns (uint256) {
        uint256 _totalLockedEBAs = totalLockedEBAs; //gas saving
        uint256 _totalShares = xEBA.totalSupply();
        if (_totalLockedEBAs == 0 || _totalShares == 0) {
            return _amount;
        } else {
            //Formula: EBAIn/xEBAOut = totalLockedEBAs/xEBASupply
            return _amount.mul(_totalShares).div(_totalLockedEBAs);
        }
    }

    ///@dev Returns the amount of EBAs received when exchanging `_shares` xEBAs.
    function _toEBA(uint256 _shares) internal view returns (uint256) {
        //Formula: xEBAIn/EBAOut = xEBASupply/totalLockedEBAs
        return _shares.mul(totalLockedEBAs).div(xEBA.totalSupply());
    }

    ///@dev Refunds the `amount` of EBAs for `recipient`.
    ///@dev Safe transfer function, just in case if rounding error causes pool to not have enough EBAs.
    function _safeRefund(address _recipient, uint256 _amount) internal {
        uint256 ebaBal = EBA.balanceOf(address(this));
        if (ebaBal >= _amount) {
            EBA.transfer(_recipient, _amount);
        } else {
            EBA.transfer(_recipient, ebaBal);
        }
    }

    ///@dev Creates the `amount` of tokens for `recipient`.
    function _mint(
        address _token,
        address _recipient,
        uint256 _amount
    ) internal {
        IERC20Mintable(_token).mint(_recipient, _amount);
    }

    ///@dev Updates reward variables of the staking pool to be up-to-date.
    function harvest() public {
        uint256 _lastRewardBlock = lastRewardBlock; //gas saving
        if (block.number <= _lastRewardBlock) {
            return;
        }
        uint256 _totalLockedEBAs = totalLockedEBAs; //gas saving
        if (_totalLockedEBAs == 0) {
            lastRewardBlock = block.number;
            return;
        }
        uint256 ebaReward = _calculateRewards(_lastRewardBlock, block.number);
        require(
            totalMinted + ebaReward <= maxMint,
            "harvest: mint amount exceeds maximum"
        );

        totalMinted = totalMinted.add(ebaReward);
        totalLockedEBAs = _totalLockedEBAs.add(ebaReward);
        _mint(address(EBA), address(this), ebaReward);
        lastRewardBlock = block.number;
    }

    ///@dev Enter the staking pool. Pay some EBAs and earn some shares.
    function enter(uint256 _amount) external {
        require(_amount > 0, "enter: bad amount");

        harvest();

        //calculate the amount of xEBAs received
        uint256 _shares = _toXEBA(_amount);
        _mint(address(xEBA), _msgSender(), _shares);
        totalLockedEBAs = totalLockedEBAs.add(_amount);
        EBA.transferFrom(_msgSender(), address(this), _amount);
        emit Enter(_msgSender(), _amount);
    }

    ///@dev Leave the staking pool. Claim back your EBAs.
    ///@dev Claimed EBAs will locked for `lockTime` seconds before being released.
    function leave(uint256 _amount) external {
        Lock memory _lock = locks[_msgSender()];

        require(_amount > 0, "leave: bad amount");
        require(
            xEBA.balanceOf(_msgSender()) >= _amount,
            "leave: insufficient balance"
        );

        harvest();

        //calculate the amount of EBAs refunded
        uint256 _refunds = _toEBA(_amount);
        xEBA.transferFrom(_msgSender(), address(this), _amount);
        xEBA.burn(_amount);

        //create lock for EBAs
        _lock.amount = _lock.amount.add(_refunds);
        _lock.releaseTime = block.timestamp + lockTime;
        locks[_msgSender()] = _lock;
        totalLockedEBAs = totalLockedEBAs.sub(_refunds);

        emit Leave(_msgSender(), _amount);
    }

    ///@dev Release locked EBAs.
    function release() external {
        Lock memory _lock = locks[_msgSender()];
        uint256 _amount = _lock.amount;

        require(_amount > 0, "release: zero balance");
        require(
            block.timestamp >= _lock.releaseTime,
            "release: not released yet"
        );

        delete locks[_msgSender()];
        _safeRefund(_msgSender(), _amount);

        emit Release(_msgSender(), _amount);
    }

    ///@dev Withdraw all without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 _shares) external {
        Lock memory _lock = locks[_msgSender()];

        require(emergency, "emergencyWithdraw: not emergency");
        require(_shares > 0, "emergencyWithdraw: bad amount");
         require(
            xEBA.balanceOf(_msgSender()) >= _shares,
            "emergencyWithdraw: insufficient balance"
        );

        //calculate the amount of EBAs refunded
        uint256 _refunds = _toEBA(_shares);
        xEBA.transferFrom(_msgSender(), address(this), _shares);
        xEBA.burn(_shares);

        totalLockedEBAs = totalLockedEBAs.sub(_refunds);
        uint256 _totalRefunds = _refunds.add(_lock.amount);
        delete locks[_msgSender()];
        _safeRefund(_msgSender(), _totalRefunds);

        emit EmergencyWithdraw(_msgSender(), _totalRefunds);
    }
}
"
