pragma solidity 0.6.12;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

// X Elpis Battle Governance token
contract xElpisBattle is ERC20("xEBA", "xEBA"), Ownable {
    mapping(address => bool) public operators;

    modifier authorized() {
        require(operators[msg.sender], "xEBA: not authorized");
        _;
    }

    event OperatorGranted(address operator);
    event OperatorRevoked(address operator);

    constructor() public {}

    ///@dev Grants `operator` role for `account`.
    function grantOperator(address _account) external onlyOwner {
        require(_account != address(0), "xEBA: zero address");
        require(!operators[_account], "xEBA: already the operator");

        operators[_account] = true;
        emit OperatorGranted(_account);
    }

    ///@dev Revokes `operator` role of `account`.
    function revokeOperator(address _account) external onlyOwner {
        require(operators[_account], "xEBA: not operator");

        operators[_account] = false;
        emit OperatorRevoked(_account);
    }

    function mint(address _to, uint256 _amount) external authorized {
        _mint(_to, _amount);
    }

    function burn(uint256 _amount) external authorized {
        _burn(msg.sender, _amount);
    }
}
