pragma solidity 0.8.19;

abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes calldata) {
        return msg.data;
    }
}

abstract contract Ownable is Context {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor() {
        _setOwner(_msgSender());
    }

    function owner() public view virtual returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(owner() == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    function renounceOwnership() public virtual onlyOwner {
        _setOwner(address(0));
    }

    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        _setOwner(newOwner);
    }

    function _setOwner(address newOwner) private {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

interface IERC20Metadata is IERC20 {

    function name() external view returns (string memory);
    function symbol() external view returns (string memory);
    function decimals() external view returns (uint8);
}


contract ERC20 is Context, IERC20, IERC20Metadata {
    mapping(address => uint256) private _balances;

    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;

    string private _name;
    string private _symbol;


    constructor(string memory name_, string memory symbol_) {
        _name = name_;
        _symbol = symbol_;
    }

    function name() public view virtual override returns (string memory) {
        return _name;
    }

    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

    function decimals() public view virtual override returns (uint8) {
        return 18;
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view virtual override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        _transfer(sender, recipient, amount);

        uint256 currentAllowance = _allowances[sender][_msgSender()];
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");
        unchecked {
            _approve(sender, _msgSender(), currentAllowance - amount);
        }

        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender] + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero");
        unchecked {
            _approve(_msgSender(), spender, currentAllowance - subtractedValue);
        }

        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(sender, recipient, amount);

        uint256 senderBalance = _balances[sender];
        require(senderBalance >= amount, "ERC20: transfer amount exceeds balance");
        unchecked {
            _balances[sender] = senderBalance - amount;
        }
        _balances[recipient] += amount;

        emit Transfer(sender, recipient, amount);

        _afterTokenTransfer(sender, recipient, amount);
    }

    function _createTotalSupply(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply += amount;
        _balances[account] += amount;
        emit Transfer(address(0), account, amount);

        _afterTokenTransfer(address(0), account, amount);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
}


interface IUniswapV2Factory {
    function createPair(address tokenA, address tokenB) external returns (address pair);
}

interface IUniswapV2Router02 {
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
    function factory() external pure returns (address);
    function WETH() external pure returns (address);
    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint amountToken, uint amountETH, uint liquidity);
}

library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath: addition overflow");
        return c;
    }

    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        return sub(a, b, "SafeMath: subtraction overflow");
    }

    function sub(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        require(b <= a, errorMessage);
        uint256 c = a - b;
        return c;
    }

    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }
        uint256 c = a * b;
        require(c / a == b, "SafeMath: multiplication overflow");
        return c;
    }

    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        return div(a, b, "SafeMath: division by zero");
    }

    function div(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        require(b > 0, errorMessage);
        uint256 c = a / b;
        return c;
    }

}

    /*
    * Contract "EBurn" is an ERC20 token that integrates burn, liquidity pool fee, and marketing fee features.
    * The contract inherits from the OpenZeppelin's ERC20 and Ownable contracts.
    * SafeMath library is used to handle mathematical operations safely.
    *
    * Upon a transfer:
    * 1. A certain percentage of the tokens is burnt and sent to a dead address.
    * 2. Another percentage is used for liquidity pool, which is swapped for ETH, and then added to the liquidity pool on Uniswap.
    * 3. A final percentage is used as a marketing fee, which is also swapped for ETH and sent to a dedicated marketing wallet.
    *
    * The contract also sets a maximum transaction amount to prevent large token transfers in one go.
    * It also sets a limit to how many tokens the contract can hold before initiating the swap for ETH and adding liquidity to Uniswap.
    *
    * ATTENTION: The maximum transaction limit and fee rates are established upon contract deployment and cannot be altered.
    * This is because there are no setter functions provided that would allow the owner to modify these values.
    * This design choice ensures a predictable and transparent token operation, which guarantees fairness for all holders.
    */

contract EBurn is ERC20, Ownable {
    using SafeMath for uint256;
   
    IUniswapV2Router02 public uniswapV2Router;
    address public immutable uniswapV2Pair;

    uint256 public liquidityFee = 1; 
    uint256 public marketingFee = 5; 
    uint256 public burnFee = 4;
    uint256 public totalFee = liquidityFee.add(marketingFee).add(burnFee);
    
    //This is the maximum amount of tokens that can be transferred in a single transaction. 
    uint256 public maxTransactionAmount = 500000000000 * (10**18); // 0.5% of total supply

    //This is the minimum amount of tokens the contract needs to hold before it starts to swap the tokens for BNB.
    uint256 public swapTokensAtAmount = 1000000000 * (10**18);

    address payable public marketingWallet = payable(0xd2E6522895528C96f2D0C61101d8F246622599a1);
    address public constant deadAddress = 0x000000000000000000000000000000000000dEaD;

    mapping (address => bool) private _isExcludedFromFees;
   
    bool private inSwapAndLiquify;
  
    event SwapEthForTokens(uint256 amountIn, address[] path);
    event SwapAndLiquify(uint256 tokensIntoLiqudity, uint256 ethReceived);
    event LiquidityAdded(uint256 tokenAmount, uint256 ETHAmount);

    event SwapTrigerred(
        uint256 tokensSwapped,
        uint256 ethReceived
    );

    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    // This contract creates a new ERC20 token with the name "EBurn" and the symbol "EBR".
    // It also sets up a new Uniswap pair for this token and the native currency (BNB in this case).
    constructor() ERC20("EBurn", "EBR") {
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);

        // Create a uniswap pair for this new token
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;

        // exclude from paying fees or having max transaction amount
        _isExcludedFromFees[owner()] = true;
        _isExcludedFromFees[marketingWallet] = true;
        _isExcludedFromFees[address(this)] = true;
        // creates initial total supply
        _createTotalSupply(owner(), 100_000_000_000_000 * (10**18));
    }

    // This is an override of the original _transfer function provided by the ERC20 contract.
    // It manages the token transfer process including applying fees and managing token swap and liquify process.
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        // Prevent transfers from and to zero address
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        // If amount is 0, just return
        if(amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        // If neither the sender nor receiver is excluded from fees, enforce the max transaction amount limit
        if(!_isExcludedFromFees[from] && !_isExcludedFromFees[to]){
            require(amount <= maxTransactionAmount, "amount exceeds the maxTransactionAmount.");
        }

        // Check if it's time to swap and liquify
        uint256 contractTokenBalance = balanceOf(address(this));
        bool overMinTokenBalance = contractTokenBalance >= swapTokensAtAmount;

        if (
            !inSwapAndLiquify &&
            overMinTokenBalance &&
            from != uniswapV2Pair
        ) {
            // Limit contractTokenBalance to swapTokensAtAmount
            contractTokenBalance = swapTokensAtAmount;

            // Execute swap and liquify
            swapAndLiquify(contractTokenBalance);
        }

        // If neither the sender nor receiver is excluded from fees, apply fees and burn tokens
        if(!_isExcludedFromFees[from] && !_isExcludedFromFees[to]) {
            uint256 _tokensForSwap = amount.mul(totalFee.sub(burnFee)).div(100);
            uint256 _burnTokens = amount.mul(burnFee).div(100);
            amount = amount.sub(_tokensForSwap.add(_burnTokens));

            // Transfer tokens for swap
            super._transfer(from, address(this), _tokensForSwap);

            // Transfer tokens for burn
            super._transfer(from, deadAddress, _burnTokens);     
        }

        // Execute the transfer
        super._transfer(from, to, amount);
    }

    /*
    * This function executes the token swap and liquidity addition operation in a single atomic transaction.
    * This function is set as private and can only be called within this contract.
    */
    function swapAndLiquify(uint256 contractTokenBalance) private lockTheSwap {
        // Calculate the number of tokens to be added to the liquidity pool
        uint256 liquidityTokens = contractTokenBalance.mul(liquidityFee).div(totalFee).div(2);

        // Calculate the number of tokens that can be swapped for ETH
        uint256 swappableTokens = contractTokenBalance.sub(liquidityTokens);

        // Record the balance before swap operation
        uint256 balanceBeforeSwap = address(this).balance;

        // Swap tokens for ETH
        swapTokensForEth(swappableTokens);

        // Calculate the amount of ETH received from swap
        uint256 newBalance = address(this).balance.sub(balanceBeforeSwap);

        // Emit SwapTriggered event
        emit SwapTrigerred(swappableTokens, newBalance);
    
        // Calculate the amount of ETH to be added to liquidity
        uint256 ethForLiquidity = newBalance.mul(liquidityFee).div(totalFee).div(2);

        // Add liquidity to the pool
        addLiquidity(liquidityTokens, ethForLiquidity);

        // Emit LiquidityAdded event
        emit LiquidityAdded(liquidityTokens, ethForLiquidity);

        // Calculate the remaining ETH after liquidity is added
        uint256 remainingEth = address(this).balance;

        // Transfer remaining ETH to the marketing wallet
        marketingWallet.transfer(remainingEth);
    }

    // This function is used to swap tokens for ETH via the Pancakeswap router.
    // It uses the exact amount of tokens and gets as much ETH as it can based on current rates.
    function swapTokensForEth(uint256 tokenAmount) internal {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    // This function is used to add liquidity to the Pancakeswap pool.
    // It uses the contract's tokens and BNB to do this.
    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) internal {
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0,
            0,
            owner(),
            block.timestamp
        );
    }

    // This function is used to check whether an account is excluded from fees.
    function isExcludedFromFees(address account) public view returns(bool) {
        return _isExcludedFromFees[account];
    }

    // Fallback function: Receives ETH when the contract swaps tokens for ETH.
    receive() external payable {
        // This function is intentionally blank. It's primary purpose is to receive ETH when swapTokensForEth is called.

  	}
}
