contract SNAIL is Initializable, ERC20Upgradeable, ERC20BurnableUpgradeable, ERC20SnapshotUpgradeable, AccessControlUpgradeable, PausableUpgradeable, ERC20PermitUpgradeable, ERC20VotesUpgradeable, ERC20FlashMintUpgradeable, UUPSUpgradeable {
    bytes32 public constant SNAPSHOT_ROLE = keccak256("SNAPSHOT_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");
    bytes32 public constant ADMIN = keccak256("ADMIN");

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor(uint256 _lockTime, bool _underLock, uint256 _preMint){
        initialize(_lockTime,_underLock,_preMint);
    }

    function initialize(uint256 _lockTime, bool _underLock, uint256 _preMint) initializer public {
        __ERC20_init("SNAIL TOKEN", "SNAIL");
        __ERC20Burnable_init();
        __ERC20Snapshot_init();
        __AccessControl_init();
        __Pausable_init();
        __ERC20Permit_init("SNAIL TOKEN");
        __ERC20FlashMint_init();
        __UUPSUpgradeable_init();

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN, msg.sender);
        _grantRole(SNAPSHOT_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _grantRole(UPGRADER_ROLE, msg.sender);

        _mint(msg.sender, _preMint * 10 ** decimals());
        setLockTime(_lockTime);
        setUnderLock(_underLock);
    }

    function snapshot() public onlyRole(SNAPSHOT_ROLE) {
        _snapshot();
    }

    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    function mint(address to, uint256 amount) public onlyRole(MINTER_ROLE) {
        _mint(to, amount);
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount)
        internal
        whenNotPaused
        checkLock(from,amount)
        override(ERC20Upgradeable, ERC20SnapshotUpgradeable)
    {
        super._beforeTokenTransfer(from, to, amount);
    }

    function _authorizeUpgrade(address newImplementation)
        internal
        onlyRole(UPGRADER_ROLE)
        override
    {}

    // The following functions are overrides required by Solidity.

    function _afterTokenTransfer(address from, address to, uint256 amount)
        internal
        override(ERC20Upgradeable, ERC20VotesUpgradeable)
    {
        super._afterTokenTransfer(from, to, amount);
    }

    function _mint(address to, uint256 amount)
        internal
        override(ERC20Upgradeable, ERC20VotesUpgradeable)
    {
        super._mint(to, amount);
    }

    function _burn(address account, uint256 amount)
        internal
        override(ERC20Upgradeable, ERC20VotesUpgradeable)
    {
        super._burn(account, amount);
    }

    struct lockInfo{
        uint256 locktimeStart;
        uint256 locktimeEnd;
        uint256 lockAmount;
        bool isLock;
    }

    mapping(address => lockInfo[]) public lockList;
    uint256 public lockTime;
    bool public underLock;

    modifier checkLock(address from, uint256 amount){
        if(from != address(0) && underLock == true){
            if(whenNotLock(from,amount) == false){
                revert(
                    string(
                        abi.encodePacked(
                            "Lock: Your token is lock"
                        )
                    )
                );
            }
        }
        _;
    }

    function getLockListByAddress(address _address) public view returns (lockInfo[] memory){
        return lockList[_address];
    }

    function whenNotLock(address from, uint256 amount) private view returns (bool){
        uint256 balance = SHTERC20(address(this)).balanceOf(from);
        bool check = true;
        uint256 totalLock = 0;
        for(uint256 i = 0; i<lockList[from].length; i++){
            if(lockList[from][i].isLock){
                if(lockList[from][i].locktimeEnd >= block.timestamp){
                   totalLock = totalLock + lockList[from][i].lockAmount;
                }
            }
        }

        if(balance - amount < totalLock){
            check = false;
        }
        
        return check;
    }

     function setLockTime(uint256 _lockTime) public onlyRole(ADMIN) {
        lockTime = _lockTime;
    }

    function setUnderLock(bool _underLock) public onlyRole(ADMIN) {
        underLock = _underLock;
    }

    function lockAddress(address from, uint256 _lockAmount) external onlyRole(ADMIN) returns (bool){
        lockList[from].push(lockInfo(block.timestamp, block.timestamp + lockTime, _lockAmount, true));
        return true;
    }

    function unlockAddressByIndex(address from, uint256 index) external onlyRole(ADMIN) returns (bool){
        lockList[from][index] = lockList[from][lockList[from].length - 1];
        lockList[from].pop();
        return true;
    }

    function unlockAddress(address from) external onlyRole(ADMIN) {
        delete lockList[from];
    }
}
