contract WojTokenStaking is Ownable {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    IERC20 public wojToken;
    uint256 public lockDuration = 120 seconds;
    uint256 public bonusPercentage = 20;

    struct Stake {
        uint256 amount;
        uint256 unlockTime;
        uint256 amountWithPrize;
        bool isWithdrawn;
    }

 
    mapping(address => bool) public whitelistedUsers;

    mapping(address => Stake[]) public stakes;
    uint256 public stakeCounter;


     uint256 public limitCountStakePerUser = 5;
     uint256 public minAmountStake = 200000000000 ; //WOJ

    event Staked(address indexed user, uint256 stakeId, uint256 amount);
    event Withdrawn(address indexed user, uint256 stakeId, uint256 amount);

    constructor(address _wojToken) {
        wojToken = IERC20(_wojToken);
    }

 modifier onlyWhitelisted() {
        require(whitelistedUsers[msg.sender], "Only whitelisted users can stake");
        _;
    }


    function stake(uint256 _amount) external onlyWhitelisted{
        require(_amount > 0, "Amount must be greater than 0");
        require(wojToken.balanceOf(msg.sender) >= _amount, "Insufficient balance");

         require(_amount >= minAmountStake, "Min Amount Stake Error");

        uint256 countStakeUser=getUserStakeCount(msg.sender);


         require(countStakeUser < limitCountStakePerUser, "Limit Stake Error");



        wojToken.safeTransferFrom(msg.sender, address(this), _amount);

        uint256 unlockTime = block.timestamp.add(lockDuration);
        stakeCounter = stakeCounter.add(1);
        uint256 amountWithPrize = _amount.mul(bonusPercentage).div(100).add(_amount);

        
        Stake memory newStake = Stake(_amount, unlockTime,amountWithPrize, false);
        stakes[msg.sender].push(newStake);

       

        emit Staked(msg.sender, stakeCounter, _amount);
    }

    function withdraw(uint256 _stakeId) external onlyWhitelisted{
        require(_stakeId > 0, "Invalid stake ID");
        Stake[] storage userStakes = stakes[msg.sender];
        require(_stakeId <= userStakes.length, "Invalid stake ID");

        Stake storage userStake = userStakes[_stakeId - 1];
        require(!userStake.isWithdrawn, "Already withdrawn");
        require(block.timestamp >= userStake.unlockTime, "Stake is still locked");

      
        uint256 totalAmount = userStake.amountWithPrize;

        userStake.isWithdrawn = true;
        wojToken.safeTransfer(msg.sender, totalAmount);

        emit Withdrawn(msg.sender, _stakeId, totalAmount);
    }

    function getTotalStakes(address _user) public view returns (uint256) {
        Stake[] memory userStakes = stakes[_user];
        uint256 totalStakes;
        for (uint256 i = 0; i < userStakes.length; i++) {
            if (!userStakes[i].isWithdrawn) {
                totalStakes = totalStakes.add(userStakes[i].amount);
            }
        }
        return totalStakes;
    }

    function getTotalReward(address _user) public view returns (uint256) {
        Stake[] memory userStakes = stakes[_user];
        uint256 totalBonuses;
        for (uint256 i = 0; i < userStakes.length; i++) {
            if (!userStakes[i].isWithdrawn) {
                uint256 bonusAmount = userStakes[i].amount.mul(bonusPercentage).div(100);
                totalBonuses = totalBonuses.add(bonusAmount);
            }
        }
        return totalBonuses;
    }

    function setLockDuration(uint256 _duration) external onlyOwner {
        lockDuration = _duration;
    }

    function setBonusPercentage(uint256 _percentage) external onlyOwner {
        bonusPercentage = _percentage;
    }

     function setMinAmountStake(uint256 _minAmount) external onlyOwner {
        minAmountStake = _minAmount;
    }

      function setLimitCountStakePerUser(uint256 _count) external onlyOwner {
        limitCountStakePerUser = _count;
    }

      function addToWhitelist(address[] memory _users,uint256[] memory _data) external onlyOwner {
 require(
        _users.length == _data.length,
        "Invalid input arrays"
    );

            bool status=true;
             for(uint i = 0; i<_users.length; i++){
                 if (_data[i]==1){
                 status=true;
                 }else{
                     status=false;
                 }
            whitelistedUsers[_users[i]] = status;
        }
        
    }

      function getAllStakes(address _user) external view returns (Stake[] memory) {
        return stakes[_user];
    }


    function getUserStakeCount(address _user) public view returns (uint256) {
      Stake[] memory userStakes = stakes[_user];
        return userStakes.length;
    }
     

    function withdraw() public onlyOwner {
        (bool os, ) = payable(owner()).call{value: address(this).balance}("");
        require(os);
    }
    function withdrawTokens(IERC20 token) public onlyOwner {
        uint256 balance = token.balanceOf(address(this));
        token.transfer(msg.sender, balance);
    }
     
}
