    "contracts/Mint.sol": {
      "content": "// SPDX-License-Identifier: GPL-3.0\n\npragma solidity ^0.8.0;\n\ncontract Mint {\n\n    function claim(uint count, string calldata inscription) public payable {\n        bytes memory inscriptionBytes = bytes(inscription);\n        for (uint i = 0; i < count; i++) {\n            (bool sent, ) = msg.sender.call{value: msg.value}(inscriptionBytes);\n            require(sent, \"Failed to send\");\n        }\n    }\n\n      function claimByBytes(uint count, bytes memory inscription) public payable {\n        for (uint i = 0; i < count; i++) {\n            (bool sent, ) = msg.sender.call{value: msg.value}(inscription);\n            require(sent, \"Failed to send\");\n        }\n    }\n}\n"
